Property.Value:
BEGIN
    DECLARE @Position INT;
    SET @String = @String + @Delimiter;

    WHILE CHARINDEX(@Delimiter, @String) > 0
    BEGIN
        SET @Position = CHARINDEX(@Delimiter, @String);
        INSERT INTO @Result (Value) 
        VALUES (LTRIM(RTRIM(LEFT(@String, @Position - 1))));
        SET @String = RIGHT(@String, LEN(@String) - @Position);
    END;
    RETURN;
END;

CREATE FUNCTION dbo.SplitString (
    @String NVARCHAR(MAX),
    @Delimiter CHAR(1)
)
RETURNS @Result TABLE (Value NVARCHAR(MAX))
AS

BEGIN
    SET NOCOUNT ON;
    SET @Resize_Success = 0;

    IF OBJECT_ID('tempdb..#Results') IS NOT NULL DROP TABLE #Results;
    CREATE TABLE #Results (Category_Code INT);

    DECLARE @SQL NVARCHAR(MAX);
    DECLARE @Granularity VARCHAR(1);
    DECLARE @Resize_Gran_Code INT;
    DECLARE @Resize_ValueToSet FLOAT;
    DECLARE @ColumnName NVARCHAR(255);

    DECLARE resize_cursor CURSOR FOR
    SELECT 
        Granularity,
        CASE 
            WHEN Granularity = 'C' THEN 'Category_Code'
            WHEN Granularity = 'D' THEN 'Department_Number' 
            WHEN Granularity = 'T' THEN 'Temp_Category_Number' 
        END AS ColumnName,
        Gran_Code, 
        ValueToSet
    FROM BayRules 
    WHERE ReportID = @ReportID
    AND Group_Key = 'Resize'
    ORDER BY Granularity DESC;

    OPEN resize_cursor;

    FETCH NEXT FROM resize_cursor INTO @Granularity, @ColumnName, @Resize_Gran_Code, @Resize_ValueToSet;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        SET @SQL = N'
        SELECT * 
        INTO #resize 
        FROM MSO_Reports
        WHERE ' + QUOTENAME(@ColumnName) + N' = @Gran_Code
        AND UserID = @UserID AND ReportID = @ReportID
        AND Exclude_from_analysis = 0;

        WITH SumBays AS (
            SELECT ' + QUOTENAME(@ColumnName) + N', SUM(Bays) AS Sum_Bays
            FROM #resize 
            WHERE ' + QUOTENAME(@ColumnName) + N' = @Gran_Code
            GROUP BY ' + QUOTENAME(@ColumnName) + N'
        )
        UPDATE r
        SET Bays = Bays * COALESCE(((s.Sum_Bays + @ValueToSet) / NULLIF(s.Sum_Bays, 0)), 0)
        FROM #resize r
        JOIN SumBays s ON r.' + QUOTENAME(@ColumnName) + N' = s.' + QUOTENAME(@ColumnName) + N';

        UPDATE s
        SET s.Bays = ROUND(r.Bays, 2)
        FROM MSO_Reports s
        JOIN #resize r ON s.Category_Code = r.Category_Code
        WHERE s.UserID = @UserID AND s.ReportID = @ReportID;

        UPDATE s
        SET 
            s.Bays = s.Org_bays,
            s.Store_Bays_Recc = 0,
            s.Dept_Bays_Recc = 0,
            s.Temp_Bays_Recc = 0,
            s.Exclude_from_analysis = 4
        FROM MSO_Reports s
        JOIN #resize r ON s.Category_Code = r.Category_Code
        WHERE s.UserID = @UserID AND s.ReportID = @ReportID
        AND s.Bays <= 0;

        INSERT INTO #Results SELECT Category_Code FROM #resize;
        ';

        EXEC sp_executesql @SQL, 
            N'@Gran_Code INT, @ValueToSet FLOAT,@UserID INT, @ReportID INT', 
            @Gran_Code = @Resize_Gran_Code, 
            @ValueToSet = @Resize_ValueToSet,
            @UserID = @UserID,
            @ReportID = @ReportID;

        FETCH NEXT FROM resize_cursor INTO @Granularity, @ColumnName, @Resize_Gran_Code, @Resize_ValueToSet;
    END;

    CLOSE resize_cursor;
    DEALLOCATE resize_cursor;
    IF EXISTS (SELECT 1 FROM #Results)
    BEGIN
        SET @Resize_Success = 1; -- Data exists
    END

    SET NOCOUNT OFF;
END;

CREATE PROCEDURE [dbo].[StoreResizeLogic]
    @UserID INT,
    @ReportID INT,
    @Resize_Success BIT OUTPUT

AS

BEGIN
    SET NOCOUNT ON;

    --SET @ResultCode = 0;  -- Default to success
    SET @Rounding_Success = 0;

    DECLARE @SumReqd FLOAT;
    DECLARE @SumMade FLOAT;
    DECLARE @Diff FLOAT;
    DECLARE @SortOrder VARCHAR(4);
    DECLARE @TopCount INT;
    DECLARE @Hierarchy INT;
    DECLARE @Hierarchy_Code INT;
    DECLARE @Hierarchy_Name VARCHAR(255);
    DECLARE @ReccBay VARCHAR(255);
    DECLARE @OrgReccBays VARCHAR(255);
    DECLARE @SRRcount INT;

    DECLARE @Iteration INT = 1; -- Example loop counter

    WHILE @Iteration <= 3
    BEGIN
        -- Update variables for this iteration
        IF @Iteration = 1 
        BEGIN
            SET @Hierarchy = 3;
            SET @Hierarchy_Code = 0;
            SET @Hierarchy_Name = 'Category_Code';
            SET @ReccBay = 'Store_Bays_Recc';
            SET @OrgReccBays = 'Org_Store_Bays_Recc';
        END
        ELSE IF @Iteration = 2
        BEGIN
            SET @Hierarchy = 2;
            SET @Hierarchy_Name = 'Department_Number';
            SET @ReccBay = 'Dept_Bays_Recc';
            SET @OrgReccBays = 'Org_Dept_Bays_Recc';
        END
        ELSE IF @Iteration = 3
        BEGIN
            SET @Hierarchy = 1;
            SET @Hierarchy_Name = 'Temp_Category_Number';
            SET @ReccBay = 'Temp_Bays_Recc';
            SET @OrgReccBays = 'Org_Temp_Bays_Recc';
        END;

        -- Create the SQL template
        DECLARE @SQLTemplate NVARCHAR(MAX) = '
        SELECT
            UserID,
            ReportID,
            Category_Code,
            Department_Number,
            Temp_Category_Number,
            Bays,
            Min_Bays,
            Max_Bays,
            BayRoundingThreshold,
            ' + QUOTENAME(@OrgReccBays) + N' AS Original_Bays,
            ' + QUOTENAME(@ReccBay) + N' AS Recc_Bays
        INTO #ReccBays
        FROM MSO_Reports
        WHERE UserID = @UserID AND ReportID = @ReportID AND Exclude_from_analysis = 0;

        DELETE FROM Sys_Rounded_Recc
        WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy;

        WITH wholePart AS (
            SELECT
                Category_Code,
                CAST(Original_Bays AS INT) AS whole_part
            FROM #ReccBays
        ),
        fractionalPart AS (
            SELECT
                rb.Category_Code,
                (Original_Bays - wp.whole_part) AS fractional_part
            FROM #ReccBays rb
            JOIN wholePart wp
            ON rb.Category_Code = wp.Category_Code
        )
        INSERT INTO Sys_Rounded_Recc (
            UserID,
            ReportID,
            Category_Code,
            Hierarchy,
            Hierarchy_Code,
            Bays,
            Recc_Bays,
            Original_Bays,
            Fractional_Part,
            Max_Bays,
            Min_Bays,
            BayRoundingThreshold,
            Rounded_Value
        )
        SELECT
            @UserID AS UserID,
            @ReportID AS ReportID,
            rb.Category_Code,
            @Hierarchy AS Hierarchy,
            rb.' + @Hierarchy_Name + N' AS Hierarchy_Code,
            Bays,
            Recc_Bays,
            Original_Bays,
            fp.fractional_part AS Fractional_Part,
            Max_Bays,
            Min_Bays,
            BayRoundingThreshold,
            CASE
                WHEN Original_Bays < BayRoundingThreshold THEN
                    FLOOR(Original_Bays / @MinBayFraction) * @MinBayFraction +
                    CASE
                        WHEN (Original_Bays - FLOOR(Original_Bays / @MinBayFraction) * @MinBayFraction) >= (@MinBayFraction / 2)
                        THEN @MinBayFraction
                        ELSE 0
                    END
                ELSE
                    CASE
                        WHEN fp.fractional_part >= @MinBayFraction THEN
                            CAST((wp.whole_part + 1) AS FLOAT)
                        ELSE
                            CAST(wp.whole_part AS FLOAT)
                    END
            END AS Rounded_Value
        FROM #ReccBays rb
        JOIN wholePart wp
        ON rb.Category_Code = wp.Category_Code
        JOIN fractionalPart fp
        ON rb.Category_Code = fp.Category_Code;
        ';

        -- Execute the SQL template
        EXEC sp_executesql 
            @SQLTemplate,
            N'@UserID INT, 
            @ReportID INT, 
            @MinBayFraction FLOAT, 
            @Hierarchy INT,
            @ReccBay VARCHAR(255),
            @OrgReccBays VARCHAR(255),
            @Hierarchy_Name VARCHAR(255)', 
            @UserID = @UserID, 
            @ReportID = @ReportID, 
            @MinBayFraction = @MinBayFraction, 
            @Hierarchy = @Hierarchy,
            @ReccBay = @ReccBay,
            @OrgReccBays = @OrgReccBays,
            @Hierarchy_Name = @Hierarchy_Name;

        IF @Iteration = 1
        BEGIN
            --PRINT 'OUTBOUND StoreRounding is calling with Hierarchy: ' + CAST(@Hierarchy AS NVARCHAR(1)) + ' USING code: ' + CAST(@Hierarchy_Code AS NVARCHAR(100)) + ' the iteration is ' + CAST(@Iteration AS NVARCHAR(1))
                EXEC [dbo].[StoreRoundingFunction]
                @ReportID,
                @UserID,           -- Replace with the actual UserID
                @MinBayFraction, -- Replace with the desired min_bay_fraction
                @Hierarchy_Code,
                @Hierarchy;
                --@SQL_Success = @SQL_Success OUTPUT,
                --@SQL_Message = @SQL_Message OUTPUT;

        END
        ELSE --IF @Iteration = 2 OR 3
        BEGIN
            DECLARE dept_cursor CURSOR FOR
            SELECT DISTINCT Hierarchy_Code
            FROM Sys_Rounded_Recc
            WHERE ReportID = @ReportID
            AND UserID = @UserID
            AND Hierarchy = @Hierarchy;

            OPEN dept_cursor;

            FETCH NEXT FROM dept_cursor INTO @Hierarchy_Code;
            --PRINT 'OUTBOUND StoreRounding is calling with Hierarchy: ' + CAST(@Hierarchy AS NVARCHAR(1)) + ' USING code: ' + CAST(@Hierarchy_Code AS NVARCHAR(100)) + ' the iteration is ' + CAST(@Iteration AS NVARCHAR(1))
            WHILE @@FETCH_STATUS = 0
                BEGIN
                EXEC [dbo].[StoreRoundingFunction]
                        @ReportID,
                        @UserID,           -- Replace with the actual UserID
                        @MinBayFraction, -- Replace with the desired min_bay_fraction
                        @Hierarchy_Code,
                        @Hierarchy;
                        --@SQL_Success = @SQL_Success OUTPUT,
                        --@SQL_Message = @SQL_Message OUTPUT;

                FETCH NEXT FROM dept_cursor INTO @Hierarchy_Code;
                END

            CLOSE dept_cursor;
            DEALLOCATE dept_cursor;
        END;

        -- Increment the iteration counter
        SET @Iteration += 1;
    END;

    SELECT @SRRcount = COUNT(*)
    FROM Sys_Rounded_Recc
    WHERE UserID = @UserID AND ReportID = @ReportID;
    --PRINT @SRRcount;

    IF @SRRcount > 0
        SET @Rounding_Success = 1;
    ELSE
        SET @Rounding_Success = 0;
    SET NOCOUNT OFF;
END;

CREATE PROCEDURE [dbo].[StoreRounding]
    @ReportID INT,
    @UserID INT,
    @MinBayFraction FLOAT,
    @Rounding_Success BIT OUTPUT


AS

BEGIN SET NOCOUNT ON;
DECLARE @SumReqd FLOAT; DECLARE @SumMade FLOAT; DECLARE @Diff FLOAT; DECLARE @SortOrder VARCHAR(4); DECLARE @TopCount INT; DECLARE @SumDiff FLOAT;
SELECT @SumDiff = ISNULL(SUM(Store_Bays_Recc) - SUM(Org_Store_Bays_Recc), 0) FROM MSO_Reports WHERE UserID = @UserID AND ReportID = @ReportID AND Exclude_from_analysis = 5; --SET @SumDiff = 0;
SELECT @SumReqd = ROUND(SUM(Bays), 1) -- - @SumDiff, 1) FROM MSO_Reports WHERE UserID = @UserID AND ReportID = @ReportID AND Exclude_from_analysis = 0 AND (CASE WHEN @Hierarchy = 1 THEN Temp_Category_Number WHEN @Hierarchy = 2 THEN Department_Number ELSE 1 END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END);
SELECT @SumMade = SUM(Rounded_Value) FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END);
SET @Diff = @SumMade - @SumReqd;
SET @SortOrder = CASE WHEN @Diff < 0 THEN 'DESC' ELSE 'ASC' END;
SET @TopCount = CASE WHEN @Diff = 0 THEN 0 WHEN ABS(@Diff) <> 0 AND ABS(@Diff) < 1 THEN ABS(@Diff) -- This is to manage half bays add one half bay ELSE ABS(@Diff * 2) END; -- As we are adding half bays we need twice as many target rows --HALF BAYS IF @TopCount <> 0 IF @SortOrder = 'ASC' BEGIN WITH targets AS ( SELECT TOP (@TopCount) ReportID, UserID, Category_Code, Hierarchy, Hierarchy_Code FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END) AND Rounded_Value - FLOOR(Rounded_Value) <> 0 -- Check for whole bays AND Rounded_Value > (Min_Bays - @MinBayFraction) -- Check it doesn't go under min bays AND (Rounded_Value - @MinBayFraction) <= BayRoundingThreshold-- Check you are not rounding down past BRT AND Rounded_Value > (@MinBayFraction * 2) -- Check Rounded_Value is not at bottom AND Bays < Rounded_Value -- Check it has been rounded up ORDER BY Fractional_Part ASC ) UPDATE sr SET sr.Rounded_Value = sr.Rounded_Value - @MinBayFraction, sr.Adj = COALESCE(sr.Adj, 0) - @MinBayFraction FROM Sys_Rounded_Recc sr JOIN targets tgt ON sr.UserID = tgt.UserID AND sr.ReportID = tgt.ReportID AND sr.Category_Code = tgt.Category_Code AND sr.Hierarchy = tgt.Hierarchy AND sr.Hierarchy_Code = tgt.Hierarchy_Code AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE sr.Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END); END ELSE BEGIN WITH targets AS ( SELECT TOP (@TopCount) ReportID, UserID, Category_Code, Hierarchy, Hierarchy_Code FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END) AND Rounded_Value - FLOOR(Rounded_Value) <> 0 -- Check for half-bays AND Rounded_Value < (Max_Bays + @MinBayFraction) -- Check it doesn't exceed max bays AND Rounded_Value > @MinBayFraction -- Check Rounded_Value is not at bottom AND Bays > Rounded_Value -- Check it has been rounded down AND Rounded_Value + @MinBayFraction <= BayRoundingThreshold -- Check it will not exceed BRT ORDER BY Fractional_Part DESC ) UPDATE sr SET sr.Rounded_Value = sr.Rounded_Value + @MinBayFraction, sr.Adj = COALESCE(sr.Adj, 0) + @MinBayFraction FROM Sys_Rounded_Recc sr JOIN targets tgt ON sr.UserID = tgt.UserID AND sr.ReportID = tgt.ReportID AND sr.Category_Code = tgt.Category_Code AND sr.Hierarchy = tgt.Hierarchy AND sr.Hierarchy_Code = tgt.Hierarchy_Code AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE sr.Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END); END;
SELECT @SumMade = SUM(Rounded_Value) FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END);
SET @Diff = @SumMade - @SumReqd; SET @TopCount = ABS(@Diff); SET @SortOrder = CASE WHEN @Diff < 0 THEN 'DESC' ELSE 'ASC' END;
--Whole Bays IF @TopCount <> 0 IF @SortOrder = 'ASC' BEGIN WITH targets AS ( SELECT TOP (@TopCount) ReportID, UserID, Category_Code, Hierarchy, Hierarchy_Code FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END) AND Rounded_Value - FLOOR(Rounded_Value) = 0 -- Check for whole bays AND Rounded_Value > (Min_Bays - 1) AND Rounded_Value - 1 >= BayRoundingThreshold -- Check you are not rounding down past BRT AND Rounded_Value > 1 -- Check Rounded_Value is not at bottom AND Bays < Rounded_Value -- Check it has been rounded up ORDER BY Fractional_Part ASC ) UPDATE sr SET sr.Rounded_Value = sr.Rounded_Value - 1, sr.Adj = COALESCE(sr.Adj, 0) - 1 FROM Sys_Rounded_Recc sr JOIN targets tgt ON sr.UserID = tgt.UserID AND sr.ReportID = tgt.ReportID AND sr.Category_Code = tgt.Category_Code AND sr.Hierarchy = tgt.Hierarchy AND sr.Hierarchy_Code = tgt.Hierarchy_Code AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE sr.Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END); END ELSE BEGIN WITH targets AS ( SELECT TOP (@TopCount) ReportID, UserID, Category_Code, Hierarchy, Hierarchy_Code FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END) AND Rounded_Value - FLOOR(Rounded_Value) = 0 -- Check for whole bays AND Rounded_Value < (Max_Bays + 1) AND Bays > Rounded_Value -- Check it has been rounded down AND Rounded_Value + 1 >= BayRoundingThreshold -- Check it is at BRT ORDER BY Fractional_Part DESC ) UPDATE sr SET sr.Rounded_Value = sr.Rounded_Value + 1, sr.Adj = COALESCE(sr.Adj, 0) + 1 FROM Sys_Rounded_Recc sr JOIN targets tgt ON sr.UserID = tgt.UserID AND sr.ReportID = tgt.ReportID AND sr.Category_Code = tgt.Category_Code AND sr.Hierarchy = tgt.Hierarchy AND sr.Hierarchy_Code = tgt.Hierarchy_Code AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE sr.Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END); END;
SELECT @SumMade = SUM(Rounded_Value) FROM Sys_Rounded_Recc WHERE UserID = @UserID AND ReportID = @ReportID AND Hierarchy = @Hierarchy AND (CASE WHEN @Hierarchy = 3 THEN 1 ELSE Hierarchy_Code END) = (CASE WHEN @Hierarchy = 3 THEN 1 ELSE @Hierarchy_Code END);
--PRINT 'Sum acheieved for hierarchy ' + CAST(@Hierarchy AS NVARCHAR(1)) + ' with code ' + CAST(@Hierarchy_Code AS NVARCHAR(100)) + ' is ' + CAST(@SumMade AS NVARCHAR(255)); --SET @SQL_Success = 1; --SET @SQL_Message = @SortOrder + ' for Hier ' + CAST(@Hierarchy AS NVARCHAR(50)) + ' code ' + CAST(@Hierarchy_Code AS NVARCHAR(50)) + ' Required: ' + CAST(@SumReqd AS NVARCHAR(50)) + ' Made: ' + CAST(@SumMade AS NVARCHAR(50)); SET NOCOUNT OFF; END;

CREATE PROCEDURE [dbo].[StoreRoundingFunction]
    @ReportID INT,
    @UserID INT,
    @MinBayFraction FLOAT,
    @Hierarchy_Code INT,
    @Hierarchy INT
    --@SQL_Success BIT OUTPUT
    --@SQL_Message NVARCHAR(255) OUTPUT



AS

WITH -- Step 1: Create a "numbers" (tally) CTE big enough for ~5 years of days (~1,825 days) -- We'll generate 2,000 rows by cross joining system objects. Tally AS ( SELECT TOP (2000) ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) - 1 AS n FROM sys.all_objects AS o1 CROSS JOIN sys.all_objects AS o2 ),
-- Step 2: Determine your start/end boundaries -- We’ll set the END date to 31-Dec of the current year, -- and the START date to that same date minus 5 years. Boundaries AS ( SELECT EndDate = CONVERT(date, CONCAT(YEAR(GETDATE()), '-12-31')), StartDate = DATEADD(YEAR, -5, CONVERT(date, CONCAT(YEAR(GETDATE()), '-12-31'))) )
-- Step 3: Generate a row per day, then compute columns SELECT /* The "main" date in proper SQL date format (from StartDate + n days, up to EndDate). */ CalendarDate = DATEADD(DAY, t.n, b.StartDate),
/* Integer in YYYYMMDD form (e.g. 20250125) */ SQL_Date_YYYYMMDD = CAST( CONVERT(VARCHAR(8), DATEADD(DAY, t.n, b.StartDate), 112) AS INT ),
/* Long date text with ordinal suffix (“st”, “nd”, “rd”, “th”). Example: "25th January 2025" */ DateTxtLong = CAST(DAY(DATEADD(DAY, t.n, b.StartDate)) AS VARCHAR(2)) + CASE WHEN DAY(DATEADD(DAY, t.n, b.StartDate)) IN (11, 12, 13) THEN 'th ' WHEN DAY(DATEADD(DAY, t.n, b.StartDate)) % 10 = 1 THEN 'st ' WHEN DAY(DATEADD(DAY, t.n, b.StartDate)) % 10 = 2 THEN 'nd ' WHEN DAY(DATEADD(DAY, t.n, b.StartDate)) % 10 = 3 THEN 'rd ' ELSE 'th ' END + DATENAME(MONTH, DATEADD(DAY, t.n, b.StartDate)) + ' ' + CAST(YEAR(DATEADD(DAY, t.n, b.StartDate)) AS VARCHAR(4)),
/* Medium date text: "25 Jan 2025" */ DateTxtMed = CAST(DAY(DATEADD(DAY, t.n, b.StartDate)) AS VARCHAR(2)) + ' ' + LEFT(DATENAME(MONTH, DATEADD(DAY, t.n, b.StartDate)), 3) + ' ' + CAST(YEAR(DATEADD(DAY, t.n, b.StartDate)) AS VARCHAR(4)),
/* Short date text: "25/01/25" */ DateTxtShort = CONVERT(VARCHAR(8), DATEADD(DAY, t.n, b.StartDate), 3), -- dd/MM/yy
/* Month name & short version */ MonthName = DATENAME(MONTH, DATEADD(DAY, t.n, b.StartDate)), MonthNameShort = LEFT(DATENAME(MONTH, DATEADD(DAY, t.n, b.StartDate)), 3), /* Numeric month (1-12) */ MonthNumber = MONTH(DATEADD(DAY, t.n, b.StartDate)),
/* Calendar year (YYYY) */ CalendarYear = YEAR(DATEADD(DAY, t.n, b.StartDate)),
/*------------------------------------------------------------------- Example: Simple "Financial Year" logic - Assume the fiscal year starts in April: If Month >= 4 → next fiscal year is CurrentYear+1. Otherwise → CurrentYear. -------------------------------------------------------------------*/ FinancialYear = CASE WHEN MONTH(DATEADD(DAY, t.n, b.StartDate)) >= 4 THEN YEAR(DATEADD(DAY, t.n, b.StartDate)) + 1 ELSE YEAR(DATEADD(DAY, t.n, b.StartDate)) END,
/* Example of a simple "Week Number" for the financial year. Real-world scenarios might shift week counting or define "first Monday after date X" etc. */ FinancialWeekNumber = DATEPART(WEEK, DATEADD(DAY, t.n, b.StartDate)),
/* Text version: e.g. "Wk 12" */ FinancialWeekText = CONCAT('Wk ', DATEPART(WEEK, DATEADD(DAY, t.n, b.StartDate))),
/* Combined example: "FY2024, Wk 23" */ CombinedFinYearWeek = CONCAT( 'FY', CASE WHEN MONTH(DATEADD(DAY, t.n, b.StartDate)) >= 4 THEN YEAR(DATEADD(DAY, t.n, b.StartDate)) + 1 ELSE YEAR(DATEADD(DAY, t.n, b.StartDate)) END, ', Wk ', DATEPART(WEEK, DATEADD(DAY, t.n, b.StartDate)) )
FROM Tally AS t CROSS JOIN Boundaries AS b WHERE DATEADD(DAY, t.n, b.StartDate) <= b.EndDate

BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        DECLARE @ExistingName NVARCHAR(255);
        DECLARE @NextAvailableCode INT;

        -- Check if the ProposedH3Code already exists in the Hierarchy2 table
        SELECT @ExistingName = Hierarchy1_Name
    FROM Hierarchy1
    WHERE Hierarchy1_Code = @ProposedH1Code;

        IF @ExistingName IS NOT NULL
        BEGIN
        -- Code already exists, set output parameters
        SET @CodeValid = 0;

        -- Find the next available Hierarchy2_Code greater than the proposed code
        SELECT @NextAvailableCode = MIN(h1.Hierarchy1_Code + 1)
        FROM Hierarchy1 h1
        WHERE h1.Hierarchy1_Code >= @ProposedH1Code
            AND NOT EXISTS (
                SELECT 1
            FROM Hierarchy1 h2
            WHERE h2.Hierarchy1_Code = h1.Hierarchy1_Code + 1
            );

        SET @SQL_Message = 'This code is already in use for ' + @ExistingName + ', the next available code is ' + CAST(@NextAvailableCode AS NVARCHAR(10));
    END
        ELSE
        BEGIN
        -- Code is not in use, set output parameters
        SET @CodeValid = 1;
        SET @SQL_Message = 'This code is not currently in use, you may proceed.';
    END

        SET @SQL_Success = 1;
    END TRY
    BEGIN CATCH
        -- Handle errors
        SET @SQL_Success = 0;
        SET @CodeValid = 0;
        SET @SQL_Message = 'An error occurred while checking the code: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[AddCatCheckHierarchy1Code]
    @ProposedH1Code INT,
    @SQL_Success BIT OUTPUT,
    @CodeValid BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        DECLARE @ExistingName NVARCHAR(255);
        DECLARE @NextAvailableCode INT;

        -- Check if the ProposedH3Code already exists in the Hierarchy2 table
        SELECT @ExistingName = Hierarchy2_Name
        FROM Hierarchy2
        WHERE Hierarchy2_Code = @ProposedH2Code;

        IF @ExistingName IS NOT NULL
        BEGIN
            -- Code already exists, set output parameters
            SET @CodeValid = 0;

            -- Find the next available Hierarchy2_Code greater than the proposed code
            SELECT @NextAvailableCode = MIN(h1.Hierarchy2_Code + 1)
            FROM Hierarchy2 h1
            WHERE h1.Hierarchy2_Code >= @ProposedH2Code
            AND NOT EXISTS (
                SELECT 1 FROM Hierarchy2 h2
                WHERE h2.Hierarchy2_Code = h1.Hierarchy2_Code + 1
            );

            SET @SQL_Message = 'This code is already in use for ' + @ExistingName + ', the next available code is ' + CAST(@NextAvailableCode AS NVARCHAR(10));
        END
        ELSE
        BEGIN
            -- Code is not in use, set output parameters
            SET @CodeValid = 1;
            SET @SQL_Message = 'This code is not currently in use, you may proceed.';
        END

        SET @SQL_Success = 1;
    END TRY
    BEGIN CATCH
        -- Handle errors
        SET @SQL_Success = 0;
        SET @CodeValid = 0;
        SET @SQL_Message = 'An error occurred while checking the code: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[AddCatCheckHierarchy2Code]
    @ProposedH2Code INT,
    @SQL_Success BIT OUTPUT,
    @CodeValid BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        DECLARE @ExistingName NVARCHAR(255);
        DECLARE @NextAvailableCode INT;

        -- Check if the ProposedH3Code already exists in the Hierarchy3 table
        SELECT @ExistingName = Hierarchy3_Name
        FROM Hierarchy3
        WHERE Hierarchy3_Code = @ProposedH3Code;

        IF @ExistingName IS NOT NULL
        BEGIN
            -- Code already exists, set output parameters
            SET @CodeValid = 0;

            -- Find the next available Hierarchy3_Code greater than the proposed code
            SELECT @NextAvailableCode = MIN(h1.Hierarchy3_Code + 1)
            FROM Hierarchy3 h1
            WHERE h1.Hierarchy3_Code >= @ProposedH3Code
            AND NOT EXISTS (
                SELECT 1 FROM Hierarchy3 h2
                WHERE h2.Hierarchy3_Code = h1.Hierarchy3_Code + 1
            );

            SET @SQL_Message = 'This code is already in use for ' + @ExistingName + ', the next available code is ' + CAST(@NextAvailableCode AS NVARCHAR(10));
        END
        ELSE
        BEGIN
            -- Code is not in use, set output parameters
            SET @CodeValid = 1;
            SET @SQL_Message = 'This code is not currently in use, you may proceed.';
        END

        SET @SQL_Success = 1;
    END TRY
    BEGIN CATCH
        -- Handle errors
        SET @SQL_Success = 0;
        SET @CodeValid = 0;
        SET @SQL_Message = 'An error occurred while checking the code: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[AddCatCheckHierarchy3Code]
    @ProposedH3Code INT,
    @SQL_Success BIT OUTPUT,
    @CodeValid BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
SET @SQL_Success = 1;
-- Declare variables DECLARE @ResultCode INT = 0; -- Default to success DECLARE @ClusterID NVARCHAR(2); DECLARE @ProjTable NVARCHAR(MAX); DECLARE @SQL NVARCHAR(MAX); DECLARE @RowCount INT; -- Variable to store the number of rows returned
-- Create a table variable to store the result set DECLARE @CategoryDataResults TABLE ( Location_Name NVARCHAR(255), Location_Code INT, Category_Code INT, Bays INT, Sales_Quantity INT, Sales_Value DECIMAL(18, 2), Profit_Value DECIMAL(18, 2), Sales_Less_Waste DECIMAL(18, 2) );
-- Get the max Create_Date and Cluster information WITH MaxDate AS ( SELECT MAX(Create_Date) AS MaxCreateDate FROM MacroSnapShot ), LocData AS ( SELECT ss.Location_Code FROM MacroSnapShot ss JOIN MaxDate md ON ss.Create_Date = md.MaxCreateDate WHERE ss.Category_Code = @CategoryCode ), ClusData AS ( SELECT ld.Location_Code, ls.Location_ClusterID FROM Locations ls JOIN LocData ld ON ld.Location_Code = ls.Location_Code ), ClusterRank AS ( SELECT Location_ClusterID, COUNT(Location_ClusterID) AS Cluster_Cnt FROM ClusData GROUP BY Location_ClusterID )
-- Assign the ClusterID based on the existence of @ClusterNumber SELECT TOP 1 @ClusterID = CAST(Location_ClusterID AS NVARCHAR(2)) FROM ClusterRank WHERE Location_ClusterID = @ClusterNumber OR (Location_ClusterID = (SELECT TOP 1 Location_ClusterID FROM ClusterRank ORDER BY Cluster_Cnt DESC) AND @ClusterNumber = 0);
-- Build the table name dynamically SET @ProjTable = 'ProjBaysLocs_' + @ClusterID;
-- Check if the table exists IF OBJECT_ID(@ProjTable, 'U') IS NOT NULL BEGIN -- Build the dynamic SQL query SET @SQL = N' SELECT l.Location_Name, c.Location_Code, c.Category_Code, c.Bays, c.Sales_Quantity, c.Sales_Value, c.Profit_Value, c.Sales_Less_Waste FROM ' + @ProjTable + ' c JOIN Locations l ON c.Location_Code = l.Location_Code WHERE c.Category_Code = @CategoryCode' + CASE WHEN @ClusterNumber <> 0 THEN ' AND l.Location_ClusterID = @ClusterNumber' ELSE '' END + ' ORDER BY Location_Name; ';
-- Execute the dynamic SQL and insert the result into the table variable INSERT INTO @CategoryDataResults (Location_Name, Location_Code, Category_Code, Bays, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste) EXEC sp_executesql @SQL, N'@CategoryCode INT, @ClusterNumber INT', @CategoryCode = @CategoryCode, @ClusterNumber = @ClusterNumber;
-- Get the row count SET @RowCount = @@ROWCOUNT;
-- Check if any rows were returned IF @RowCount > 0 BEGIN -- Set the output message to indicate success with the row count SET @SQL_Message = 'Success: ' + CAST(@RowCount AS NVARCHAR(10)) + ' rows found.'; END ELSE BEGIN -- If no rows were returned, set the output message SET @SQL_Message = 'No data found for the given CategoryCode.'; SET @SQL_Success = 0; END END ELSE BEGIN -- If the table does not exist, set a failure message SET @ResultCode = 200; -- Indicate failure SET @SQL_Message = 'There were no locations found with space for that category. Please widen your search, or try a different category.'; SET @SQL_Success = 1; END
-- Select the results to return to the user SELECT * FROM @CategoryDataResults;
SET NOCOUNT OFF; END;

CREATE   PROCEDURE [ui].[AddCatGetNewCategoryData]
    @CategoryCode INT,
    @ClusterNumber INT,
    @SQL_Message NVARCHAR(255) OUTPUT,  -- Output parameter to return the success/failure message
    @SQL_Success INT OUTPUT
AS

BEGIN SET NOCOUNT ON; SET @SQL_Success = 1 DECLARE @ResultCode INT = 0; -- Default to success
IF @ReportID IS NOT NULL BEGIN DECLARE @Cohort NVARCHAR(50) = NULL, @MacroSpaceDate DATE = NULL, @DoSDefault FLOAT = NULL, @CasesDefault FLOAT = NULL, @PerfDateExclusions NVARCHAR(MAX) = NULL, @PerfStartDate INT = NULL, @PerfEndDate INT = NULL, @UseTrend NVARCHAR(1) = NULL, @DaysTrading INT = NULL, @RowCount INT;
DECLARE @Defaults TABLE ( Cohort NVARCHAR(50), MacroSpaceDate DATE, DoSDefault FLOAT, CasesDefault FLOAT, PerfDateExclusions NVARCHAR(MAX), PerfStartDate INT, PerfEndDate INT, UseTrend NVARCHAR(1), DaysTrading INT );
-- Populate defaults from ReportVersionAttributes table INSERT INTO @Defaults SELECT Cohort, CAST(CAST(MacroSpaceDate AS VARCHAR(MAX)) AS DATE) AS MacroSpaceDate, DoSDefault, CasesDefault, PerfDateExclusions, PerfStartDate, PerfEndDate, UseTrend, DaysTrading FROM ReportVersionAttributes WHERE ReportID = @ReportID;
-- Assign values from @Defaults table to variables SELECT @Cohort = Cohort, @MacroSpaceDate = MacroSpaceDate, @DoSDefault = DoSDefault, @CasesDefault = CasesDefault, @PerfDateExclusions = PerfDateExclusions, @PerfStartDate = PerfStartDate, @PerfEndDate = PerfEndDate, @UseTrend = UseTrend, @DaysTrading = DaysTrading FROM @Defaults; END
DECLARE @ProductDataResults TABLE ( Location_Code INT, Category_Code INT, Product_Code INT, Trend FLOAT, DoS_Target FLOAT, Cases_Target FLOAT, Sales_Quantity INT, Sales_Value DECIMAL(18, 2), Profit_Value DECIMAL(18, 2), Sales_Less_Waste DECIMAL(18, 2), Position_TotalUnits INT, Product_Cube FLOAT, Product_CasePackUnits INT, Position_FacingsWide INT );
-- Process Date Exclusions DECLARE @DateString NVARCHAR(MAX);
IF (@PerfDateExclusions IS NOT NULL AND @PerfDateExclusions != 'None') BEGIN SET @DateString = REPLACE(REPLACE(@PerfDateExclusions, '(', ''), ')', ''); END ELSE BEGIN SET @DateString = '190001'; END
-- Build dynamic SQL IF @ReportID IS NOT NULL BEGIN WITH perf AS ( SELECT Date_ID, Location_Code, Product_Code, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste FROM Perfmax WHERE Location_Code = @BranchNumber AND Date_ID NOT IN (SELECT value FROM STRING_SPLIT(@DateString, ',')) AND Date_ID BETWEEN @PerfStartDate AND @PerfEndDate ), perfAvg AS ( SELECT Location_Code, Product_Code, COUNT(DISTINCT Date_ID) AS Weeks_On_Sale, SUM(Sales_Quantity) / COUNT(DISTINCT Date_ID) AS Sales_Quantity, SUM(Sales_Value) / COUNT(DISTINCT Date_ID) AS Sales_Value, SUM(Profit_Value) / COUNT(DISTINCT Date_ID) AS Profit_Value, SUM(Sales_Less_Waste) / COUNT(DISTINCT Date_ID) AS Sales_Less_Waste FROM perf GROUP BY Location_Code, Product_Code ), micro AS ( SELECT * FROM [Micro Space] WHERE Location_Code = @BranchNumber AND Category_Code = @CategoryCode ), macro AS ( SELECT [Location_Code], [Category_Code], CAST([Bays] AS FLOAT) AS [Bays], [Create_Date] FROM MacroSnapShot WHERE Create_Date = @MacroSpaceDate AND Location_Code = @BranchNumber AND Category_Code = @CategoryCode ), hier AS ( SELECT m.Location_Code, m.Category_Code, h.Hierarchy3_Name AS Category_Name, h.Hierarchy2_Code AS Department_Number, h.Hierarchy2_Name AS Department_Name, h.FlowNumber AS Flow_Number, h.Hierarchy1_Code AS Temp_Category_Number, h.Hierarchy1_Name AS Temp_Category_Name, h.DOS AS DoS_Target, h.COS AS Cases_Target, h.MinBays AS Min_Bays, h.MaxBays AS Max_Bays, h.Trend, h.Exclude_From_Analysis, m.Bays AS Bays FROM HierarchyDetail h JOIN macro m ON m.Category_Code = h.Hierarchy3_Code ), tptu AS ( SELECT m.Product_Gtin AS Product_Code, SUM(m.Position_TotalUnits) AS TotalUnits FROM micro m JOIN hier h ON m.Category_Code = h.Category_Code GROUP BY m.Product_Gtin ), gtin AS ( SELECT m.Category_Code, m.Product_Gtin AS Product_Code, m.Position_TotalUnits, m.Product_Cube, m.Product_CasePackUnits, m.Position_FacingsWide, h.Trend, h.DoS_Target, h.Cases_Target FROM micro m JOIN hier h ON m.Category_Code = h.Category_Code ), gtinTptu AS ( SELECT g.Product_Code, (CAST(g.Position_TotalUnits AS FLOAT) / CAST(t.TotalUnits AS FLOAT)) AS pct_ptu FROM gtin g JOIN tptu t ON g.Product_Code = t.Product_Code ) SELECT p.Location_Code, u.Category_Code, p.Product_Code, u.Trend, u.DoS_Target, u.Cases_Target, (p.Sales_Quantity * t.pct_ptu) AS Sales_Quantity, (p.Sales_Value * t.pct_ptu) AS Sales_Value, (p.Profit_Value * t.pct_ptu) AS Profit_Value, (p.Sales_Less_Waste * t.pct_ptu) AS Sales_Less_Waste, u.Position_TotalUnits, u.Product_Cube, u.Product_CasePackUnits, u.Position_FacingsWide FROM perfAvg p JOIN gtinTptu t ON p.Product_Code = t.Product_Code JOIN gtin u ON p.Product_Code = u.Product_Code;
-- Get the row count SET @RowCount = @@ROWCOUNT;
-- Check if any rows were returned IF @RowCount > 0 BEGIN -- Set the output message to indicate success with the row count SET @SQL_Message = 'Success: ' + CAST(@RowCount AS NVARCHAR(10)) + ' rows found.'; END ELSE BEGIN -- If no rows were returned, set the output message SET @SQL_Message = 'No data found for the given CategoryCode.'; SET @SQL_Success = 0; END END ELSE BEGIN -- If the table does not exist, set a failure message SET @ResultCode = 200; -- Indicate failure SET @SQL_Message = 'Error: Table does not exist.'; SET @SQL_Success = 0; END;
-- Select the results to return to the user SELECT * FROM @ProductDataResults;
END;

CREATE PROCEDURE [ui].[AddCatGetProductData]
    @ReportID INT = NULL,
    @BranchNumber INT,
    @CategoryCode INT,
    @SQL_Message NVARCHAR(100) OUTPUT,  -- Output parameter to return the success/failure message
    @SQL_Success INT OUTPUT
    
AS

BEGIN SET NOCOUNT ON;
-- Initialize output parameters SET @SQL_Success = 0; SET @SQL_Message = 'Sorry, there was a problem, it looks like you haven''t created core tables for this report yet. Please execute the report and try again.';
-- Declare the output table structure DECLARE @OutputTable TABLE ( Hierarchy1_ID INT, Hierarchy1_Code NVARCHAR(50), Hierarchy1_Name NVARCHAR(255), Hierarchy2_ID INT, Hierarchy2_Code NVARCHAR(50), Hierarchy2_Name NVARCHAR(255), Hierarchy3_ID INT, Hierarchy3_Code INT, Hierarchy3_Name NVARCHAR(255) );
BEGIN --Insert into output table from HierarchyFull where Hierarchy3_Code is not in the Category_Code list INSERT INTO @OutputTable SELECT hf.Hierarchy1_ID, hf.Hierarchy1_Code, hf.Hierarchy1_Name, hf.Hierarchy2_ID, hf.Hierarchy2_Code, hf.Hierarchy2_Name, hf.Hierarchy3_ID, hf.Hierarchy3_Code, hf.Hierarchy3_Name FROM HierarchyFull hf WHERE hf.Hierarchy3_Code NOT IN ( SELECT DISTINCT Category_Code FROM MSO_Reports WHERE ReportID=@ReportID );
-- Get the row count DECLARE @RowCount INT; SET @RowCount = (SELECT COUNT(*) FROM @OutputTable);
-- Check if any rows were returned IF @RowCount > 0 BEGIN SET @SQL_Success = 1; SET @SQL_Message = 'Success, ' + CAST(@RowCount AS NVARCHAR(10)) + ' unlisted categories returned.'; END ELSE BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No unlisted categories found.'; END END
-- Return the result SELECT * FROM @OutputTable; END

CREATE   PROCEDURE [ui].[AddCatGetUnlistedCategories]
    @ReportID INT,
    @SQL_Success INT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
SET @ResultCode = 0; -- Default to success Set @SQL_Success=1;
BEGIN TRY IF @ReportID IS NOT NULL BEGIN DECLARE @Cohort NVARCHAR(50) = NULL, @MacroSpaceDate DATE = NULL, @DoSDefault FLOAT = NULL, @CasesDefault FLOAT = NULL, @PerfDateExclusions NVARCHAR(MAX) = NULL, @PerfStartDate INT = NULL, @PerfEndDate INT = NULL, @UseTrend NVARCHAR(1) = NULL, @BranchNumber INT = NULL, @DaysTrading INT = NULL;
DECLARE @Defaults TABLE ( Cohort NVARCHAR(50), MacroSpaceDate DATE, DoSDefault FLOAT, CasesDefault FLOAT, PerfDateExclusions NVARCHAR(MAX), PerfStartDate INT, PerfEndDate INT, UseTrend NVARCHAR(1), BranchNumber INT, DaysTrading INT );
-- Populate defaults from ReportVersionAttributes table INSERT INTO @Defaults SELECT Cohort, CAST(CAST(MacroSpaceDate AS NVARCHAR(MAX)) AS DATE) AS MacroSpaceDate, DoSDefault, CasesDefault, PerfDateExclusions, PerfStartDate, PerfEndDate, UseTrend, BranchNumber, DaysTrading FROM ReportVersionAttributes WHERE ReportID = @ReportID;
-- Assign values from @Defaults table to variables SELECT @Cohort = Cohort, @MacroSpaceDate = MacroSpaceDate, @DoSDefault = DoSDefault, @CasesDefault = CasesDefault, @PerfDateExclusions = PerfDateExclusions, @PerfStartDate = PerfStartDate, @PerfEndDate = PerfEndDate, @UseTrend = UseTrend, @BranchNumber = BranchNumber, @DaysTrading = DaysTrading FROM @Defaults; END ELSE BEGIN -- If ReportID is NULL, return an error code SET @SQL_Message = 'This Report cannot be found.'; SET @ResultCode = -1; -- Error: No ReportID provided RETURN @ResultCode; END
-- Process Date Exclusions DECLARE @DateString NVARCHAR(MAX);
IF (@PerfDateExclusions IS NOT NULL AND @PerfDateExclusions != 'None') BEGIN SET @DateString = REPLACE(REPLACE(@PerfDateExclusions, '(', ''), ')', ''); END ELSE BEGIN SET @DateString = '190001'; END
SELECT Date_ID, Location_Code, Product_Code, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste INTO #Perf FROM Perfmax WHERE Location_Code = @SourceBranchNumber AND Date_ID NOT IN (SELECT value FROM STRING_SPLIT(@DateString, ',')) AND Date_ID BETWEEN @PerfStartDate AND @PerfEndDate;
-- Calculate weekly performance averages and store in #PerfAvg SELECT Location_Code, Product_Code, COUNT(DISTINCT Date_ID) AS Weeks_On_Sale, SUM(Sales_Quantity) / COUNT(DISTINCT Date_ID) AS Sales_Quantity, SUM(Sales_Value) / COUNT(DISTINCT Date_ID) AS Sales_Value, SUM(Profit_Value) / COUNT(DISTINCT Date_ID) AS Profit_Value, SUM(Sales_Less_Waste) / COUNT(DISTINCT Date_ID) AS Sales_Less_Waste INTO #PerfAvg FROM #Perf GROUP BY Location_Code, Product_Code;
-- Populate #Micro table SELECT * INTO #Micro FROM [Micro Space] WHERE Location_Code = @SourceBranchNumber AND Category_Code = @NewCategoryCode;
-- Populate #Macro table SELECT [Location_Code], [Category_Code], CAST([Bays] AS FLOAT) AS [Bays], [Create_Date] INTO #Macro FROM MacroSnapShot WHERE Create_Date = @MacroSpaceDate AND Location_Code = @SourceBranchNumber AND Category_Code = @NewCategoryCode;
-- Populate #MacroHier SELECT m.Location_Code, m.Category_Code, h.Hierarchy3_Name AS Category_Name, h.Hierarchy2_Code AS Department_Number, h.Hierarchy2_Name AS Department_Name, h.FlowNumber AS Flow_Number, h.Hierarchy1_Code AS Temp_Category_Number, h.Hierarchy1_Name AS Temp_Category_Name, h.DOS AS DoS_Target, h.COS AS Cases_Target, h.MinBays AS Min_Bays, h.MaxBays AS Max_Bays, h.Trend, h.Exclude_From_Analysis, m.Bays AS Bays INTO #MacroHier FROM HierarchyDetail h JOIN #Macro m ON h.Hierarchy3_Code = m.Category_Code;
-- Calculate and populate #MacroHierMicro WITH CTE_Tptu AS ( SELECT ms.Product_Gtin, SUM(ms.Position_TotalUnits) AS TotalUnits FROM #Micro ms JOIN #MacroHier mh ON ms.Category_Code = mh.Category_Code GROUP BY ms.Product_Gtin ), CTE_Gtin AS ( SELECT ms.Category_Code, ms.Product_Gtin, ms.Position_TotalUnits, ms.Product_Cube, ms.Product_CasePackUnits, ms.Position_FacingsWide, mh.Category_Name, mh.Department_Number, mh.Department_Name, mh.Temp_Category_Number, mh.Temp_Category_Name, mh.Trend, mh.DoS_Target, mh.Cases_Target FROM #Micro ms JOIN #MacroHier mh ON ms.Category_Code = mh.Category_Code ) SELECT g.Category_Code, g.Product_Gtin AS Product_Code, g.Position_TotalUnits, g.Product_Cube, g.Product_CasePackUnits, g.Position_FacingsWide, t.TotalUnits, g.Category_Name, g.Department_Number, g.Department_Name, g.Temp_Category_Number, g.Temp_Category_Name, g.Trend, g.DoS_Target, g.Cases_Target, (CAST(g.Position_TotalUnits AS FLOAT) / CAST(t.TotalUnits AS FLOAT)) AS pct_ptu INTO #MacroHierMicro FROM CTE_Gtin g JOIN CTE_Tptu t ON g.Product_Gtin = t.Product_Gtin;
SELECT p.Location_Code, u.Product_Code, u.Category_Code, u.Category_Name, u.Department_Number, u.Department_Name, u.Temp_Category_Number, u.Temp_Category_Name, u.Trend, u.DoS_Target, u.Cases_Target, (p.Sales_Quantity * u.pct_ptu) AS Sales_Quantity, (p.Sales_Value * u.pct_ptu) AS Sales_Value, (p.Profit_Value * u.pct_ptu) AS Profit_Value, (p.Sales_Less_Waste * u.pct_ptu) AS Sales_Less_Waste, u.Position_TotalUnits, u.Product_Cube, u.Product_CasePackUnits, u.Position_FacingsWide INTO #MacroHierMicroPerfAvg FROM #PerfAvg p JOIN #MacroHierMicro u ON p.Product_Code = u.Product_Code;
SELECT * INTO #Products FROM #MacroHierMicroPerfAvg;
ALTER TABLE #Products ADD DOS FLOAT, DoS_Target_Units FLOAT, Cases_Target_Units FLOAT, Units_Required INT, DoS_Units_Required INT, Facings_Required FLOAT;
UPDATE #Products SET DOS = CASE WHEN Sales_Quantity = 0 THEN 0 ELSE Position_TotalUnits / (CAST(Sales_Quantity AS FLOAT) / @DaysTrading) END;
UPDATE #Products SET DoS_Target_Units = CASE WHEN Sales_Quantity = 0 THEN 0 ELSE (Sales_Quantity / @DaysTrading) * DoS_Target END;
UPDATE #Products SET Cases_Target_Units = Product_CasePackUnits * Cases_Target;
SELECT Location_Code, Category_Code, SUM(Sales_Quantity) * CASE WHEN @UseTrend = 'Y' THEN (1 + AVG(Trend)) ELSE 1 END AS Sales_Quantity, SUM(Sales_Value) * CASE WHEN @UseTrend = 'Y' THEN (1 + AVG(Trend)) ELSE 1 END AS Sales_Value, SUM(Profit_Value) * CASE WHEN @UseTrend = 'Y' THEN (1 + AVG(Trend)) ELSE 1 END AS Profit_Value, SUM(Sales_Less_Waste) * CASE WHEN @UseTrend = 'Y' THEN (1 + AVG(Trend)) ELSE 1 END AS Sales_Less_Waste, SUM(Sales_Quantity) AS Org_Sales_Quantity, SUM(Sales_Value) AS Org_Sales_Value, SUM(Profit_Value) AS Org_Profit_Value, SUM(Sales_Less_Waste) AS Org_Sales_Less_Waste, AVG(Trend) AS Avg_Trend INTO #MacroHierMicroPerfAvgSumGrp FROM #MacroHierMicroPerfAvg GROUP BY Location_Code, Category_Code;
SELECT mh.Location_Code, mh.Category_Code, mh.Category_Name, mh.Department_Number, 'a' AS Department_Sub, mh.Flow_Number, mh.Department_Name, mh.Temp_Category_Number, mh.Temp_Category_Name, ps.Avg_Trend, (ps.Sales_Quantity / Bays) * 0.001 AS Sales_Quantity, (ps.Sales_Value / Bays) * 0.001 AS Sales_Value, (ps.Profit_Value / Bays) * 0.001 AS Profit_Value, (ps.Sales_Less_Waste / Bays) * 0.001 AS Sales_Less_Waste, (ps.Org_Sales_Quantity / Bays) * 0.001 AS Org_Sales_Quantity, (ps.Org_Sales_Value / Bays) * 0.001 AS Org_Sales_Value, (ps.Org_Profit_Value / Bays) * 0.001 AS Org_Profit_Value, (ps.Org_Sales_Less_Waste / Bays) * 0.001 AS Org_Sales_Less_Waste, 0.001 AS Bays, mh.Min_Bays, mh.Max_Bays, 'N' AS Exclude_From_Analysis, mh.DoS_Target, mh.Cases_Target, 0.001 AS Org_Bays INTO #Category FROM #MacroHier mh JOIN #MacroHierMicroPerfAvgSumGrp ps ON mh.Category_Code = ps.Category_Code;
WITH TargetSums AS ( SELECT Category_Code, ROUND(AVG(DoS_Target), 2) AS AvgDoSTarget, ROUND(AVG(Cases_Target), 2) AS AvgCasesTarget FROM #Products GROUP BY Category_Code ) UPDATE sc SET sc.DoS_Target = ts.AvgDoSTarget, sc.Cases_Target = ts.AvgCasesTarget FROM #Category sc JOIN TargetSums ts ON sc.Category_Code = ts.Category_Code;
ALTER TABLE #Category ADD UnitSalesMix_UserSet FLOAT, ValueSalesMix_UserSet FLOAT, ProfitSalesMix_UserSet FLOAT, SalesLessWaste_UserSet FLOAT, SalesProfitMix_UserSet FLOAT, SalesMixCube_UserSet FLOAT, DOSCasePack_UserSet FLOAT, FixtureDensity_UserSet FLOAT, MixIncCohort_UserSet FLOAT, BayRoundingThreshold FLOAT, Category_QuantCont_Pt FLOAT, Depart_QuantCont_Pt FLOAT, Temp_QuantCont_Pt FLOAT, Category_ValueCont_Pt FLOAT, Depart_ValueCont_Pt FLOAT, Temp_ValueCont_Pt FLOAT, Category_ProfitCont_Pt FLOAT, Depart_ProfitCont_Pt FLOAT, Temp_ProfitCont_Pt FLOAT, Category_WasteCont_Pt FLOAT, Depart_WasteCont_Pt FLOAT, Temp_WasteCont_Pt FLOAT, Store_PerfMix_Pt FLOAT, Dept_PerfMix_Pt FLOAT, Temp_PerfMix_Pt FLOAT, Store_BaysPerfMix FLOAT, Dept_BaysPerfMix FLOAT, Temp_BaysPerfMix FLOAT, Store_Ranking INT, Dept_Ranking INT, Temp_Ranking INT, Sum_Cubes FLOAT, Products_Count FLOAT, Cat_ProdCube_Avg FLOAT, Store_ProdCube_Avg FLOAT, Dept_ProdCube_Avg FLOAT, Temp_ProdCube_Avg FLOAT, Store_ProdCat_Avg FLOAT, Dept_ProdCat_Avg FLOAT, Temp_ProdCat_Avg FLOAT, Store_ProdCubeRatio FLOAT, Dept_ProdCubeRatio FLOAT, Temp_ProdCubeRatio FLOAT, Store_BaysFlex_PerfMix FLOAT, Dept_BaysFlex_PerfMix FLOAT, Temp_BaysFlex_PerfMix FLOAT, Bays_Req_Target FLOAT, Org_Bays_Req_Target FLOAT, Store_DOSCOS_Bays FLOAT, Dept_DOSCOS_Bays FLOAT, Temp_DOSCOS_Bays FLOAT, Store_Bays_Recc FLOAT, Dept_Bays_Recc FLOAT, Temp_Bays_Recc FLOAT, Org_Store_Bays_Recc FLOAT, Org_Dept_Bays_Recc FLOAT, Org_Temp_Bays_Recc FLOAT, DoSTargetAchieved FLOAT, CaseTargetAchieved FLOAT, BothTargetAchieved FLOAT, Store_Projected_Sales_Quantity FLOAT, Store_Projected_Org_Sales_Quantity FLOAT, Store_Projected_Sales_Value FLOAT, Store_Projected_Org_Sales_Value FLOAT, Store_Projected_Profit_Value FLOAT, Store_Projected_Org_Profit_Value FLOAT, Store_Projected_Sales_Less_Waste FLOAT, Store_Projected_Org_Sales_Less_Waste FLOAT, Dept_Projected_Sales_Quantity FLOAT, Dept_Projected_Org_Sales_Quantity FLOAT, Dept_Projected_Sales_Value FLOAT, Dept_Projected_Org_Sales_Value FLOAT, Dept_Projected_Profit_Value FLOAT, Dept_Projected_Org_Profit_Value FLOAT, Dept_Projected_Sales_Less_Waste FLOAT, Dept_Projected_Org_Sales_Less_Waste FLOAT, Temp_Projected_Sales_Quantity FLOAT, Temp_Projected_Org_Sales_Quantity FLOAT, Temp_Projected_Sales_Value FLOAT, Temp_Projected_Org_Sales_Value FLOAT, Temp_Projected_Profit_Value FLOAT, Temp_Projected_Org_Profit_Value FLOAT, Temp_Projected_Sales_Less_Waste FLOAT, Temp_Projected_Org_Sales_Less_Waste FLOAT;
