CREATE   PROCEDURE [ui].[CheckReportExists]
    @ReportID INT,
    @ReportFound BIT OUTPUT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        BEGIN TRANSACTION;

        -- Step 1: Drop the DepartmentColours table if it exists
        IF OBJECT_ID('[dbo].[DepartmentColours]', 'U') IS NOT NULL
            DROP TABLE [dbo].[DepartmentColours];

        -- Step 2: Create the DepartmentColours table
        CREATE TABLE [dbo].[DepartmentColours] (
            DepartmentNumber INT PRIMARY KEY,
            R INT,
            G INT,
            B INT
        );

        -- Step 3: Insert distinct department numbers and random RGB values into DepartmentColours
        WITH DistinctDepartments AS (
            SELECT DISTINCT Department_Number
            FROM [dbo].[User Managed Data]
        )
        INSERT INTO [dbo].[DepartmentColours] (DepartmentNumber, R, G, B)
        SELECT 
            Department_Number,
            ABS(CHECKSUM(NEWID())) % 255 + 1 AS R,
            ABS(CHECKSUM(NEWID())) % 255 + 1 AS G,
            ABS(CHECKSUM(NEWID())) % 255 + 1 AS B
        FROM 
            DistinctDepartments;

        COMMIT TRANSACTION;
    END TRY
    BEGIN CATCH
        ROLLBACK TRANSACTION;
        THROW;
    END CATCH
END;

BEGIN SET NOCOUNT ON; SET XACT_ABORT ON;
-- Initialize outputs SET @SQL_Success = 0; SET @SQL_Message = N'Procedure failed to complete.';
DECLARE @NewReportID INT;
BEGIN TRY -- Generate a new ReportID safely BEGIN TRAN;
SELECT @NewReportID = ISNULL(MAX(ReportID), 0) + 1 FROM dbo.ReportVersionAttributes WITH (TABLOCKX, HOLDLOCK);
-- Insert new row with the generated ReportID INSERT INTO dbo.ReportVersionAttributes ( ReportID, ReportOwner, BranchNumber, ReportName, PerfStartDate, PerfEndDate, MacroSpaceDate, Cohort, CohortStores, BranchName, FullBaySize, RoundBaySize, UnitOfMeasure, DaysTrading, ViewbyStoreTempcatDept, MinBaysforMaxRange, UseProductCube, UseTrend, UseMinLMtr, UseMaxLMtr, IncNumberofProducts, PotentialSalesAdjustment, CubeFlex, TopHighlight, BottomHighlight, UnitSalesMix_UserSet, ValueSalesMix_UserSet, ProfitSalesMix_UserSet, SalesLessWaste_UserSet, SalesProfitMix_UserSet, SalesMixCube_UserSet, DOSCasePack_UserSet, FixtureDensity_UserSet, MixIncCohort_UserSet, SpaceAdjustment, ElasticitySalesDiff, DensityMetric, UMDID, DoSDefault, CasesDefault, BayRoundingThreshold, PerfDateExclusions, ExcludeCategories, RefreshTypeNeeded, BayRulesCount, LastRefreshed, CoreTablesCreated, Status, Cluster_ID ) SELECT @NewReportID, @NewOwnerID, BranchNumber, @NewReportName, PerfStartDate, PerfEndDate, MacroSpaceDate, Cohort, CohortStores, BranchName, FullBaySize, RoundBaySize, UnitOfMeasure, DaysTrading, ViewbyStoreTempcatDept, MinBaysforMaxRange, UseProductCube, UseTrend, UseMinLMtr, UseMaxLMtr, IncNumberofProducts, PotentialSalesAdjustment, CubeFlex, TopHighlight, BottomHighlight, UnitSalesMix_UserSet, ValueSalesMix_UserSet, ProfitSalesMix_UserSet, SalesLessWaste_UserSet, SalesProfitMix_UserSet, SalesMixCube_UserSet, DOSCasePack_UserSet, FixtureDensity_UserSet, MixIncCohort_UserSet, SpaceAdjustment, ElasticitySalesDiff, DensityMetric, UMDID, DoSDefault, CasesDefault, BayRoundingThreshold, PerfDateExclusions, ExcludeCategories, RefreshTypeNeeded, BayRulesCount, LastRefreshed, CoreTablesCreated, Status, Cluster_ID FROM dbo.ReportVersionAttributes WHERE ReportID = @OldReportID;
-- Copy BayRules from old report to new INSERT INTO dbo.BayRules (ReportID, Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key) SELECT @NewReportID, Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key FROM dbo.BayRules WHERE ReportID = @OldReportID AND Group_Key IN ('Drivers', 'Metrics', 'Rounding', 'Resize');
COMMIT;
SET @SQL_Success = 1; SET @SQL_Message = N'Procedure completed successfully. New ReportID: ' + CONVERT(NVARCHAR(10), @NewReportID); END TRY BEGIN CATCH IF XACT_STATE() <> 0 ROLLBACK; SET @SQL_Success = 0; SET @SQL_Message = ERROR_MESSAGE(); END CATCH END

CREATE PROCEDURE [ui].[DuplicateReportVersionAttribute2]
    @OldReportID INT,
    @NewReportName NVARCHAR(MAX),
    @NewOwnerID NVARCHAR(4),
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(200) OUTPUT
AS

BEGIN
    BEGIN TRY
        DECLARE @BranchCount INT;
        DECLARE @OutputTable TABLE (
            Branch_Number INT,
            Branch_Name VARCHAR(255),
            Cohort VARCHAR(255),
            BranchCountInCohort INT
        );

        INSERT INTO @OutputTable
    SELECT 
        Location_Code AS Branch_Number,
        Location_Name AS Branch_Name,
        Cluster_Name AS Cohort,
        1 AS BranchCountInCohort
     FROM LocationsFull;


        SET @BranchCount = (SELECT COUNT(*) FROM @OutputTable);

        -- Set SQL_Success and SQL_Message output parameters
        IF @BranchCount > 0
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = CONCAT('Success: ', @BranchCount, ' branch(es) included in the output.');
        END
        ELSE
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = 'No branches found for the given UMDID.';
        END

        -- Return the result set
        SELECT * FROM @OutputTable;
    END TRY
    BEGIN CATCH
        SET @SQL_Success = 0;
        SET @SQL_Message = ERROR_MESSAGE();
    END CATCH
END

CREATE   PROCEDURE [ui].[GetBranchDetails]
    @SQL_Success BIT OUTPUT,
    @SQL_Message VARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
-- Initialize output parameters SET @SQL_Success = 0; SET @SQL_Message = 'Procedure started...';
BEGIN TRY --==================================== -- 1. Return full hierarchy (Table #1) --==================================== SELECT Hierarchy1_Code, Hierarchy1_Name, Hierarchy2_Code, Hierarchy2_Name, Hierarchy3_Code, Hierarchy3_Name, Trend, MinBays, MaxBays, Exclude_From_Analysis, FlowNumber FROM HierarchyDetail WHERE HierarchyVersion_ID = @HierVer ORDER BY FlowNumber ASC;
--=================================================== -- 2. Return distinct CategoryCodes NOT in Hierarchy3 -- from MacroSnapShot (Table #2) --=================================================== SELECT DISTINCT MS.Category_Code FROM MacroSnapShot AS MS WHERE MS.Create_Date = @MacroDate AND MS.Category_Code NOT IN ( SELECT HD.Hierarchy3_Code FROM HierarchyDetail AS HD WHERE HD.HierarchyVersion_ID = @HierVer );
--============================================ -- 3. Return all records from Hierarchy1_Child (Table #3) --============================================ SELECT * FROM Hierarchy1_Child WHERE HierarchyVersion_ID = @HierVer;
--============================================ -- 4. Return all records from Hierarchy2_Child (Table #4) --============================================ SELECT * FROM Hierarchy2_Child WHERE HierarchyVersion_ID = @HierVer;
--============================================ -- 5. Return all records from Hierarchy3 (Table #5) --============================================ SELECT * FROM Hierarchy3 WHERE HierarchyVersion_ID = @HierVer;
-- If everything succeeds: SET @SQL_Success = 1; SET @SQL_Message = 'Hierarchy, snapshot categories, and child hierarchies successfully retrieved.'; END TRY BEGIN CATCH SET @SQL_Success = 0; SET @SQL_Message = CONCAT('There was a problem, error code: ', ERROR_MESSAGE()); END CATCH; END

CREATE   PROCEDURE [ui].[HierMan_GetFullHierarchyCurrent]
    @MacroDate   DATE,
    @HierVer     INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON; SET XACT_ABORT ON; -- auto-rollback on error
-- init outputs SET @SQL_Success = 0; SET @SQL_Message = N''; SET @NewHierarchyVersion_ID = NULL;
DECLARE @CopiedCounts NVARCHAR(200) = N'';
------------------------------------------------------------ -- Basic validation + normalization ------------------------------------------------------------ SET @VersionName = NULLIF(LTRIM(RTRIM(@VersionName)), N''); SET @VersionStatus = COALESCE(NULLIF(LTRIM(RTRIM(@VersionStatus)), N''), N'Draft'); SET @CreatedBy = NULLIF(LTRIM(RTRIM(@CreatedBy)), N'');
IF @VersionName IS NULL BEGIN SET @SQL_Message = N'VersionName is required.'; RETURN; END;
-- Validate status IF @VersionStatus NOT IN (N'Draft', N'Active', N'Archived') BEGIN SET @SQL_Message = N'VersionStatus must be ''Draft'', ''Active'', or ''Archived''.'; RETURN; END;
-- Check for duplicate name IF EXISTS ( SELECT 1 FROM dbo.HierarchyVersion WHERE HierarchyVersion_Name = @VersionName ) BEGIN SET @SQL_Message = N'A hierarchy version with name "' + @VersionName + N'" already exists.'; RETURN; END;
-- Validate duplicate source if specified IF @DuplicateFromExisting_ID IS NOT NULL AND @DuplicateFromExisting_ID > 0 BEGIN IF NOT EXISTS ( SELECT 1 FROM dbo.HierarchyVersion WHERE HierarchyVersion_ID = @DuplicateFromExisting_ID ) BEGIN SET @SQL_Message = N'Source hierarchy version (ID=' + CAST(@DuplicateFromExisting_ID AS NVARCHAR(10)) + N') does not exist.'; RETURN; END; END;
------------------------------------------------------------ -- Create new hierarchy version ------------------------------------------------------------ BEGIN TRY BEGIN TRAN;
-- Insert the new hierarchy version INSERT INTO dbo.HierarchyVersion ( HierarchyVersion_Name, HierarchyVersion_Status, HierarchyVersion_CreatedBy, HierarchyVersionCreatedDate ) VALUES ( @VersionName, @VersionStatus, @CreatedBy, GETDATE() );
SET @NewHierarchyVersion_ID = CONVERT(INT, SCOPE_IDENTITY());
-- If duplicating from existing version, copy all hierarchy data IF @DuplicateFromExisting_ID IS NOT NULL AND @DuplicateFromExisting_ID > 0 BEGIN DECLARE @H1_Mapping TABLE ( OldH1_ID INT, NewH1_ID INT, OldH1_Code INT );
DECLARE @H2_Mapping TABLE ( OldH2_ID INT, NewH2_ID INT, OldH2_Code INT );
DECLARE @NextH1_ID INT; DECLARE @NextH2_ID INT; DECLARE @NextH3_ID INT; DECLARE @CopiedH1Count INT = 0; DECLARE @CopiedH2Count INT = 0; DECLARE @CopiedH3Count INT = 0;
SELECT @NextH1_ID = ISNULL(MAX(Hierarchy1_ID), 0) + 1 FROM dbo.Hierarchy1;
DECLARE @H1_Source TABLE ( OldH1_ID INT, NewH1_ID INT, Hierarchy1_Code INT, Hierarchy1_Name NVARCHAR(255), Trend DECIMAL(5,3), MinBays DECIMAL(5,1), MaxBays DECIMAL(5,1), DOS DECIMAL(5,1), COS DECIMAL(5,1), Exclude_From_Analysis VARCHAR(1), BayRoundingThreshold DECIMAL(5,1), Metric1 INT, Metric2 INT, Metric3 INT, Metric4 INT, Driver1 INT, Driver2 INT, Driver3 INT, Driver4 INT );
WITH SourceH1 AS ( SELECT h1.Hierarchy1_ID AS OldH1_ID, h1.Hierarchy1_Code, h1.Hierarchy1_Name, h1.Trend, h1.MinBays, h1.MaxBays, h1.DOS, h1.COS, h1.Exclude_From_Analysis, h1.BayRoundingThreshold, h1.Metric1, h1.Metric2, h1.Metric3, h1.Metric4, h1.Driver1, h1.Driver2, h1.Driver3, h1.Driver4, ROW_NUMBER() OVER (ORDER BY h1.Hierarchy1_ID) - 1 AS RowNum FROM dbo.Hierarchy1 h1 WHERE h1.HierarchyVersion_ID = @DuplicateFromExisting_ID ) INSERT INTO @H1_Source ( OldH1_ID, NewH1_ID, Hierarchy1_Code, Hierarchy1_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT SourceH1.OldH1_ID, @NextH1_ID + SourceH1.RowNum, SourceH1.Hierarchy1_Code, SourceH1.Hierarchy1_Name, SourceH1.Trend, SourceH1.MinBays, SourceH1.MaxBays, SourceH1.DOS, SourceH1.COS, SourceH1.Exclude_From_Analysis, SourceH1.BayRoundingThreshold, SourceH1.Metric1, SourceH1.Metric2, SourceH1.Metric3, SourceH1.Metric4, SourceH1.Driver1, SourceH1.Driver2, SourceH1.Driver3, SourceH1.Driver4 FROM SourceH1;
INSERT INTO dbo.Hierarchy1 ( Hierarchy1_ID, HierarchyVersion_ID, Hierarchy1_Code, Hierarchy1_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT H1Source.NewH1_ID, @NewHierarchyVersion_ID, H1Source.Hierarchy1_Code, H1Source.Hierarchy1_Name, H1Source.Trend, H1Source.MinBays, H1Source.MaxBays, H1Source.DOS, H1Source.COS, H1Source.Exclude_From_Analysis, H1Source.BayRoundingThreshold, H1Source.Metric1, H1Source.Metric2, H1Source.Metric3, H1Source.Metric4, H1Source.Driver1, H1Source.Driver2, H1Source.Driver3, H1Source.Driver4 FROM @H1_Source AS H1Source;
INSERT INTO @H1_Mapping (OldH1_ID, NewH1_ID, OldH1_Code) SELECT OldH1_ID, NewH1_ID, Hierarchy1_Code FROM @H1_Source;
SELECT @CopiedH1Count = COUNT(*) FROM @H1_Source;
SELECT @NextH2_ID = ISNULL(MAX(Hierarchy2_ID), 0) + 1 FROM dbo.Hierarchy2;
DECLARE @H2_Source TABLE ( OldH2_ID INT, NewH2_ID INT, ParentOldH1_ID INT, Hierarchy2_Code INT, Hierarchy2_Name NVARCHAR(255), Trend DECIMAL(5,3), MinBays DECIMAL(5,1), MaxBays DECIMAL(5,1), DOS DECIMAL(5,1), COS DECIMAL(5,1), Exclude_From_Analysis VARCHAR(1), BayRoundingThreshold DECIMAL(5,1), Metric1 INT, Metric2 INT, Metric3 INT, Metric4 INT, Driver1 INT, Driver2 INT, Driver3 INT, Driver4 INT );
WITH SourceH2 AS ( SELECT h2.Hierarchy2_ID AS OldH2_ID, h2.ParentHierarchy_ID, h2.Hierarchy2_Code, h2.Hierarchy2_Name, h2.Trend, h2.MinBays, h2.MaxBays, h2.DOS, h2.COS, h2.Exclude_From_Analysis, h2.BayRoundingThreshold, h2.Metric1, h2.Metric2, h2.Metric3, h2.Metric4, h2.Driver1, h2.Driver2, h2.Driver3, h2.Driver4, ROW_NUMBER() OVER (ORDER BY h2.Hierarchy2_ID) - 1 AS RowNum FROM dbo.Hierarchy2 h2 WHERE h2.HierarchyVersion_ID = @DuplicateFromExisting_ID ) INSERT INTO @H2_Source ( OldH2_ID, NewH2_ID, ParentOldH1_ID, Hierarchy2_Code, Hierarchy2_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT SourceH2.OldH2_ID, @NextH2_ID + SourceH2.RowNum, SourceH2.ParentHierarchy_ID, SourceH2.Hierarchy2_Code, SourceH2.Hierarchy2_Name, SourceH2.Trend, SourceH2.MinBays, SourceH2.MaxBays, SourceH2.DOS, SourceH2.COS, SourceH2.Exclude_From_Analysis, SourceH2.BayRoundingThreshold, SourceH2.Metric1, SourceH2.Metric2, SourceH2.Metric3, SourceH2.Metric4, SourceH2.Driver1, SourceH2.Driver2, SourceH2.Driver3, SourceH2.Driver4 FROM SourceH2;
INSERT INTO dbo.Hierarchy2 ( Hierarchy2_ID, HierarchyVersion_ID, ParentHierarchy_ID, Hierarchy2_Code, Hierarchy2_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT H2Source.NewH2_ID, @NewHierarchyVersion_ID, h1_map.NewH1_ID, H2Source.Hierarchy2_Code, H2Source.Hierarchy2_Name, H2Source.Trend, H2Source.MinBays, H2Source.MaxBays, H2Source.DOS, H2Source.COS, H2Source.Exclude_From_Analysis, H2Source.BayRoundingThreshold, H2Source.Metric1, H2Source.Metric2, H2Source.Metric3, H2Source.Metric4, H2Source.Driver1, H2Source.Driver2, H2Source.Driver3, H2Source.Driver4 FROM @H2_Source AS H2Source INNER JOIN @H1_Mapping h1_map ON H2Source.ParentOldH1_ID = h1_map.OldH1_ID;
INSERT INTO @H2_Mapping (OldH2_ID, NewH2_ID, OldH2_Code) SELECT OldH2_ID, NewH2_ID, Hierarchy2_Code FROM @H2_Source;
SELECT @CopiedH2Count = COUNT(*) FROM @H2_Source;
SELECT @NextH3_ID = ISNULL(MAX(Hierarchy3_ID), 0) + 1 FROM dbo.Hierarchy3;
DECLARE @H3_Source TABLE ( OldH3_ID INT, NewH3_ID INT, ParentOldH2_ID INT, Hierarchy3_Code INT, Hierarchy3_Name NVARCHAR(255), Trend DECIMAL(5,3), MinBays DECIMAL(5,1), MaxBays DECIMAL(5,1), DOS DECIMAL(5,1), COS DECIMAL(5,1), Exclude_From_Analysis VARCHAR(1), FlowNumber INT, BayRoundingThreshold DECIMAL(5,1), Metric1 INT, Metric2 INT, Metric3 INT, Metric4 INT, Driver1 INT, Driver2 INT, Driver3 INT, Driver4 INT );
WITH SourceH3 AS ( SELECT h3.Hierarchy3_ID AS OldH3_ID, h3.ParentHierarchy_ID, h3.Hierarchy3_Code, h3.Hierarchy3_Name, h3.Trend, h3.MinBays, h3.MaxBays, h3.DOS, h3.COS, h3.Exclude_From_Analysis, h3.FlowNumber, h3.BayRoundingThreshold, h3.Metric1, h3.Metric2, h3.Metric3, h3.Metric4, h3.Driver1, h3.Driver2, h3.Driver3, h3.Driver4, ROW_NUMBER() OVER (ORDER BY h3.Hierarchy3_ID) - 1 AS RowNum FROM dbo.Hierarchy3 h3 WHERE h3.HierarchyVersion_ID = @DuplicateFromExisting_ID ) INSERT INTO @H3_Source ( OldH3_ID, NewH3_ID, ParentOldH2_ID, Hierarchy3_Code, Hierarchy3_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, FlowNumber, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT SourceH3.OldH3_ID, @NextH3_ID + SourceH3.RowNum, SourceH3.ParentHierarchy_ID, SourceH3.Hierarchy3_Code, SourceH3.Hierarchy3_Name, SourceH3.Trend, SourceH3.MinBays, SourceH3.MaxBays, SourceH3.DOS, SourceH3.COS, SourceH3.Exclude_From_Analysis, SourceH3.FlowNumber, SourceH3.BayRoundingThreshold, SourceH3.Metric1, SourceH3.Metric2, SourceH3.Metric3, SourceH3.Metric4, SourceH3.Driver1, SourceH3.Driver2, SourceH3.Driver3, SourceH3.Driver4 FROM SourceH3;
INSERT INTO dbo.Hierarchy3 ( Hierarchy3_ID, HierarchyVersion_ID, ParentHierarchy_ID, Hierarchy3_Code, Hierarchy3_Name, Trend, MinBays, MaxBays, DOS, COS, Exclude_From_Analysis, FlowNumber, BayRoundingThreshold, Metric1, Metric2, Metric3, Metric4, Driver1, Driver2, Driver3, Driver4 ) SELECT H3Source.NewH3_ID, @NewHierarchyVersion_ID, h2_map.NewH2_ID, H3Source.Hierarchy3_Code, H3Source.Hierarchy3_Name, H3Source.Trend, H3Source.MinBays, H3Source.MaxBays, H3Source.DOS, H3Source.COS, H3Source.Exclude_From_Analysis, H3Source.FlowNumber, H3Source.BayRoundingThreshold, H3Source.Metric1, H3Source.Metric2, H3Source.Metric3, H3Source.Metric4, H3Source.Driver1, H3Source.Driver2, H3Source.Driver3, H3Source.Driver4 FROM @H3_Source AS H3Source INNER JOIN @H2_Mapping h2_map ON H3Source.ParentOldH2_ID = h2_map.OldH2_ID;
SELECT @CopiedH3Count = COUNT(*) FROM @H3_Source;
SET @CopiedCounts = N' Copied: ' + CAST(@CopiedH3Count AS NVARCHAR(10)) + N' H3, ' + CAST(@CopiedH2Count AS NVARCHAR(10)) + N' H2, ' + CAST(@CopiedH1Count AS NVARCHAR(10)) + N' H1 items.'; END; ELSE BEGIN SET @CopiedCounts = N' Created as empty version.'; END;
COMMIT TRAN;
SET @SQL_Success = 1; SET @SQL_Message = N'Hierarchy version "' + @VersionName + N'" created successfully (ID=' + CAST(@NewHierarchyVersion_ID AS NVARCHAR(10)) + N').' + @CopiedCounts;
END TRY BEGIN CATCH IF XACT_STATE() <> 0 ROLLBACK TRAN;
SET @SQL_Success = 0; SET @SQL_Message = CONCAT( N'Error ', ERROR_NUMBER(), N' at line ', ERROR_LINE(), N': ', ERROR_MESSAGE() ); RETURN; END CATCH; END

CREATE   PROCEDURE [ui].[HierMan2_CreateHierarchyVersion]
    @VersionName                 NVARCHAR(255)    = NULL,      -- required
    @VersionStatus              NVARCHAR(50)     = 'Draft',    -- default to Draft
    @CreatedBy                  NVARCHAR(255)    = NULL,       -- user email
    @DuplicateFromExisting_ID   INT              = NULL,       -- 0 or NULL = empty, >0 = duplicate from
    @NewHierarchyVersion_ID     INT              OUTPUT,       -- Match donor app naming
    @SQL_Success                BIT              OUTPUT,
    @SQL_Message                NVARCHAR(4000)   OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    SET @SQL_Success = 0;
    SET @SQL_Message = '';

    BEGIN TRY
        DECLARE @ChildCount INT;

        SELECT @ChildCount = Child_H2
        FROM Hierarchy1_Child
        WHERE Hierarchy1_ID = @Hier1_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        IF @ChildCount IS NULL
        BEGIN
            SET @SQL_Message = 'Failure: Entry not found with the provided ID and version.';
            RETURN;
        END

        IF @ChildCount > 0
        BEGIN
            SET @SQL_Message = 'Deletion aborted: This Hierarchy1 record has associated Hierarchy2 child records.';
            RETURN;
        END

        DELETE FROM Hierarchy1 
        WHERE Hierarchy1_ID = @Hier1_ID 
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        SET @SQL_Success = 1;
        SET @SQL_Message = 'Success: Entry deleted successfully.';
    END TRY
    BEGIN CATCH
        SET @SQL_Message = 'An error occurred: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[HierMan2_Delete1]
    @Hier1_ID INT,
    @HierarchyVersion_ID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    SET @SQL_Success = 0;
    SET @SQL_Message = '';

    BEGIN TRY
        DECLARE @ChildCount INT;

        SELECT @ChildCount = Child_H3
        FROM Hierarchy2_Child
        WHERE Hierarchy2_ID = @Hier2_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        IF @ChildCount IS NULL
        BEGIN
            SET @SQL_Message = 'Failure: Entry not found with the provided ID and version.';
            RETURN;
        END

        IF @ChildCount > 0
        BEGIN
            SET @SQL_Message = 'Deletion aborted: This Hierarchy2 record has associated Hierarchy3 child records.';
            RETURN;
        END

        DELETE FROM Hierarchy2
        WHERE Hierarchy2_ID = @Hier2_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        SET @SQL_Success = 1;
        SET @SQL_Message = 'Success: Entry deleted successfully.';
    END TRY
    BEGIN CATCH
        SET @SQL_Message = 'An error occurred: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[HierMan2_Delete2]
    @Hier2_ID INT,
    @HierarchyVersion_ID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    SET @SQL_Success = 0;
    SET @SQL_Message = '';

    BEGIN TRY
        IF EXISTS (
            SELECT 1 
            FROM Hierarchy3 
            WHERE Hierarchy3_ID = @Hier3_ID
              AND HierarchyVersion_ID = @HierarchyVersion_ID
        )
        BEGIN
            DELETE FROM Hierarchy3 
            WHERE Hierarchy3_ID = @Hier3_ID
              AND HierarchyVersion_ID = @HierarchyVersion_ID;

            SET @SQL_Success = 1;
            SET @SQL_Message = 'Success: Entry deleted successfully.';
        END
        ELSE
        BEGIN
            SET @SQL_Message = 'Failure: Entry not found with the provided ID and version.';
        END
    END TRY
    BEGIN CATCH
        SET @SQL_Message = 'An error occurred: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[HierMan2_Delete3]
    @Hier3_ID INT,
    @HierarchyVersion_ID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;  -- auto-rollback on error

    -- init outputs
    SET @SQL_Success = 0;
    SET @SQL_Message = N'';

    ------------------------------------------------------------
    -- Basic validation
    ------------------------------------------------------------
    IF @HierarchyVersion_ID IS NULL OR @HierarchyVersion_ID <= 0
    BEGIN
        SET @SQL_Message = N'HierarchyVersion_ID is required and must be greater than 0.';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Check if version exists
    ------------------------------------------------------------
    DECLARE @VersionName NVARCHAR(255);
    DECLARE @VersionStatus NVARCHAR(50);

    SELECT @VersionName = HierarchyVersion_Name, @VersionStatus = HierarchyVersion_Status
    FROM dbo.HierarchyVersion
    WHERE HierarchyVersion_ID = @HierarchyVersion_ID;

    IF @VersionName IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy version with ID ' + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N' not found.';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Prevent deletion of Active versions (business rule)
    ------------------------------------------------------------
    IF @VersionStatus = N'Active'
    BEGIN
        SET @SQL_Message = N'Cannot delete Active hierarchy version ''' + @VersionName + N'''. Please change status to Draft or Archived first.';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Check for child records count (for informational message)
    ------------------------------------------------------------
    DECLARE @H1Count INT = 0, @H2Count INT = 0, @H3Count INT = 0;

    SELECT @H1Count = COUNT(*) FROM dbo.Hierarchy1 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;
    SELECT @H2Count = COUNT(*) FROM dbo.Hierarchy2 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;
    SELECT @H3Count = COUNT(*) FROM dbo.Hierarchy3 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;

    ------------------------------------------------------------
    -- Delete operation (cascade from parent to children)
    ------------------------------------------------------------
    BEGIN TRY
        BEGIN TRANSACTION;

        -- Delete in proper order: H3 -> H2 -> H1 -> HierarchyVersion
        -- This ensures referential integrity

        DELETE FROM dbo.Hierarchy3 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;
        DELETE FROM dbo.Hierarchy2 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;  
        DELETE FROM dbo.Hierarchy1 WHERE HierarchyVersion_ID = @HierarchyVersion_ID;
        DELETE FROM dbo.HierarchyVersion WHERE HierarchyVersion_ID = @HierarchyVersion_ID;

        COMMIT TRANSACTION;

        -- Success message with deletion summary
        SET @SQL_Success = 1;
        SET @SQL_Message = N'Hierarchy version ''' + @VersionName + N''' deleted successfully. ' +
                          N'Removed: ' + CAST(@H1Count AS NVARCHAR(10)) + N' H1 items, ' +
                          CAST(@H2Count AS NVARCHAR(10)) + N' H2 items, ' +
                          CAST(@H3Count AS NVARCHAR(10)) + N' H3 items.';

    END TRY
    BEGIN CATCH
        -- Rollback transaction on error
        IF @@TRANCOUNT > 0
            ROLLBACK TRANSACTION;

        -- Return error details
        SET @SQL_Success = 0;
        SET @SQL_Message = N'Error deleting hierarchy version ''' + @VersionName + N''': ' + ERROR_MESSAGE();

        -- Log error details for troubleshooting
        DECLARE @ErrorDetails NVARCHAR(4000);
        SET @ErrorDetails = N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10)) + 
                           N' at Line ' + CAST(ERROR_LINE() AS NVARCHAR(10)) + 
                           N': ' + ERROR_MESSAGE();

        -- Could log to error table here if needed
        PRINT @ErrorDetails;

    END CATCH;
END;

CREATE PROCEDURE [ui].[HierMan2_DeleteHierarchyVersion]
    @HierarchyVersion_ID        INT              = NULL,       -- required: version to delete
    @SQL_Success                BIT              OUTPUT,
    @SQL_Message                NVARCHAR(4000)   OUTPUT
AS

BEGIN
    -- Prevent extra result sets from interfering with SELECT statements
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = '';

    BEGIN TRY
        -- Check if the table exists first (optional defensive programming)
        IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'HierarchyVersion')
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = 'Error: Table HierarchyVersion does not exist.';
            RETURN -1;
        END

        -- Main query to get active versions
        SELECT 
            HierarchyVersion_ID AS [ID], 
            HierarchyVersion_Name AS [Name]
        FROM 
            dbo.HierarchyVersion 
        WHERE 
            HierarchyVersion_Status = 'Active'
        ORDER BY 
            HierarchyVersionCreatedDate DESC; -- Optional: Add ordering for consistent results

        -- Get row count for feedback
        DECLARE @RowCount INT = @@ROWCOUNT;

        -- Set success parameters
        SET @SQL_Success = 1;
        SET @SQL_Message = CONCAT('Successfully retrieved ', @RowCount, ' active hierarchy version(s).');

        -- Return success code
        RETURN 0;

    END TRY
    BEGIN CATCH
        -- Capture error information
        DECLARE @ErrorNumber INT = ERROR_NUMBER();
        DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
        DECLARE @ErrorState INT = ERROR_STATE();
        DECLARE @ErrorProcedure NVARCHAR(128) = ERROR_PROCEDURE();
        DECLARE @ErrorLine INT = ERROR_LINE();
        DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();

        -- Set failure parameters
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            'Error ', @ErrorNumber, 
            ' in procedure ', ISNULL(@ErrorProcedure, 'HierMan2_GetActiveVersions'),
            ' at line ', @ErrorLine,
            ': ', @ErrorMessage
        );


        -- Return error code
        RETURN -1;

    END CATCH
END;

CREATE   PROCEDURE [ui].[HierMan2_GetActiveVersions]
    @SQL_Success INT OUTPUT,
    @SQL_Message NVARCHAR(MAX) OUTPUT
AS

BEGIN
    -- Prevent extra result sets from interfering with SELECT statements
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = '';

    BEGIN TRY
        -- Check if the table exists first (optional defensive programming)
        IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'HierarchyVersion')
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = 'Error: Table HierarchyVersion does not exist.';
            RETURN -1;
        END

        -- Main query to get active versions
        SELECT 
            HierarchyVersion_ID AS [ID], 
            HierarchyVersion_Name AS [Name],
            HierarchyVersion_Status AS [Status],
            HierarchyVersion_CreatedBy AS [CreatedBy],
            HierarchyVersionCreatedDate AS [CreatedDate]
        FROM 
            dbo.HierarchyVersion 

        ORDER BY 
            HierarchyVersionCreatedDate DESC; -- Optional: Add ordering for consistent results

        -- Get row count for feedback
        DECLARE @RowCount INT = @@ROWCOUNT;

        -- Set success parameters
        SET @SQL_Success = 1;
        SET @SQL_Message = CONCAT('Successfully retrieved ', @RowCount, ' active hierarchy version(s).');

        -- Return success code
        RETURN 0;

    END TRY
    BEGIN CATCH
        -- Capture error information
        DECLARE @ErrorNumber INT = ERROR_NUMBER();
        DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
        DECLARE @ErrorState INT = ERROR_STATE();
        DECLARE @ErrorProcedure NVARCHAR(128) = ERROR_PROCEDURE();
        DECLARE @ErrorLine INT = ERROR_LINE();
        DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();

        -- Set failure parameters
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            'Error ', @ErrorNumber, 
            ' in procedure ', ISNULL(@ErrorProcedure, 'HierMan2_GetActiveVersions'),
            ' at line ', @ErrorLine,
            ': ', @ErrorMessage
        );


        -- Return error code
        RETURN -1;

    END CATCH
END;

CREATE     PROCEDURE [ui].[HierMan2_GetAllVersions]
    @SQL_Success INT OUTPUT,
    @SQL_Message NVARCHAR(MAX) OUTPUT
AS

BEGIN SET NOCOUNT ON;
-- defaults SET @SQL_Success = 0; SET @SQL_Message = N'';
-- basic validation IF @Hier_Version IS NULL OR @Hier_Version <= 0 BEGIN SET @SQL_Message = N'@Hier_Version must be a positive INT.'; RETURN; END;
BEGIN TRY DECLARE @c_detail INT = 0, @c_h1 INT = 0, @c_h2 INT = 0, @c_h3 INT = 0;
/* ========================================= 1) Full hierarchy (Result set #1) ========================================= */ SELECT hd.Hierarchy1_Code, hd.Hierarchy1_Name, hd.Hierarchy2_Code, hd.Hierarchy2_Name, hd.Hierarchy3_Code, hd.Hierarchy3_Name, hd.Trend, hd.MinBays, hd.MaxBays, hd.Exclude_From_Analysis, hd.FlowNumber FROM dbo.HierarchyDetail AS hd WHERE hd.HierarchyVersion_ID = @Hier_Version ORDER BY hd.FlowNumber ASC, hd.Hierarchy3_Code; SET @c_detail = @@ROWCOUNT;
/* ========================================= 2) Hierarchy1 with child counts (Result set #2) ========================================= */ SELECT h1.Hierarchy1_ID, h1.Hierarchy1_Code, h1.Hierarchy1_Name, h1.Trend, h1.MinBays, h1.MaxBays, h1.DOS AS DaysOnSite, h1.COS AS CapacityOnSite, h1.Exclude_From_Analysis AS Exclude, h1.BayRoundingThreshold, h1.Metric1 AS Metric1_Pct, h1.Metric2 AS Metric2_Pct, h1.Metric3 AS Metric3_Pct, h1.Metric4 AS Metric4_Pct, h1.Driver1 AS Driver1_Pct, h1.Driver2 AS Driver2_Pct, h1.Driver3 AS Driver3_Pct, h1.Driver4 AS Driver4_Pct, h1.HierarchyVersion_ID, COUNT(DISTINCT h2.Hierarchy2_ID) AS Child_H2, COUNT(DISTINCT h3.Hierarchy3_ID) AS Child_H3 FROM dbo.Hierarchy1 AS h1 LEFT JOIN dbo.Hierarchy2 AS h2 ON h2.ParentHierarchy_ID = h1.Hierarchy1_ID LEFT JOIN dbo.Hierarchy3 AS h3 ON h3.ParentHierarchy_ID = h2.Hierarchy2_ID WHERE h1.HierarchyVersion_ID = @Hier_Version GROUP BY h1.Hierarchy1_ID, h1.Hierarchy1_Code, h1.Hierarchy1_Name, h1.Trend, h1.MinBays, h1.MaxBays, h1.DOS, h1.COS, h1.Exclude_From_Analysis, h1.BayRoundingThreshold, h1.Metric1, h1.Metric2, h1.Metric3, h1.Metric4, h1.Driver1, h1.Driver2, h1.Driver3, h1.Driver4, h1.HierarchyVersion_ID ORDER BY h1.Hierarchy1_Code; SET @c_h1 = @@ROWCOUNT;
/* ========================================= 3) Hierarchy2 with all fields (Result set #3) ========================================= */ SELECT h2.Hierarchy2_ID, h2.Hierarchy2_Code, h2.Hierarchy2_Name, h2.Trend, h2.MinBays, h2.MaxBays, h2.DOS AS DaysOnSite, h2.COS AS CapacityOnSite, h2.Exclude_From_Analysis AS Exclude, h2.BayRoundingThreshold, h2.Metric1 AS Metric1_Pct, h2.Metric2 AS Metric2_Pct, h2.Metric3 AS Metric3_Pct, h2.Metric4 AS Metric4_Pct, h2.Driver1 AS Driver1_Pct, h2.Driver2 AS Driver2_Pct, h2.Driver3 AS Driver3_Pct, h2.Driver4 AS Driver4_Pct, h2.ParentHierarchy_ID, h2.HierarchyVersion_ID, COUNT(DISTINCT h3.Hierarchy3_ID) AS Child_H3 FROM dbo.Hierarchy2 AS h2 LEFT JOIN dbo.Hierarchy3 AS h3 ON h3.ParentHierarchy_ID = h2.Hierarchy2_ID WHERE h2.HierarchyVersion_ID = @Hier_Version GROUP BY h2.Hierarchy2_ID, h2.Hierarchy2_Code, h2.Hierarchy2_Name, h2.Trend, h2.MinBays, h2.MaxBays, h2.DOS, h2.COS, h2.Exclude_From_Analysis, h2.BayRoundingThreshold, h2.Metric1, h2.Metric2, h2.Metric3, h2.Metric4, h2.Driver1, h2.Driver2, h2.Driver3, h2.Driver4, h2.ParentHierarchy_ID, h2.HierarchyVersion_ID ORDER BY h2.ParentHierarchy_ID, h2.Hierarchy2_Code; SET @c_h2 = @@ROWCOUNT;
/* ========================================= 4) Hierarchy3 table (Result set #4) ========================================= */ SELECT h3.Hierarchy3_ID, h3.Hierarchy3_Code, h3.Hierarchy3_Name, h3.ParentHierarchy_ID, h3.Trend, h3.MinBays, h3.MaxBays, h3.DOS AS DaysOnSite, h3.COS AS CapacityOnSite, h3.Exclude_From_Analysis AS Exclude, h3.FlowNumber, h3.BayRoundingThreshold, h3.Metric1 AS Metric1_Pct, h3.Metric2 AS Metric2_Pct, h3.Metric3 AS Metric3_Pct, h3.Metric4 AS Metric4_Pct, h3.Driver1 AS Driver1_Pct, h3.Driver2 AS Driver2_Pct, h3.Driver3 AS Driver3_Pct, h3.Driver4 AS Driver4_Pct, h3.HierarchyVersion_ID FROM dbo.Hierarchy3 AS h3 WHERE h3.HierarchyVersion_ID = @Hier_Version ORDER BY h3.FlowNumber ASC, h3.Hierarchy3_Code; SET @c_h3 = @@ROWCOUNT;
-- success summary SET @SQL_Success = 1; SET @SQL_Message = CONCAT( N'OK (Version ', @Hier_Version, N'). ', N'HierarchyDetail=', @c_detail, N'; ', N'H1_Child=', @c_h1, N'; ', N'H2_Child=', @c_h2, N'; ', N'H3=', @c_h3, N'.' ); END TRY BEGIN CATCH DECLARE @ErrNum INT = ERROR_NUMBER(), @ErrLine INT = ERROR_LINE(), @ErrProc NVARCHAR(128) = ERROR_PROCEDURE(), @ErrMsg NVARCHAR(2048) = ERROR_MESSAGE();
SET @SQL_Success = 0; SET @SQL_Message = CONCAT( N'Error ', @ErrNum, N' in ', ISNULL(@ErrProc, N'[procedure]'), N' at line ', @ErrLine, N': ', @ErrMsg ); -- Optional: rethrow if you want the client to receive an error -- THROW; END CATCH; END

CREATE  PROCEDURE [ui].[HierMan2_GetFullHierarchy]
    @SQL_Success  BIT OUTPUT,
    @SQL_Message  NVARCHAR(4000) OUTPUT,   -- more room than 255
    @Hier_Version INT
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- Initialize all OUTPUTs to NULL in one go
        SELECT
            @Hierarchy1_Code           = NULL,
            @Hierarchy1_Name           = NULL,
            @Trend                     = NULL,
            @MinBays                   = NULL,
            @MaxBays                   = NULL,
            @DOS                       = NULL,
            @COS                       = NULL,
            @Exclude_From_Analysis     = NULL,
            @BayRoundingThreshold      = NULL,
            @Metric1                   = NULL,
            @Metric2                   = NULL,
            @Metric3                   = NULL,
            @Metric4                   = NULL,
            @Driver1                   = NULL,
            @Driver2                   = NULL,
            @Driver3                   = NULL,
            @Driver4                   = NULL,
            @SQL_Success               = NULL,
            @SQL_Message               = NULL;

        -- Fetch into OUTPUT parameters (including new Metrics/Drivers fields)
        SELECT
            @Hierarchy1_Code           = Hierarchy1_Code,
            @Hierarchy1_Name           = Hierarchy1_Name,
            @Trend                     = Trend,
            @MinBays                   = MinBays,
            @MaxBays                   = MaxBays,
            @DOS                       = DOS,
            @COS                       = COS,
            @Exclude_From_Analysis     = Exclude_From_Analysis,
            @BayRoundingThreshold      = BayRoundingThreshold,
            @Metric1                   = Metric1,
            @Metric2                   = Metric2,
            @Metric3                   = Metric3,
            @Metric4                   = Metric4,
            @Driver1                   = Driver1,
            @Driver2                   = Driver2,
            @Driver3                   = Driver3,
            @Driver4                   = Driver4
        FROM dbo.Hierarchy1
        WHERE Hierarchy1_ID = @Hierarchy1_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        IF @@ROWCOUNT = 0
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'No Hierarchy1 record found for ID = '
                               + CAST(@Hierarchy1_ID AS NVARCHAR(10)) + N' and Version = '
                               + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N'.';
        END
        ELSE
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = N'H1 record fetched successfully with all fields including Metrics/Drivers.';
        END
    END TRY
    BEGIN CATCH
        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH
END

CREATE PROCEDURE [ui].[HierMan2_GetHierarchy1Details]
    @Hierarchy1_ID             INT,
    @HierarchyVersion_ID       INT,
    @Hierarchy1_Code           NVARCHAR(255)    OUTPUT,
    @Hierarchy1_Name           NVARCHAR(255)    OUTPUT,
    @Trend                     FLOAT            OUTPUT,
    @MinBays                   FLOAT            OUTPUT,
    @MaxBays                   FLOAT            OUTPUT,
    @DOS                       FLOAT            OUTPUT,
    @COS                       FLOAT            OUTPUT,
    @Exclude_From_Analysis     NVARCHAR(1)      OUTPUT,
    @BayRoundingThreshold      FLOAT            OUTPUT,
    -- NEW: Added Metrics fields
    @Metric1                   INT              OUTPUT,
    @Metric2                   INT              OUTPUT,
    @Metric3                   INT              OUTPUT,
    @Metric4                   INT              OUTPUT,
    -- NEW: Added Drivers fields
    @Driver1                   INT              OUTPUT,
    @Driver2                   INT              OUTPUT,
    @Driver3                   INT              OUTPUT,
    @Driver4                   INT              OUTPUT,
    @SQL_Success               BIT              OUTPUT,
    @SQL_Message               NVARCHAR(4000)   OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- 1. Initialize all OUTPUTs to NULL
        SELECT
            @Hierarchy2_Code        = NULL,
            @Hierarchy2_Name        = NULL,
            @ParentHierarchy_ID     = NULL,
            @Trend                  = NULL,
            @MinBays                = NULL,
            @MaxBays                = NULL,
            @DOS                    = NULL,
            @COS                    = NULL,
            @Exclude_From_Analysis  = NULL,
            @BayRoundingThreshold   = NULL,
            @Metric1                = NULL,
            @Metric2                = NULL,
            @Metric3                = NULL,
            @Metric4                = NULL,
            @Driver1                = NULL,
            @Driver2                = NULL,
            @Driver3                = NULL,
            @Driver4                = NULL,
            @SQL_Success            = NULL,
            @SQL_Message            = NULL;

        -- 2. Fetch into OUTPUT parameters (including new Metrics/Drivers fields)
        SELECT
            @Hierarchy2_Code        = Hierarchy2_Code,
            @Hierarchy2_Name        = Hierarchy2_Name,
            @ParentHierarchy_ID     = ParentHierarchy_ID,
            @Trend                  = Trend,
            @MinBays                = MinBays,
            @MaxBays                = MaxBays,
            @DOS                    = DOS,
            @COS                    = COS,
            @Exclude_From_Analysis  = Exclude_From_Analysis,
            @BayRoundingThreshold   = BayRoundingThreshold,
            @Metric1                = Metric1,
            @Metric2                = Metric2,
            @Metric3                = Metric3,
            @Metric4                = Metric4,
            @Driver1                = Driver1,
            @Driver2                = Driver2,
            @Driver3                = Driver3,
            @Driver4                = Driver4
        FROM dbo.Hierarchy2
        WHERE Hierarchy2_ID = @Hierarchy2_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        -- 3. Check if found
        IF @@ROWCOUNT = 0
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'No Hierarchy2 record found for ID = '
                             + CAST(@Hierarchy2_ID AS NVARCHAR(10)) + N' and Version = '
                             + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N'.';
        END
        ELSE
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = N'H2 record fetched successfully with all fields including Metrics/Drivers.';
        END
    END TRY
    BEGIN CATCH
        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH
END

CREATE PROCEDURE [ui].[HierMan2_GetHierarchy2Details]
    @Hierarchy2_ID            INT,
    @HierarchyVersion_ID      INT,
    @Hierarchy2_Code          NVARCHAR(255)     OUTPUT,
    @Hierarchy2_Name          NVARCHAR(255)     OUTPUT,
    @ParentHierarchy_ID       INT               OUTPUT,
    @Trend                    FLOAT             OUTPUT,
    @MinBays                  FLOAT             OUTPUT,
    @MaxBays                  FLOAT             OUTPUT,
    @DOS                      FLOAT             OUTPUT,
    @COS                      FLOAT             OUTPUT,
    @Exclude_From_Analysis    NVARCHAR(1)       OUTPUT,
    @BayRoundingThreshold     FLOAT             OUTPUT,
    -- NEW: Added Metrics fields
    @Metric1                  INT               OUTPUT,
    @Metric2                  INT               OUTPUT,
    @Metric3                  INT               OUTPUT,
    @Metric4                  INT               OUTPUT,
    -- NEW: Added Drivers fields
    @Driver1                  INT               OUTPUT,
    @Driver2                  INT               OUTPUT,
    @Driver3                  INT               OUTPUT,
    @Driver4                  INT               OUTPUT,
    @SQL_Success              BIT               OUTPUT,
    @SQL_Message              NVARCHAR(4000)    OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- 1. Initialize all OUTPUTs to NULL
        SELECT
            @Hierarchy3_Code        = NULL,
            @Hierarchy3_Name        = NULL,
            @ParentHierarchy_ID     = NULL,
            @Trend                  = NULL,
            @MinBays                = NULL,
            @MaxBays                = NULL,
            @DOS                    = NULL,
            @COS                    = NULL,
