            @Metric3                = NULL,
            @Metric4                = NULL,
            @Driver1                = NULL,
            @Driver2                = NULL,
            @Driver3                = NULL,
            @Driver4                = NULL,
            @SQL_Success            = NULL,
            @SQL_Message            = NULL;

        -- 2. Fetch into OUTPUT parameters (including new Metrics/Drivers fields)
        SELECT
            @Hierarchy3_Code        = Hierarchy3_Code,
            @Hierarchy3_Name        = Hierarchy3_Name,
            @ParentHierarchy_ID     = ParentHierarchy_ID,
            @Trend                  = Trend,
            @MinBays                = MinBays,
            @MaxBays                = MaxBays,
            @DOS                    = DOS,
            @COS                    = COS,
            @Exclude_From_Analysis  = Exclude_From_Analysis,
            @BayRoundingThreshold   = BayRoundingThreshold,
            @Metric1                = Metric1,
            @Metric2                = Metric2,
            @Metric3                = Metric3,
            @Metric4                = Metric4,
            @Driver1                = Driver1,
            @Driver2                = Driver2,
            @Driver3                = Driver3,
            @Driver4                = Driver4
        FROM dbo.Hierarchy3
        WHERE Hierarchy3_ID = @Hierarchy3_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        -- 3. Check if found
        IF @@ROWCOUNT = 0
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'No Hierarchy3 record found for ID = '
                             + CAST(@Hierarchy3_ID AS NVARCHAR(10)) + N' and Version = '
                             + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N'.';
        END
        ELSE
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = N'H3 record fetched successfully with all fields including Metrics/Drivers.';
        END
    END TRY
    BEGIN CATCH
        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH
END

CREATE PROCEDURE [ui].[HierMan2_GetHierarchy3Details]
    @Hierarchy3_ID            INT,
    @HierarchyVersion_ID      INT,
    @Hierarchy3_Code          NVARCHAR(255)     OUTPUT,
    @Hierarchy3_Name          NVARCHAR(255)     OUTPUT,
    @ParentHierarchy_ID       INT               OUTPUT,
    @Trend                    FLOAT             OUTPUT,
    @MinBays                  FLOAT             OUTPUT,
    @MaxBays                  FLOAT             OUTPUT,
    @DOS                      FLOAT             OUTPUT,
    @COS                      FLOAT             OUTPUT,
    @Exclude_From_Analysis    NVARCHAR(1)       OUTPUT,
    @BayRoundingThreshold     FLOAT             OUTPUT,
    -- NEW: Added Metrics fields
    @Metric1                  INT               OUTPUT,
    @Metric2                  INT               OUTPUT,
    @Metric3                  INT               OUTPUT,
    @Metric4                  INT               OUTPUT,
    -- NEW: Added Drivers fields
    @Driver1                  INT               OUTPUT,
    @Driver2                  INT               OUTPUT,
    @Driver3                  INT               OUTPUT,
    @Driver4                  INT               OUTPUT,
    @SQL_Success              BIT               OUTPUT,
    @SQL_Message              NVARCHAR(4000)    OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- init outputs
    SET @SQL_Success   = 0;
    SET @SQL_Message   = N'';
    SET @Hierarchy1_ID = NULL;

    ------------------------------------------------------------
    -- Basic validation + normalization
    ------------------------------------------------------------
    IF @HierarchyVersion_ID IS NULL OR @HierarchyVersion_ID <= 0
    BEGIN
        SET @SQL_Message = N'HierarchyVersion_ID must be a positive INT.';
        RETURN;
    END;

    -- Check if version exists
    IF NOT EXISTS (SELECT 1 FROM dbo.HierarchyVersion WHERE HierarchyVersion_ID = @HierarchyVersion_ID)
    BEGIN
        SET @SQL_Message = N'Hierarchy version with ID ' + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N' not found.';
        RETURN;
    END;

    DECLARE @NameWarning NVARCHAR(500) = N'';

    ------------------------------------------------------------
    -- Validate Metrics: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Metrics_AllNull BIT =
        CASE WHEN @Metric1 IS NULL AND @Metric2 IS NULL AND @Metric3 IS NULL AND @Metric4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Metrics_AllSet  BIT =
        CASE WHEN @Metric1 IS NOT NULL AND @Metric2 IS NOT NULL AND @Metric3 IS NOT NULL AND @Metric4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Metrics_AllNull = 0 AND @Metrics_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Metric values must be NULL or all provided.';
        RETURN;
    END;

    IF @Metrics_AllSet = 1
    BEGIN
        IF (@Metric1 < 0 OR @Metric1 > 100 OR
            @Metric2 < 0 OR @Metric2 > 100 OR
            @Metric3 < 0 OR @Metric3 > 100 OR
            @Metric4 < 0 OR @Metric4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Metric value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Metric1 + @Metric2 + @Metric3 + @Metric4) <> 100
        BEGIN
            SET @SQL_Message = N'Metric1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Validate Drivers: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Drivers_AllNull BIT =
        CASE WHEN @Driver1 IS NULL AND @Driver2 IS NULL AND @Driver3 IS NULL AND @Driver4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Drivers_AllSet  BIT =
        CASE WHEN @Driver1 IS NOT NULL AND @Driver2 IS NOT NULL AND @Driver3 IS NOT NULL AND @Driver4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Drivers_AllNull = 0 AND @Drivers_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Driver values must be NULL or all provided.';
        RETURN;
    END;

    IF @Drivers_AllSet = 1
    BEGIN
        IF (@Driver1 < 0 OR @Driver1 > 100 OR
            @Driver2 < 0 OR @Driver2 > 100 OR
            @Driver3 < 0 OR @Driver3 > 100 OR
            @Driver4 < 0 OR @Driver4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Driver value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Driver1 + @Driver2 + @Driver3 + @Driver4) <> 100
        BEGIN
            SET @SQL_Message = N'Driver1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Code within version (fail-fast)
    ------------------------------------------------------------
    IF @Hierarchy1_Code IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Code     = @Hierarchy1_Code
    )
    BEGIN
        DECLARE @DupID1 INT;
        SELECT TOP (1) @DupID1 = Hierarchy1_ID
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Code     = @Hierarchy1_Code;

        SET @SQL_Message =
            N'Hierarchy1_Code ' + CAST(@Hierarchy1_Code AS NVARCHAR(20))
          + N' already exists in this version (ID=' + CAST(@DupID1 AS NVARCHAR(20)) + N').';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Name within version (warning only)
    ------------------------------------------------------------
    IF @Hierarchy1_Name IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Name     = @Hierarchy1_Name
    )
    BEGIN
        DECLARE @DupID2 INT, @DupCode2 INT;
        SELECT TOP (1)
            @DupID2  = Hierarchy1_ID,
            @DupCode2 = Hierarchy1_Code
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Name     = @Hierarchy1_Name;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy1_Name
          + N'" already exists (ID=' + CAST(@DupID2 AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@DupCode2, -1) AS NVARCHAR(20)) + N').';
    END;

    ------------------------------------------------------------
    -- Insert (FIXED: Generate explicit ID since Hierarchy1_ID is NOT IDENTITY)
    ------------------------------------------------------------
    BEGIN TRY
        BEGIN TRAN;

        -- Generate the next available ID
        SELECT @Hierarchy1_ID = ISNULL(MAX(Hierarchy1_ID), 0) + 1 
        FROM dbo.Hierarchy1;

        INSERT INTO dbo.Hierarchy1
        (
            Hierarchy1_ID,
            HierarchyVersion_ID,
            Hierarchy1_Code,
            Hierarchy1_Name,
            Trend,
            MinBays,
            MaxBays,
            DOS,
            COS,
            Exclude_From_Analysis,
            BayRoundingThreshold,
            Metric1, Metric2, Metric3, Metric4,
            Driver1, Driver2, Driver3, Driver4
        )
        VALUES
        (
            @Hierarchy1_ID,
            @HierarchyVersion_ID,
            @Hierarchy1_Code,
            @Hierarchy1_Name,
            @Trend,
            @MinBays,
            @MaxBays,
            @DOS,
            @COS,
            @Exclude_From_Analysis,
            @BayRoundingThreshold,
            @Metric1, @Metric2, @Metric3, @Metric4,
            @Driver1, @Driver2, @Driver3, @Driver4
        );

        -- ID was already set above before the INSERT
        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message =
            N'H1 record created successfully.'
          + N' New ID=' + CAST(@Hierarchy1_ID AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@Hierarchy1_Code, -1) AS NVARCHAR(20))
          + N', Name="' + ISNULL(@Hierarchy1_Name, N'<NULL>') + N'".'
          + @NameWarning;

    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0
            ROLLBACK TRAN;

        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH

END

CREATE PROCEDURE [ui].[HierMan2_Insert1]
    @Hierarchy1_ID            INT              OUTPUT,
    @HierarchyVersion_ID      INT,                        -- required
    @Hierarchy1_Code          INT            = NULL,      -- table is INT
    @Hierarchy1_Name          VARCHAR(255)   = NULL,
    @Trend                    DECIMAL(5,3)   = NULL,
    @MinBays                  DECIMAL(5,1)   = NULL,
    @MaxBays                  DECIMAL(5,1)   = NULL,
    @DOS                      DECIMAL(5,1)   = NULL,
    @COS                      DECIMAL(5,1)   = NULL,
    @Exclude_From_Analysis    VARCHAR(1)     = NULL,      -- expect 'Y'/'N'
    @BayRoundingThreshold     DECIMAL(5,1)   = NULL,

    -- NEW: metrics (0–100, all null or all set & sum=100)
    @Metric1                  INT            = NULL,
    @Metric2                  INT            = NULL,
    @Metric3                  INT            = NULL,
    @Metric4                  INT            = NULL,

    -- NEW: drivers (0–100, all null or all set & sum=100)
    @Driver1                  INT            = NULL,
    @Driver2                  INT            = NULL,
    @Driver3                  INT            = NULL,
    @Driver4                  INT            = NULL,

    @SQL_Success              BIT             OUTPUT,
    @SQL_Message              NVARCHAR(4000)  OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- init outputs
    SET @SQL_Success   = 0;
    SET @SQL_Message   = N'';
    SET @Hierarchy2_ID = NULL;

    ------------------------------------------------------------
    -- Basic validation + normalization
    ------------------------------------------------------------
    IF @HierarchyVersion_ID IS NULL OR @HierarchyVersion_ID <= 0
    BEGIN
        SET @SQL_Message = N'HierarchyVersion_ID must be a positive INT.';
        RETURN;
    END;

    -- Check if version exists
    IF NOT EXISTS (SELECT 1 FROM dbo.HierarchyVersion WHERE HierarchyVersion_ID = @HierarchyVersion_ID)
    BEGIN
        SET @SQL_Message = N'Hierarchy version with ID ' + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N' not found.';
        RETURN;
    END;

    -- Check if parent H1 exists (if provided)
    IF @ParentHierarchy_ID IS NOT NULL 
    BEGIN
        IF NOT EXISTS (
            SELECT 1 FROM dbo.Hierarchy1 
            WHERE Hierarchy1_ID = @ParentHierarchy_ID 
              AND HierarchyVersion_ID = @HierarchyVersion_ID
        )
        BEGIN
            SET @SQL_Message = N'Parent Hierarchy1 with ID ' + CAST(@ParentHierarchy_ID AS NVARCHAR(10)) + N' not found in this version.';
            RETURN;
        END;
    END;

    DECLARE @NameWarning NVARCHAR(500) = N'';

    ------------------------------------------------------------
    -- Validate Metrics: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Metrics_AllNull BIT =
        CASE WHEN @Metric1 IS NULL AND @Metric2 IS NULL AND @Metric3 IS NULL AND @Metric4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Metrics_AllSet  BIT =
        CASE WHEN @Metric1 IS NOT NULL AND @Metric2 IS NOT NULL AND @Metric3 IS NOT NULL AND @Metric4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Metrics_AllNull = 0 AND @Metrics_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Metric values must be NULL or all provided.';
        RETURN;
    END;

    IF @Metrics_AllSet = 1
    BEGIN
        IF (@Metric1 < 0 OR @Metric1 > 100 OR
            @Metric2 < 0 OR @Metric2 > 100 OR
            @Metric3 < 0 OR @Metric3 > 100 OR
            @Metric4 < 0 OR @Metric4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Metric value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Metric1 + @Metric2 + @Metric3 + @Metric4) <> 100
        BEGIN
            SET @SQL_Message = N'Metric1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Validate Drivers: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Drivers_AllNull BIT =
        CASE WHEN @Driver1 IS NULL AND @Driver2 IS NULL AND @Driver3 IS NULL AND @Driver4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Drivers_AllSet  BIT =
        CASE WHEN @Driver1 IS NOT NULL AND @Driver2 IS NOT NULL AND @Driver3 IS NOT NULL AND @Driver4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Drivers_AllNull = 0 AND @Drivers_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Driver values must be NULL or all provided.';
        RETURN;
    END;

    IF @Drivers_AllSet = 1
    BEGIN
        IF (@Driver1 < 0 OR @Driver1 > 100 OR
            @Driver2 < 0 OR @Driver2 > 100 OR
            @Driver3 < 0 OR @Driver3 > 100 OR
            @Driver4 < 0 OR @Driver4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Driver value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Driver1 + @Driver2 + @Driver3 + @Driver4) <> 100
        BEGIN
            SET @SQL_Message = N'Driver1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Code within version (fail-fast)
    ------------------------------------------------------------
    IF @Hierarchy2_Code IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Code     = @Hierarchy2_Code
    )
    BEGIN
        DECLARE @DupID1 INT;
        SELECT TOP (1) @DupID1 = Hierarchy2_ID
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Code     = @Hierarchy2_Code;

        SET @SQL_Message =
            N'Hierarchy2_Code ' + CAST(@Hierarchy2_Code AS NVARCHAR(20))
          + N' already exists in this version (ID=' + CAST(@DupID1 AS NVARCHAR(20)) + N').';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Name within version (warning only)
    ------------------------------------------------------------
    IF @Hierarchy2_Name IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Name     = @Hierarchy2_Name
    )
    BEGIN
        DECLARE @DupID2 INT, @DupCode2 INT;
        SELECT TOP (1)
            @DupID2  = Hierarchy2_ID,
            @DupCode2 = Hierarchy2_Code
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Name     = @Hierarchy2_Name;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy2_Name
          + N'" already exists (ID=' + CAST(@DupID2 AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@DupCode2, -1) AS NVARCHAR(20)) + N').';
    END;

    ------------------------------------------------------------
    -- Insert (FIXED: Generate explicit ID since Hierarchy2_ID is NOT IDENTITY)
    ------------------------------------------------------------
    BEGIN TRY
        BEGIN TRAN;

        -- Generate the next available ID
        SELECT @Hierarchy2_ID = ISNULL(MAX(Hierarchy2_ID), 0) + 1 
        FROM dbo.Hierarchy2;

        INSERT INTO dbo.Hierarchy2
        (
            Hierarchy2_ID,
            HierarchyVersion_ID,
            Hierarchy2_Code,
            Hierarchy2_Name,
            ParentHierarchy_ID,
            Trend,
            MinBays,
            MaxBays,
            DOS,
            COS,
            Exclude_From_Analysis,
            BayRoundingThreshold,
            Metric1, Metric2, Metric3, Metric4,
            Driver1, Driver2, Driver3, Driver4
        )
        VALUES
        (
            @Hierarchy2_ID,
            @HierarchyVersion_ID,
            @Hierarchy2_Code,
            @Hierarchy2_Name,
            @ParentHierarchy_ID,
            @Trend,
            @MinBays,
            @MaxBays,
            @DOS,
            @COS,
            @Exclude_From_Analysis,
            @BayRoundingThreshold,
            @Metric1, @Metric2, @Metric3, @Metric4,
            @Driver1, @Driver2, @Driver3, @Driver4
        );

        -- ID was already set above before the INSERT
        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message =
            N'H2 record created successfully.'
          + N' New ID=' + CAST(@Hierarchy2_ID AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@Hierarchy2_Code, -1) AS NVARCHAR(20))
          + N', Name="' + ISNULL(@Hierarchy2_Name, N'<NULL>') + N'".'
          + @NameWarning;

    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0
            ROLLBACK TRAN;

        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH

END

CREATE PROCEDURE [ui].[HierMan2_Insert2]
    @Hierarchy2_ID           INT              OUTPUT,
    @HierarchyVersion_ID     INT,                          -- required
    @Hierarchy2_Code         INT              = NULL,      -- table is INT
    @Hierarchy2_Name         VARCHAR(255)     = NULL,
    @ParentHierarchy_ID      INT              = NULL,
    @Trend                   DECIMAL(5,3)     = NULL,
    @MinBays                 DECIMAL(5,1)     = NULL,
    @MaxBays                 DECIMAL(5,1)     = NULL,
    @DOS                     DECIMAL(5,1)     = NULL,
    @COS                     DECIMAL(5,1)     = NULL,
    @Exclude_From_Analysis   VARCHAR(1)       = NULL,      -- 'Y'/'N'
    @BayRoundingThreshold    DECIMAL(5,1)     = NULL,

    -- NEW: metrics (0–100, all null OR all set & sum=100)
    @Metric1                 INT              = NULL,
    @Metric2                 INT              = NULL,
    @Metric3                 INT              = NULL,
    @Metric4                 INT              = NULL,

    -- NEW: drivers (0–100, all null OR all set & sum=100)
    @Driver1                 INT              = NULL,
    @Driver2                 INT              = NULL,
    @Driver3                 INT              = NULL,
    @Driver4                 INT              = NULL,

    @SQL_Success             BIT              OUTPUT,
    @SQL_Message             NVARCHAR(4000)   OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- init outputs
    SET @SQL_Success   = 0;
    SET @SQL_Message   = N'';
    SET @Hierarchy3_ID = NULL;

    ------------------------------------------------------------
    -- Basic validation + normalization
    ------------------------------------------------------------
    IF @HierarchyVersion_ID IS NULL OR @HierarchyVersion_ID <= 0
    BEGIN
        SET @SQL_Message = N'HierarchyVersion_ID must be a positive INT.';
        RETURN;
    END;

    -- Check if version exists
    IF NOT EXISTS (SELECT 1 FROM dbo.HierarchyVersion WHERE HierarchyVersion_ID = @HierarchyVersion_ID)
    BEGIN
        SET @SQL_Message = N'Hierarchy version with ID ' + CAST(@HierarchyVersion_ID AS NVARCHAR(10)) + N' not found.';
        RETURN;
    END;

    -- Check if parent H2 exists (if provided)
    IF @ParentHierarchy_ID IS NOT NULL 
    BEGIN
        IF NOT EXISTS (
            SELECT 1 FROM dbo.Hierarchy2 
            WHERE Hierarchy2_ID = @ParentHierarchy_ID 
              AND HierarchyVersion_ID = @HierarchyVersion_ID
        )
        BEGIN
            SET @SQL_Message = N'Parent Hierarchy2 with ID ' + CAST(@ParentHierarchy_ID AS NVARCHAR(10)) + N' not found in this version.';
            RETURN;
        END;
    END;

    DECLARE @NameWarning NVARCHAR(500) = N'';

    ------------------------------------------------------------
    -- Validate Metrics: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Metrics_AllNull BIT =
        CASE WHEN @Metric1 IS NULL AND @Metric2 IS NULL AND @Metric3 IS NULL AND @Metric4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Metrics_AllSet  BIT =
        CASE WHEN @Metric1 IS NOT NULL AND @Metric2 IS NOT NULL AND @Metric3 IS NOT NULL AND @Metric4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Metrics_AllNull = 0 AND @Metrics_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Metric values must be NULL or all provided.';
        RETURN;
    END;

    IF @Metrics_AllSet = 1
    BEGIN
        IF (@Metric1 < 0 OR @Metric1 > 100 OR
            @Metric2 < 0 OR @Metric2 > 100 OR
            @Metric3 < 0 OR @Metric3 > 100 OR
            @Metric4 < 0 OR @Metric4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Metric value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Metric1 + @Metric2 + @Metric3 + @Metric4) <> 100
        BEGIN
            SET @SQL_Message = N'Metric1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Validate Drivers: either all NULL, or all between 0..100 and sum=100
    ------------------------------------------------------------
    DECLARE @Drivers_AllNull BIT =
        CASE WHEN @Driver1 IS NULL AND @Driver2 IS NULL AND @Driver3 IS NULL AND @Driver4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Drivers_AllSet  BIT =
        CASE WHEN @Driver1 IS NOT NULL AND @Driver2 IS NOT NULL AND @Driver3 IS NOT NULL AND @Driver4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Drivers_AllNull = 0 AND @Drivers_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Driver values must be NULL or all provided.';
        RETURN;
    END;

    IF @Drivers_AllSet = 1
    BEGIN
        IF (@Driver1 < 0 OR @Driver1 > 100 OR
            @Driver2 < 0 OR @Driver2 > 100 OR
            @Driver3 < 0 OR @Driver3 > 100 OR
            @Driver4 < 0 OR @Driver4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Driver value must be between 0 and 100.';
            RETURN;
        END;

        IF (@Driver1 + @Driver2 + @Driver3 + @Driver4) <> 100
        BEGIN
            SET @SQL_Message = N'Driver1..4 must sum to 100.';
            RETURN;
        END;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Code within version (fail-fast)
    ------------------------------------------------------------
    IF @Hierarchy3_Code IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Code     = @Hierarchy3_Code
    )
    BEGIN
        DECLARE @DupID1 INT;
        SELECT TOP (1) @DupID1 = Hierarchy3_ID
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Code     = @Hierarchy3_Code;

        SET @SQL_Message =
            N'Hierarchy3_Code ' + CAST(@Hierarchy3_Code AS NVARCHAR(20))
          + N' already exists in this version (ID=' + CAST(@DupID1 AS NVARCHAR(20)) + N').';
        RETURN;
    END;

    ------------------------------------------------------------
    -- Check for duplicate Name within version (warning only)
    ------------------------------------------------------------
    IF @Hierarchy3_Name IS NOT NULL AND EXISTS (
        SELECT 1
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Name     = @Hierarchy3_Name
    )
    BEGIN
        DECLARE @DupID2 INT, @DupCode2 INT;
        SELECT TOP (1)
            @DupID2  = Hierarchy3_ID,
            @DupCode2 = Hierarchy3_Code
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Name     = @Hierarchy3_Name;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy3_Name
          + N'" already exists (ID=' + CAST(@DupID2 AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@DupCode2, -1) AS NVARCHAR(20)) + N').';
    END;

    ------------------------------------------------------------
    -- Insert (FIXED: Generate explicit ID since Hierarchy3_ID is NOT IDENTITY)
    ------------------------------------------------------------
    BEGIN TRY
        BEGIN TRAN;

        -- Generate the next available ID
        SELECT @Hierarchy3_ID = ISNULL(MAX(Hierarchy3_ID), 0) + 1 
        FROM dbo.Hierarchy3;

        INSERT INTO dbo.Hierarchy3
        (
            Hierarchy3_ID,
            HierarchyVersion_ID,
            Hierarchy3_Code,
            Hierarchy3_Name,
            ParentHierarchy_ID,
            Trend,
            MinBays,
            MaxBays,
            DOS,
            COS,
            Exclude_From_Analysis,
            FlowNumber,
            BayRoundingThreshold,
            Metric1, Metric2, Metric3, Metric4,
            Driver1, Driver2, Driver3, Driver4
        )
        VALUES
        (
            @Hierarchy3_ID,
            @HierarchyVersion_ID,
            @Hierarchy3_Code,
            @Hierarchy3_Name,
            @ParentHierarchy_ID,
            @Trend,
            @MinBays,
            @MaxBays,
            @DOS,
            @COS,
            @Exclude_From_Analysis,
            @FlowNumber,
            @BayRoundingThreshold,
            @Metric1, @Metric2, @Metric3, @Metric4,
            @Driver1, @Driver2, @Driver3, @Driver4
        );

        -- ID was already set above before the INSERT
        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message =
            N'H3 record created successfully.'
          + N' New ID=' + CAST(@Hierarchy3_ID AS NVARCHAR(20))
          + N', Code=' + CAST(ISNULL(@Hierarchy3_Code, -1) AS NVARCHAR(20))
          + N', Name="' + ISNULL(@Hierarchy3_Name, N'<NULL>') + N'".'
          + @NameWarning;

    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0
            ROLLBACK TRAN;

        SET @SQL_Success = 0;
        SET @SQL_Message =
            N'Error ' + CAST(ERROR_NUMBER() AS NVARCHAR(10))
          + N' in ' + ISNULL(ERROR_PROCEDURE(), N'<unknown>')
          + N' at line ' + CAST(ERROR_LINE() AS NVARCHAR(10))
          + N': ' + ERROR_MESSAGE();
    END CATCH

END

CREATE PROCEDURE [ui].[HierMan2_Insert3]
    @Hierarchy3_ID           INT              OUTPUT,
    @HierarchyVersion_ID     INT,                          -- required
    @Hierarchy3_Code         INT              = NULL,      -- table is INT
    @Hierarchy3_Name         VARCHAR(255)     = NULL,
    @ParentHierarchy_ID      INT              = NULL,      -- H2 parent
    @FlowNumber              INT              = NULL,      -- H3-specific field
    @Trend                   DECIMAL(5,3)     = NULL,
    @MinBays                 DECIMAL(5,1)     = NULL,
    @MaxBays                 DECIMAL(5,1)     = NULL,
    @DOS                     DECIMAL(5,1)     = NULL,
    @COS                     DECIMAL(5,1)     = NULL,
    @Exclude_From_Analysis   VARCHAR(1)       = NULL,      -- 'Y'/'N'
    @BayRoundingThreshold    DECIMAL(5,1)     = NULL,

    -- NEW: metrics (0–100, all null OR all set & sum=100)
    @Metric1                 INT              = NULL,
    @Metric2                 INT              = NULL,
    @Metric3                 INT              = NULL,
    @Metric4                 INT              = NULL,

    -- NEW: drivers (0–100, all null OR all set & sum=100)
    @Driver1                 INT              = NULL,
    @Driver2                 INT              = NULL,
    @Driver3                 INT              = NULL,
    @Driver4                 INT              = NULL,

    @SQL_Success             BIT              OUTPUT,
    @SQL_Message             NVARCHAR(4000)   OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- init
    SET @SQL_Success = 0;
    SET @SQL_Message = N'';

    /* 1) Ensure target row exists */
    IF NOT EXISTS (
        SELECT 1
        FROM dbo.Hierarchy1
        WHERE Hierarchy1_ID = @Hierarchy1_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID
    )
    BEGIN
        SET @SQL_Message = N'No record found for Hierarchy1_ID='
                         + CAST(@Hierarchy1_ID AS NVARCHAR(20))
                         + N' and Version=' + CAST(@HierarchyVersion_ID AS NVARCHAR(20)) + N'.';
        RETURN;
    END;

    /* 2) Load current values */
    DECLARE
        @Curr_Code        INT,
        @Curr_Name        VARCHAR(255),
        @Curr_Trend       DECIMAL(5,3),
        @Curr_MinBays     DECIMAL(5,1),
        @Curr_MaxBays     DECIMAL(5,1),
        @Curr_DOS         DECIMAL(5,1),
        @Curr_COS         DECIMAL(5,1),
        @Curr_Exclude     VARCHAR(1),
        @Curr_Threshold   DECIMAL(5,1),
        @Curr_Metric1     INT,
        @Curr_Metric2     INT,
        @Curr_Metric3     INT,
        @Curr_Metric4     INT,
        @Curr_Driver1     INT,
        @Curr_Driver2     INT,
        @Curr_Driver3     INT,
        @Curr_Driver4     INT;

    SELECT
        @Curr_Code       = Hierarchy1_Code,
        @Curr_Name       = Hierarchy1_Name,
        @Curr_Trend      = Trend,
        @Curr_MinBays    = MinBays,
        @Curr_MaxBays    = MaxBays,
        @Curr_DOS        = DOS,
        @Curr_COS        = COS,
        @Curr_Exclude    = Exclude_From_Analysis,
        @Curr_Threshold  = BayRoundingThreshold,
        @Curr_Metric1    = Metric1,
        @Curr_Metric2    = Metric2,
        @Curr_Metric3    = Metric3,
        @Curr_Metric4    = Metric4,
        @Curr_Driver1    = Driver1,
        @Curr_Driver2    = Driver2,
        @Curr_Driver3    = Driver3,
        @Curr_Driver4    = Driver4
    FROM dbo.Hierarchy1
    WHERE Hierarchy1_ID = @Hierarchy1_ID
      AND HierarchyVersion_ID = @HierarchyVersion_ID;

    /* 3) Normalize & merge: trim; NULL -> keep current; coerce Y/N */
    -- Code is INT so just coalesce
    SET @Hierarchy1_Code = COALESCE(@Hierarchy1_Code, @Curr_Code);
    SET @Hierarchy1_Name = NULLIF(LTRIM(RTRIM(COALESCE(@Hierarchy1_Name, @Curr_Name))), '');
    SET @Trend           = COALESCE(@Trend,           @Curr_Trend);
    SET @MinBays         = COALESCE(@MinBays,         @Curr_MinBays);
    SET @MaxBays         = COALESCE(@MaxBays,         @Curr_MaxBays);
    SET @DOS             = COALESCE(@DOS,             @Curr_DOS);
    SET @COS             = COALESCE(@COS,             @Curr_COS);
    SET @Exclude_From_Analysis = UPPER(COALESCE(@Exclude_From_Analysis, @Curr_Exclude));
    SET @BayRoundingThreshold  = COALESCE(@BayRoundingThreshold, @Curr_Threshold);

    -- Merge metrics/drivers (caller can pass only the ones they change)
    SET @Metric1 = COALESCE(@Metric1, @Curr_Metric1);
    SET @Metric2 = COALESCE(@Metric2, @Curr_Metric2);
    SET @Metric3 = COALESCE(@Metric3, @Curr_Metric3);
    SET @Metric4 = COALESCE(@Metric4, @Curr_Metric4);

    SET @Driver1 = COALESCE(@Driver1, @Curr_Driver1);
    SET @Driver2 = COALESCE(@Driver2, @Curr_Driver2);
    SET @Driver3 = COALESCE(@Driver3, @Curr_Driver3);
    SET @Driver4 = COALESCE(@Driver4, @Curr_Driver4);

    /* 4) Requireds & simple checks */
    IF @Hierarchy1_Code IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy1_Code is required.';
        RETURN;
    END;
    IF @Hierarchy1_Name IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy1_Name is required (after trimming).';
        RETURN;
    END;
    IF @Exclude_From_Analysis NOT IN ('Y', 'N')
    BEGIN
        SET @SQL_Message = N'Exclude_From_Analysis must be ''Y'' or ''N''.';
        RETURN;
    END;
    IF @MinBays IS NOT NULL AND @MaxBays IS NOT NULL AND @MinBays > @MaxBays
    BEGIN
        SET @SQL_Message = N'MinBays cannot be greater than MaxBays.';
        RETURN;
    END;

    /* 5) Validate metrics: either all NULL OR all in [0..100] and sum=100 */
    DECLARE @Metrics_AllNull BIT =
        CASE WHEN @Metric1 IS NULL AND @Metric2 IS NULL AND @Metric3 IS NULL AND @Metric4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Metrics_AllSet BIT =
        CASE WHEN @Metric1 IS NOT NULL AND @Metric2 IS NOT NULL AND @Metric3 IS NOT NULL AND @Metric4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Metrics_AllNull = 0 AND @Metrics_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Metric values must be NULL or all provided.';
        RETURN;
    END;

    IF @Metrics_AllSet = 1
    BEGIN
        IF (@Metric1 < 0 OR @Metric1 > 100 OR
            @Metric2 < 0 OR @Metric2 > 100 OR
            @Metric3 < 0 OR @Metric3 > 100 OR
            @Metric4 < 0 OR @Metric4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Metric value must be between 0 and 100.';
            RETURN;
        END;
        IF (@Metric1 + @Metric2 + @Metric3 + @Metric4) <> 100
        BEGIN
            SET @SQL_Message = N'Metric1..4 must sum to 100.';
            RETURN;
        END;
    END;

    /* 6) Validate drivers: either all NULL OR all in [0..100] and sum=100 */
    DECLARE @Drivers_AllNull BIT =
        CASE WHEN @Driver1 IS NULL AND @Driver2 IS NULL AND @Driver3 IS NULL AND @Driver4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Drivers_AllSet BIT =
        CASE WHEN @Driver1 IS NOT NULL AND @Driver2 IS NOT NULL AND @Driver3 IS NOT NULL AND @Driver4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Drivers_AllNull = 0 AND @Drivers_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Driver values must be NULL or all provided.';
        RETURN;
    END;

    IF @Drivers_AllSet = 1
    BEGIN
        IF (@Driver1 < 0 OR @Driver1 > 100 OR
            @Driver2 < 0 OR @Driver2 > 100 OR
            @Driver3 < 0 OR @Driver3 > 100 OR
            @Driver4 < 0 OR @Driver4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Driver value must be between 0 and 100.';
            RETURN;
        END;
        IF (@Driver1 + @Driver2 + @Driver3 + @Driver4) <> 100
        BEGIN
            SET @SQL_Message = N'Driver1..4 must sum to 100.';
            RETURN;
        END;
    END;

    /* 7) Enforce CODE uniqueness within version (exclude this row) */
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Code     = @Hierarchy1_Code
          AND Hierarchy1_ID      <> @Hierarchy1_ID
    )
    BEGIN
        DECLARE @DupID INT, @DupName VARCHAR(255);
        SELECT TOP (1)
            @DupID   = Hierarchy1_ID,
            @DupName = Hierarchy1_Name
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Code     = @Hierarchy1_Code
          AND Hierarchy1_ID      <> @Hierarchy1_ID;

        SET @SQL_Message = N'Cannot update: Hierarchy1_Code ' + CAST(@Hierarchy1_Code AS NVARCHAR(20))
                         + N' is already used by ID=' + CAST(@DupID AS NVARCHAR(20))
                         + N' (Name="' + ISNULL(@DupName, N'NULL') + N'").';
        RETURN;
    END;

    /* 8) NAME duplicates allowed: warn but do not block */
    DECLARE @NameWarning NVARCHAR(1000) = N'';
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Name     = @Hierarchy1_Name
          AND Hierarchy1_ID      <> @Hierarchy1_ID
    )
    BEGIN
        DECLARE @DupID2 INT, @DupCode2 INT;
        SELECT TOP (1)
            @DupID2  = Hierarchy1_ID,
            @DupCode2 = Hierarchy1_Code
        FROM dbo.Hierarchy1
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy1_Name     = @Hierarchy1_Name
          AND Hierarchy1_ID      <> @Hierarchy1_ID;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy1_Name
          + N'" already used by ID=' + CAST(@DupID2 AS NVARCHAR(20))
          + N' (Code=' + CAST(ISNULL(@DupCode2,-1) AS NVARCHAR(20)) + N').';
    END;

    /* 9) No-op check after normalization/merge */
    IF  @Hierarchy1_Code        = @Curr_Code
    AND @Hierarchy1_Name        = @Curr_Name
    AND COALESCE(@Trend,0)      = COALESCE(@Curr_Trend,0)
    AND COALESCE(@MinBays,0)    = COALESCE(@Curr_MinBays,0)
    AND COALESCE(@MaxBays,0)    = COALESCE(@Curr_MaxBays,0)
    AND COALESCE(@DOS,0)        = COALESCE(@Curr_DOS,0)
    AND COALESCE(@COS,0)        = COALESCE(@Curr_COS,0)
    AND @Exclude_From_Analysis  = @Curr_Exclude
    AND COALESCE(@BayRoundingThreshold,0) = COALESCE(@Curr_Threshold,0)
    AND COALESCE(@Metric1,-1)   = COALESCE(@Curr_Metric1,-1)
    AND COALESCE(@Metric2,-1)   = COALESCE(@Curr_Metric2,-1)
    AND COALESCE(@Metric3,-1)   = COALESCE(@Curr_Metric3,-1)
    AND COALESCE(@Metric4,-1)   = COALESCE(@Curr_Metric4,-1)
    AND COALESCE(@Driver1,-1)   = COALESCE(@Curr_Driver1,-1)
    AND COALESCE(@Driver2,-1)   = COALESCE(@Curr_Driver2,-1)
    AND COALESCE(@Driver3,-1)   = COALESCE(@Curr_Driver3,-1)
    AND COALESCE(@Driver4,-1)   = COALESCE(@Curr_Driver4,-1)
    BEGIN
        SET @SQL_Success = 1;
        SET @SQL_Message = N'No changes detected.' + @NameWarning;
        RETURN;
    END;

    /* 10) Update */
    BEGIN TRY
        BEGIN TRAN;

        UPDATE dbo.Hierarchy1
        SET
            Hierarchy1_Code          = @Hierarchy1_Code,
            Hierarchy1_Name          = @Hierarchy1_Name,
            Trend                    = @Trend,
            MinBays                  = @MinBays,
            MaxBays                  = @MaxBays,
            DOS                      = @DOS,
            COS                      = @COS,
            Exclude_From_Analysis    = @Exclude_From_Analysis,
            BayRoundingThreshold     = @BayRoundingThreshold,
            Metric1                  = @Metric1,
            Metric2                  = @Metric2,
            Metric3                  = @Metric3,
            Metric4                  = @Metric4,
            Driver1                  = @Driver1,
            Driver2                  = @Driver2,
            Driver3                  = @Driver3,
            Driver4                  = @Driver4
        WHERE Hierarchy1_ID = @Hierarchy1_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message = N'Update successful.' + @NameWarning;
    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0 ROLLBACK TRAN;

        IF ERROR_NUMBER() IN (2601, 2627)
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'Cannot update: Hierarchy1_Code '
                             + CAST(ISNULL(@Hierarchy1_Code,-1) AS NVARCHAR(20))
                             + N' conflicts with an existing row in this version.';
            RETURN;
        END;

        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            N'Error ', ERROR_NUMBER(), N' at line ', ERROR_LINE(), N': ', ERROR_MESSAGE()
        );
    END CATCH;
END

CREATE PROCEDURE [ui].[HierMan2_Update1]
    @Hierarchy1_ID            INT,
    @HierarchyVersion_ID      INT,
    @Hierarchy1_Code          INT             = NULL,      -- table is INT
    @Hierarchy1_Name          VARCHAR(255)    = NULL,
    @Trend                    DECIMAL(5,3)    = NULL,
    @MinBays                  DECIMAL(5,1)    = NULL,
    @MaxBays                  DECIMAL(5,1)    = NULL,
    @DOS                      DECIMAL(5,1)    = NULL,
    @COS                      DECIMAL(5,1)    = NULL,
    @Exclude_From_Analysis    VARCHAR(1)      = NULL,      -- expect 'Y'/'N'
    @BayRoundingThreshold     DECIMAL(5,1)    = NULL,

    -- NEW: metrics/drivers (merge semantics; validated after merge)
    @Metric1                  INT             = NULL,
    @Metric2                  INT             = NULL,
    @Metric3                  INT             = NULL,
    @Metric4                  INT             = NULL,
    @Driver1                  INT             = NULL,
    @Driver2                  INT             = NULL,
    @Driver3                  INT             = NULL,
    @Driver4                  INT             = NULL,

    @SQL_Success              BIT             OUTPUT,
    @SQL_Message              NVARCHAR(4000)  OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- Defaults
    SET @SQL_Success = 0;
    SET @SQL_Message = N'';

    -- Ensure target row exists (by ID + Version)
    IF NOT EXISTS (
        SELECT 1
        FROM dbo.Hierarchy2
        WHERE Hierarchy2_ID = @Hierarchy2_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID
    )
    BEGIN
        SET @SQL_Message = N'No record found for Hierarchy2_ID='
                         + CAST(@Hierarchy2_ID AS NVARCHAR(20))
                         + N' and Version=' + CAST(@HierarchyVersion_ID AS NVARCHAR(20)) + N'.';
        RETURN;
    END;

    -- Load current values
    DECLARE
        @Curr_Code       INT,
        @Curr_Name       VARCHAR(255),
        @Curr_Parent     INT,
        @Curr_Trend      DECIMAL(5,3),
        @Curr_MinBays    DECIMAL(5,1),
        @Curr_MaxBays    DECIMAL(5,1),
        @Curr_DOS        DECIMAL(5,1),
        @Curr_COS        DECIMAL(5,1),
        @Curr_Exclude    VARCHAR(1),
        @Curr_Threshold  DECIMAL(5,1),
        @Curr_Metric1    INT,
        @Curr_Metric2    INT,
        @Curr_Metric3    INT,
        @Curr_Metric4    INT,
        @Curr_Driver1    INT,
        @Curr_Driver2    INT,
        @Curr_Driver3    INT,
        @Curr_Driver4    INT;

    SELECT
        @Curr_Code      = Hierarchy2_Code,
        @Curr_Name      = Hierarchy2_Name,
        @Curr_Parent    = ParentHierarchy_ID,
        @Curr_Trend     = Trend,
        @Curr_MinBays   = MinBays,
        @Curr_MaxBays   = MaxBays,
        @Curr_DOS       = DOS,
        @Curr_COS       = COS,
        @Curr_Exclude   = Exclude_From_Analysis,
        @Curr_Threshold = BayRoundingThreshold,
        @Curr_Metric1   = Metric1,
        @Curr_Metric2   = Metric2,
        @Curr_Metric3   = Metric3,
        @Curr_Metric4   = Metric4,
        @Curr_Driver1   = Driver1,
        @Curr_Driver2   = Driver2,
        @Curr_Driver3   = Driver3,
        @Curr_Driver4   = Driver4
    FROM dbo.Hierarchy2
    WHERE Hierarchy2_ID = @Hierarchy2_ID
      AND HierarchyVersion_ID = @HierarchyVersion_ID;

    -- Normalize inputs (NULL => keep current)
    SET @Hierarchy2_Code = COALESCE(@Hierarchy2_Code, @Curr_Code);
    SET @Hierarchy2_Name = NULLIF(LTRIM(RTRIM(COALESCE(@Hierarchy2_Name, @Curr_Name))), '');
    SET @ParentHierarchy_ID   = COALESCE(@ParentHierarchy_ID,   @Curr_Parent);
    SET @Trend                = COALESCE(@Trend,                @Curr_Trend);
    SET @MinBays              = COALESCE(@MinBays,              @Curr_MinBays);
    SET @MaxBays              = COALESCE(@MaxBays,              @Curr_MaxBays);
    SET @DOS                  = COALESCE(@DOS,                  @Curr_DOS);
    SET @COS                  = COALESCE(@COS,                  @Curr_COS);
    SET @Exclude_From_Analysis = UPPER(COALESCE(@Exclude_From_Analysis, @Curr_Exclude));
    SET @BayRoundingThreshold = COALESCE(@BayRoundingThreshold, @Curr_Threshold);

    -- Merge metrics/drivers
    SET @Metric1 = COALESCE(@Metric1, @Curr_Metric1);
    SET @Metric2 = COALESCE(@Metric2, @Curr_Metric2);
    SET @Metric3 = COALESCE(@Metric3, @Curr_Metric3);
    SET @Metric4 = COALESCE(@Metric4, @Curr_Metric4);

    SET @Driver1 = COALESCE(@Driver1, @Curr_Driver1);
    SET @Driver2 = COALESCE(@Driver2, @Curr_Driver2);
    SET @Driver3 = COALESCE(@Driver3, @Curr_Driver3);
    SET @Driver4 = COALESCE(@Driver4, @Curr_Driver4);

    -- Basic checks
    IF @Hierarchy2_Code IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy2_Code is required.';
        RETURN;
    END;

    IF @Hierarchy2_Name IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy2_Name is required (after trimming).';
        RETURN;
    END;

    IF @Exclude_From_Analysis NOT IN ('Y','N')
    BEGIN
        SET @SQL_Message = N'Exclude_From_Analysis must be ''Y'' or ''N''.';
        RETURN;
    END;

    IF @MinBays IS NOT NULL AND @MaxBays IS NOT NULL AND @MinBays > @MaxBays
    BEGIN
        SET @SQL_Message = N'MinBays cannot be greater than MaxBays.';
        RETURN;
    END;

    -- Parent must exist (if changed)
    IF @ParentHierarchy_ID IS NOT NULL AND @ParentHierarchy_ID <> @Curr_Parent
       AND NOT EXISTS (
            SELECT 1
            FROM dbo.Hierarchy1
            WHERE Hierarchy1_ID = @ParentHierarchy_ID
              AND HierarchyVersion_ID = @HierarchyVersion_ID
       )
    BEGIN
        SET @SQL_Message = N'Invalid ParentHierarchy_ID='
                         + CAST(@ParentHierarchy_ID AS NVARCHAR(20))
                         + N' for Version=' + CAST(@HierarchyVersion_ID AS NVARCHAR(20)) + N'.';
        RETURN;
    END;

    -- Metrics: all NULL OR all set in [0..100] and sum=100
    DECLARE @Metrics_AllNull BIT =
        CASE WHEN @Metric1 IS NULL AND @Metric2 IS NULL AND @Metric3 IS NULL AND @Metric4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Metrics_AllSet BIT =
        CASE WHEN @Metric1 IS NOT NULL AND @Metric2 IS NOT NULL AND @Metric3 IS NOT NULL AND @Metric4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Metrics_AllNull = 0 AND @Metrics_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Metric values must be NULL or all provided.';
        RETURN;
    END;

    IF @Metrics_AllSet = 1
    BEGIN
        IF (@Metric1 < 0 OR @Metric1 > 100 OR
            @Metric2 < 0 OR @Metric2 > 100 OR
            @Metric3 < 0 OR @Metric3 > 100 OR
            @Metric4 < 0 OR @Metric4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Metric value must be between 0 and 100.';
            RETURN;
        END;
        IF (@Metric1 + @Metric2 + @Metric3 + @Metric4) <> 100
        BEGIN
            SET @SQL_Message = N'Metric1..4 must sum to 100.';
            RETURN;
        END;
    END;

    -- Drivers: all NULL OR all set in [0..100] and sum=100
    DECLARE @Drivers_AllNull BIT =
        CASE WHEN @Driver1 IS NULL AND @Driver2 IS NULL AND @Driver3 IS NULL AND @Driver4 IS NULL THEN 1 ELSE 0 END;
    DECLARE @Drivers_AllSet BIT =
        CASE WHEN @Driver1 IS NOT NULL AND @Driver2 IS NOT NULL AND @Driver3 IS NOT NULL AND @Driver4 IS NOT NULL THEN 1 ELSE 0 END;

    IF @Drivers_AllNull = 0 AND @Drivers_AllSet = 0
    BEGIN
        SET @SQL_Message = N'All Driver values must be NULL or all provided.';
        RETURN;
    END;

    IF @Drivers_AllSet = 1
    BEGIN
        IF (@Driver1 < 0 OR @Driver1 > 100 OR
            @Driver2 < 0 OR @Driver2 > 100 OR
            @Driver3 < 0 OR @Driver3 > 100 OR
            @Driver4 < 0 OR @Driver4 > 100)
        BEGIN
            SET @SQL_Message = N'Each Driver value must be between 0 and 100.';
            RETURN;
        END;
        IF (@Driver1 + @Driver2 + @Driver3 + @Driver4) <> 100
        BEGIN
            SET @SQL_Message = N'Driver1..4 must sum to 100.';
            RETURN;
        END;
    END;

    -- Enforce CODE uniqueness per version (exclude this row)
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Code     = @Hierarchy2_Code
          AND Hierarchy2_ID      <> @Hierarchy2_ID
    )
    BEGIN
        DECLARE @DupID INT, @DupName VARCHAR(255);
        SELECT TOP (1)
            @DupID   = Hierarchy2_ID,
            @DupName = Hierarchy2_Name
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Code     = @Hierarchy2_Code
          AND Hierarchy2_ID      <> @Hierarchy2_ID;

        SET @SQL_Message = N'Cannot update: Hierarchy2_Code '
                         + CAST(@Hierarchy2_Code AS NVARCHAR(20))
                         + N' is already used by ID=' + CAST(@DupID AS NVARCHAR(20))
                         + N' (Name="' + ISNULL(@DupName, N'NULL') + N'").';
        RETURN;
    END;

    -- NAME duplicates allowed: build a warning, do not block
    DECLARE @NameWarning NVARCHAR(1000) = N'';
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Name     = @Hierarchy2_Name
          AND Hierarchy2_ID      <> @Hierarchy2_ID
    )
    BEGIN
        DECLARE @DupID2 INT, @DupCode2 INT;
        SELECT TOP (1)
            @DupID2  = Hierarchy2_ID,
            @DupCode2 = Hierarchy2_Code
        FROM dbo.Hierarchy2
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy2_Name     = @Hierarchy2_Name
          AND Hierarchy2_ID      <> @Hierarchy2_ID;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy2_Name
          + N'" already used by ID=' + CAST(@DupID2 AS NVARCHAR(20))
          + N' (Code=' + CAST(ISNULL(@DupCode2,-1) AS NVARCHAR(20)) + N').';
    END;

    -- No-op check after normalization/merge
    IF  @Hierarchy2_Code       = @Curr_Code
    AND @Hierarchy2_Name       = @Curr_Name
    AND @ParentHierarchy_ID    = @Curr_Parent
    AND COALESCE(@Trend,0)     = COALESCE(@Curr_Trend,0)
    AND COALESCE(@MinBays,0)   = COALESCE(@Curr_MinBays,0)
    AND COALESCE(@MaxBays,0)   = COALESCE(@Curr_MaxBays,0)
    AND COALESCE(@DOS,0)       = COALESCE(@Curr_DOS,0)
    AND COALESCE(@COS,0)       = COALESCE(@Curr_COS,0)
    AND @Exclude_From_Analysis = @Curr_Exclude
    AND COALESCE(@BayRoundingThreshold,0) = COALESCE(@Curr_Threshold,0)
    AND COALESCE(@Metric1,-1)  = COALESCE(@Curr_Metric1,-1)
    AND COALESCE(@Metric2,-1)  = COALESCE(@Curr_Metric2,-1)
    AND COALESCE(@Metric3,-1)  = COALESCE(@Curr_Metric3,-1)
    AND COALESCE(@Metric4,-1)  = COALESCE(@Curr_Metric4,-1)
    AND COALESCE(@Driver1,-1)  = COALESCE(@Curr_Driver1,-1)
    AND COALESCE(@Driver2,-1)  = COALESCE(@Curr_Driver2,-1)
    AND COALESCE(@Driver3,-1)  = COALESCE(@Curr_Driver3,-1)
    AND COALESCE(@Driver4,-1)  = COALESCE(@Curr_Driver4,-1)
    BEGIN
        SET @SQL_Success = 1;
        SET @SQL_Message = N'No changes detected.' + @NameWarning;
        RETURN;
    END;

    BEGIN TRY
        BEGIN TRAN;

        UPDATE dbo.Hierarchy2
        SET
            Hierarchy2_Code         = @Hierarchy2_Code,
            Hierarchy2_Name         = @Hierarchy2_Name,
            ParentHierarchy_ID      = @ParentHierarchy_ID,
            Trend                   = @Trend,
            MinBays                 = @MinBays,
            MaxBays                 = @MaxBays,
            DOS                     = @DOS,
            COS                     = @COS,
            Exclude_From_Analysis   = @Exclude_From_Analysis,
            BayRoundingThreshold    = @BayRoundingThreshold,
            Metric1                 = @Metric1,
            Metric2                 = @Metric2,
            Metric3                 = @Metric3,
            Metric4                 = @Metric4,
            Driver1                 = @Driver1,
            Driver2                 = @Driver2,
            Driver3                 = @Driver3,
            Driver4                 = @Driver4
        WHERE Hierarchy2_ID = @Hierarchy2_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message = N'Update successful.' + @NameWarning;
    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0 ROLLBACK TRAN;

        IF ERROR_NUMBER() IN (2601, 2627)
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'Cannot update: Hierarchy2_Code '
                             + CAST(ISNULL(@Hierarchy2_Code,-1) AS NVARCHAR(20))
                             + N' conflicts with an existing row in this version.';
            RETURN;
        END;

        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            N'Error ', ERROR_NUMBER(), N' at line ', ERROR_LINE(), N': ', ERROR_MESSAGE()
        );
    END CATCH;
END

CREATE PROCEDURE [ui].[HierMan2_Update2]
    @Hierarchy2_ID           INT,
    @HierarchyVersion_ID     INT,
    @Hierarchy2_Code         INT             = NULL,      -- table is INT
    @Hierarchy2_Name         VARCHAR(255)    = NULL,
    @ParentHierarchy_ID      INT             = NULL,
    @Trend                   DECIMAL(5,3)    = NULL,
    @MinBays                 DECIMAL(5,1)    = NULL,
    @MaxBays                 DECIMAL(5,1)    = NULL,
    @DOS                     DECIMAL(5,1)    = NULL,
    @COS                     DECIMAL(5,1)    = NULL,
    @Exclude_From_Analysis   VARCHAR(1)      = NULL,      -- 'Y'/'N'
    @BayRoundingThreshold    DECIMAL(5,1)    = NULL,

    -- NEW: metrics/drivers
    @Metric1                 INT             = NULL,
    @Metric2                 INT             = NULL,
    @Metric3                 INT             = NULL,
    @Metric4                 INT             = NULL,
    @Driver1                 INT             = NULL,
    @Driver2                 INT             = NULL,
    @Driver3                 INT             = NULL,
    @Driver4                 INT             = NULL,

    @SQL_Success             BIT             OUTPUT,
    @SQL_Message             NVARCHAR(4000)  OUTPUT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    -- init
    SET @SQL_Success = 0;
    SET @SQL_Message = N'';

    /* 1) Ensure target row exists */
    IF NOT EXISTS (
        SELECT 1
        FROM dbo.Hierarchy3
        WHERE Hierarchy3_ID = @Hierarchy3_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID
    )
    BEGIN
        SET @SQL_Message = N'No record found for Hierarchy3_ID='
                         + CAST(@Hierarchy3_ID AS NVARCHAR(20))
                         + N' and Version=' + CAST(@HierarchyVersion_ID AS NVARCHAR(20)) + N'.';
        RETURN;
    END;

    /* 2) Load current values */
    DECLARE
        @Curr_Code       NVARCHAR(255),
        @Curr_Name       NVARCHAR(255),
        @Curr_Parent     INT,
        @Curr_Trend      FLOAT,
        @Curr_MinBays    FLOAT,
        @Curr_MaxBays    FLOAT,
        @Curr_DOS        FLOAT,
        @Curr_COS        FLOAT,
        @Curr_Exclude    NVARCHAR(1),
        @Curr_Threshold  FLOAT;

    SELECT
        @Curr_Code      = Hierarchy3_Code,
        @Curr_Name      = Hierarchy3_Name,
        @Curr_Parent    = ParentHierarchy_ID,
        @Curr_Trend     = Trend,
        @Curr_MinBays   = MinBays,
        @Curr_MaxBays   = MaxBays,
        @Curr_DOS       = DOS,
        @Curr_COS       = COS,
        @Curr_Exclude   = Exclude_From_Analysis,
        @Curr_Threshold = BayRoundingThreshold
    FROM dbo.Hierarchy3
    WHERE Hierarchy3_ID = @Hierarchy3_ID
      AND HierarchyVersion_ID = @HierarchyVersion_ID;

    /* 3) Normalize: trim; NULL -> keep current; coerce Y/N */
    SET @Hierarchy3_Code = NULLIF(LTRIM(RTRIM(COALESCE(@Hierarchy3_Code, @Curr_Code))), N'');
    SET @Hierarchy3_Name = NULLIF(LTRIM(RTRIM(COALESCE(@Hierarchy3_Name, @Curr_Name))), N'');
    SET @ParentHierarchy_ID   = COALESCE(@ParentHierarchy_ID,   @Curr_Parent);
    SET @Trend                = COALESCE(@Trend,                @Curr_Trend);
    SET @MinBays              = COALESCE(@MinBays,              @Curr_MinBays);
    SET @MaxBays              = COALESCE(@MaxBays,              @Curr_MaxBays);
    SET @DOS                  = COALESCE(@DOS,                  @Curr_DOS);
    SET @COS                  = COALESCE(@COS,                  @Curr_COS);
    SET @Exclude_From_Analysis = UPPER(COALESCE(@Exclude_From_Analysis, @Curr_Exclude));
    SET @BayRoundingThreshold  = COALESCE(@BayRoundingThreshold, @Curr_Threshold);

    IF @Hierarchy3_Code IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy3_Code is required (after trimming).';
        RETURN;
    END;
    IF @Hierarchy3_Name IS NULL
    BEGIN
        SET @SQL_Message = N'Hierarchy3_Name is required (after trimming).';
        RETURN;
    END;
    IF @Exclude_From_Analysis NOT IN (N'Y', N'N')
    BEGIN
        SET @SQL_Message = N'Exclude_From_Analysis must be ''Y'' or ''N''.';
        RETURN;
    END;
    IF @MinBays IS NOT NULL AND @MaxBays IS NOT NULL AND @MinBays > @MaxBays
    BEGIN
        SET @SQL_Message = N'MinBays cannot be greater than MaxBays.';
        RETURN;
    END;

    /* 4) Parent must exist (if changed/provided) */
    IF @ParentHierarchy_ID IS NOT NULL AND @ParentHierarchy_ID <> @Curr_Parent
       AND NOT EXISTS (
            SELECT 1
            FROM dbo.Hierarchy2
            WHERE Hierarchy2_ID = @ParentHierarchy_ID
              AND HierarchyVersion_ID = @HierarchyVersion_ID
       )
    BEGIN
        SET @SQL_Message = N'Invalid ParentHierarchy_ID='
                         + CAST(@ParentHierarchy_ID AS NVARCHAR(20))
                         + N' for Version=' + CAST(@HierarchyVersion_ID AS NVARCHAR(20)) + N'.';
        RETURN;
    END;

    /* 5) Enforce CODE uniqueness within version (exclude this row) */
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Code     = @Hierarchy3_Code
          AND Hierarchy3_ID      <> @Hierarchy3_ID
    )
    BEGIN
        DECLARE @DupID INT, @DupName NVARCHAR(255);
        SELECT TOP (1)
            @DupID   = Hierarchy3_ID,
            @DupName = Hierarchy3_Name
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Code     = @Hierarchy3_Code
          AND Hierarchy3_ID      <> @Hierarchy3_ID;

        SET @SQL_Message = N'Cannot update: Hierarchy3_Code "' + @Hierarchy3_Code
                         + N'" is already used by ID=' + CAST(@DupID AS NVARCHAR(20))
                         + N' (Name="' + ISNULL(@DupName, N'NULL') + N'").';
        RETURN;
    END;

    /* 6) NAME duplicates allowed: warn but do not block */
    DECLARE @NameWarning NVARCHAR(1000) = N'';
    IF EXISTS (
        SELECT 1
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Name     = @Hierarchy3_Name
          AND Hierarchy3_ID      <> @Hierarchy3_ID
    )
    BEGIN
        DECLARE @DupID3 INT, @DupCode3 NVARCHAR(255);
        SELECT TOP (1)
            @DupID3  = Hierarchy3_ID,
            @DupCode3 = Hierarchy3_Code
        FROM dbo.Hierarchy3
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID
          AND Hierarchy3_Name     = @Hierarchy3_Name
          AND Hierarchy3_ID      <> @Hierarchy3_ID;

        SET @NameWarning =
            N' WARNING: name "' + @Hierarchy3_Name
          + N'" already used by ID=' + CAST(@DupID3 AS NVARCHAR(20))
          + N' (Code="' + ISNULL(@DupCode3, N'NULL') + N'").';
    END;

    /* 7) No-op check after normalization */
    IF  @Hierarchy3_Code       = @Curr_Code
    AND @Hierarchy3_Name       = @Curr_Name
    AND @ParentHierarchy_ID    = @Curr_Parent
    AND COALESCE(@Trend,0)     = COALESCE(@Curr_Trend,0)
    AND COALESCE(@MinBays,0)   = COALESCE(@Curr_MinBays,0)
    AND COALESCE(@MaxBays,0)   = COALESCE(@Curr_MaxBays,0)
    AND COALESCE(@DOS,0)       = COALESCE(@Curr_DOS,0)
    AND COALESCE(@COS,0)       = COALESCE(@Curr_COS,0)
    AND @Exclude_From_Analysis = @Curr_Exclude
    AND COALESCE(@BayRoundingThreshold,0) = COALESCE(@Curr_Threshold,0)
    BEGIN
        SET @SQL_Success = 1;
        SET @SQL_Message = N'No changes detected.' + @NameWarning;
        RETURN;
    END;

    /* 8) Update */
    BEGIN TRY
        BEGIN TRAN;

        UPDATE dbo.Hierarchy3
        SET
            Hierarchy3_Code         = @Hierarchy3_Code,
            Hierarchy3_Name         = @Hierarchy3_Name,
            ParentHierarchy_ID      = @ParentHierarchy_ID,
            Trend                   = @Trend,
            MinBays                 = @MinBays,
            MaxBays                 = @MaxBays,
            DOS                     = @DOS,
            COS                     = @COS,
            Exclude_From_Analysis   = @Exclude_From_Analysis,
            BayRoundingThreshold    = @BayRoundingThreshold
        WHERE Hierarchy3_ID = @Hierarchy3_ID
          AND HierarchyVersion_ID = @HierarchyVersion_ID;

        COMMIT TRAN;

        SET @SQL_Success = 1;
        SET @SQL_Message = N'Update successful.' + @NameWarning;
    END TRY
    BEGIN CATCH
        IF XACT_STATE() <> 0 ROLLBACK TRAN;

        IF ERROR_NUMBER() IN (2601, 2627)
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'Cannot update: Hierarchy3_Code "' + ISNULL(@Hierarchy3_Code, N'')
                             + N'" conflicts with an existing row in this version.';
            RETURN;
        END;

        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            N'Error ', ERROR_NUMBER(), N' at line ', ERROR_LINE(), N': ', ERROR_MESSAGE()
        );
    END CATCH;
END

CREATE   PROCEDURE [ui].[HierMan2_Update3]
    @Hierarchy3_ID            INT,
    @HierarchyVersion_ID      INT,
    @Hierarchy3_Code          NVARCHAR(255)   = NULL,
    @Hierarchy3_Name          NVARCHAR(255)   = NULL,
    @ParentHierarchy_ID       INT             = NULL,
    @Trend                    FLOAT           = NULL,
    @MinBays                  FLOAT           = NULL,
    @MaxBays                  FLOAT           = NULL,
    @DOS                      FLOAT           = NULL,
    @COS                      FLOAT           = NULL,
    @Exclude_From_Analysis    NVARCHAR(1)     = NULL,   -- expect 'Y'/'N'
    @BayRoundingThreshold     FLOAT           = NULL,
    @SQL_Success              BIT             OUTPUT,
    @SQL_Message              NVARCHAR(4000)  OUTPUT    -- more room
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- 1) Check if the hierarchy version exists
        IF NOT EXISTS (
            SELECT 1
            FROM dbo.HierarchyVersion
            WHERE HierarchyVersion_ID = @HierarchyVersion_ID
        )
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = CONCAT('HierarchyVersion_ID ', @HierarchyVersion_ID, ' was not found.');
            RETURN;
        END

        -- 2) Update only provided fields
        UPDATE dbo.HierarchyVersion
        SET
            HierarchyVersion_Name = COALESCE(@VersionName, HierarchyVersion_Name),
            HierarchyVersion_Status = COALESCE(@Status, HierarchyVersion_Status)
        WHERE HierarchyVersion_ID = @HierarchyVersion_ID;

        -- 3) Return success message
        SET @SQL_Success = 1;
        SET @SQL_Message = 'Success, hierarchy version updated.';
    END TRY
    BEGIN CATCH
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            'Error ', ERROR_NUMBER(), ': ', ERROR_MESSAGE()
        );
    END CATCH
END

CREATE   PROCEDURE [ui].[HierMan2_UpdateHierarchyVersion]
    @HierarchyVersion_ID  INT,
    @VersionName          NVARCHAR(255) = NULL,   -- Pass NULL to keep existing value
    @Status               VARCHAR(32)    = NULL,  -- Pass NULL to keep existing value
    @SQL_Success          BIT            OUT,
    @SQL_Message          NVARCHAR(4000) OUT
AS

CREATE   PROCEDURE [ui].[ListH123]
    @HierVer INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    DECLARE @TableName NVARCHAR(50) = QUOTENAME('C_StoreCategory_' + CAST(@ReportID AS NVARCHAR(10)));
    DECLARE @SqlQuery NVARCHAR(MAX);

    -- Check if the table exists
    IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = @TableName)
    BEGIN
        SET @BayReccDiff = NULL;
        RETURN;
    END

    -- Construct the SQL query based on Granularity
    IF @Granularity = 'S'
    BEGIN
        SET @SqlQuery = N'SELECT @BayReccDiff = SUM(Store_Bays_Recc - Bays) FROM ' + @TableName;
    END
    ELSE IF @Granularity = 'T'
    BEGIN
        SET @SqlQuery = N'SELECT @BayReccDiff = SUM(Temp_Bays_Recc - Bays) FROM ' + @TableName;
    END
    ELSE IF @Granularity = 'D'
    BEGIN
        SET @SqlQuery = N'SELECT @BayReccDiff = SUM(Dept_Bays_Recc - Bays) FROM ' + @TableName;
    END
    ELSE
    BEGIN
        -- If the granularity value is not 'S', 'T', or 'D', return a NULL value
        SET @BayReccDiff = NULL;
        RETURN;
    END

    -- Execute the constructed SQL query
    EXEC sp_executesql @SqlQuery, N'@BayReccDiff DECIMAL(18, 2) OUTPUT', @BayReccDiff OUTPUT;
END;

CREATE   PROCEDURE [ui].[New_BaysReccDiff]
    @ReportID INT,
    @Granularity CHAR(1),
    @BayReccDiff DECIMAL(18, 2) OUTPUT  -- Output parameter to return the calculated difference
AS

BEGIN SET NOCOUNT ON;
-- Initialize outputs SET @SQL_Success = 0; SET @SQL_Message = 'Processing started';
BEGIN TRY -- Handle 'Delete' Action IF @Action = 'Delete' BEGIN -- Validate mandatory parameters IF @ReportID IS NULL OR @H3Code IS NULL BEGIN SET @SQL_Message = 'Delete action requires ReportID and H3Code.'; RETURN; END
-- Check for an existing row IF EXISTS ( SELECT 1 FROM BayRules WHERE ReportID = @ReportID AND Granularity = 'C' AND Gran_Code = @H3Code AND Group_Key = 'OutputOverrides' ) BEGIN -- Delete the existing row DELETE FROM BayRules WHERE ReportID = @ReportID AND Granularity = 'C' AND Gran_Code = @H3Code AND Group_Key = 'OutputOverrides';
-- Set success message SET @SQL_Success = 1; SET @SQL_Message = 'Row deleted successfully.'; END ELSE BEGIN SET @SQL_Success = 1; SET @SQL_Message = 'No matching row found to delete.'; END
RETURN; END
-- Handle 'Insert' and 'Update' Actions IF @Action IN ('Insert', 'Update') BEGIN -- Validate mandatory parameters IF @ReportID IS NULL OR @H3Code IS NULL OR @BaysOverrideValue IS NULL BEGIN SET @SQL_Message = 'Insert/Update action requires ReportID, H3Code, and BaysOverrideValue.'; RETURN; END
-- Check for an existing row IF EXISTS ( SELECT 1 FROM BayRules WHERE ReportID = @ReportID AND Granularity = 'C' AND Gran_Code = @H3Code AND Group_Key = 'OutputOverrides' ) BEGIN -- Update the row for 'Update' Action IF @Action = 'Update' BEGIN UPDATE BayRules SET ValueToSet = @BaysOverrideValue WHERE ReportID = @ReportID AND Granularity = 'C' AND Gran_Code = @H3Code AND Group_Key = 'OutputOverrides';
SET @SQL_Success = 1; SET @SQL_Message = 'Row updated successfully.'; END END ELSE BEGIN -- Insert the row for 'Insert' Action IF @Action = 'Insert' BEGIN INSERT INTO BayRules (ReportID, Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key) VALUES (@ReportID, 'C', @H3Code, 'Bays', @BaysOverrideValue, 'C', 'OutputOverrides');
SET @SQL_Success = 1; SET @SQL_Message = 'Row inserted successfully.'; END END END END TRY BEGIN CATCH -- Handle any errors SET @SQL_Success = 0; SET @SQL_Message = ERROR_MESSAGE(); END CATCH END;

CREATE   PROCEDURE [ui].[NEW_CreateOrUpdateOutputOverride]
    @ReportID INT,
    @H3Code INT,
    @BaysOverrideValue FLOAT = NULL, -- Optional for 'Delete' action
    @Action NVARCHAR(10),           -- Expected values: 'Insert', 'Update', 'Delete'
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
BEGIN TRY
---------------------------------------------------------------------------- -- 1) First result set: PerfMax data ---------------------------------------------------------------------------- SELECT DISTINCT CAST(Date_ID AS INT) AS Date_ID, CONCAT( SUBSTRING(CAST(Date_ID AS VARCHAR(10)), 1, 4), -- First 4 chars = Year ', Wk ', SUBSTRING( CAST(Date_ID AS VARCHAR(10)), 5, LEN(CAST(Date_ID AS VARCHAR(10))) - 4 -- Remainder = Week number ) ) AS PerfDateTxt FROM dbo.Perfmax WHERE Location_Code = @LocationCode ORDER BY CAST(Date_ID AS INT) DESC;
---------------------------------------------------------------------------- -- 2) Second result set: MacroSnapShot joined to the vw_Calendar -- We return it as (MacroDate, MacroDateINT, MacroDateTxt) ---------------------------------------------------------------------------- SELECT DISTINCT ms.Create_Date AS MacroDate, c.SQL_Date_YYYYMMDD AS SQLDate, c.DateTxtMed AS TextDate FROM dbo.MacroSnapShot AS ms JOIN dbo.vw_Calendar AS c ON ms.Create_Date = c.CalendarDate WHERE ms.Location_Code = @LocationCode ORDER BY ms.Create_Date DESC;
---------------------------------------------------------------------------- -- Indicate success via output parameters ---------------------------------------------------------------------------- SET @SQL_Success = 1; SET @SQL_Message = 'Dates retrieved successfully.';
END TRY BEGIN CATCH
---------------------------------------------------------------------------- -- Return placeholder rows for BOTH result sets if an error occurs ----------------------------------------------------------------------------
-- Placeholder for PerfMax result set SELECT CAST(NULL AS INT) AS Date_ID, CAST(NULL AS VARCHAR(50)) AS PerfDateTxt;
-- Placeholder for MacroSnapShot result set SELECT CAST(NULL AS DATETIME) AS MacroDate, CAST(NULL AS INT) AS SQLDate, CAST(NULL AS VARCHAR(50)) AS TextDate;
---------------------------------------------------------------------------- -- Indicate failure and capture the SQL error message ---------------------------------------------------------------------------- SET @SQL_Success = 0; SET @SQL_Message = ERROR_MESSAGE();
END CATCH; END;

CREATE   PROCEDURE [ui].[New_GetPerfAndMacroDatesForLocation]
(
    @LocationCode INT,
    @SQL_Success  BIT           OUTPUT,
    @SQL_Message  VARCHAR(200)  OUTPUT
)
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- Start a transaction
        BEGIN TRANSACTION;

        -- Step 1: Delete rows from BayRules
        DELETE FROM BayRules
        WHERE ReportID = @ReportID;
        --  AND Group_Key IN ('Drivers', 'Metrics', 'Resize', 'Rounding');

        -- Step 2: Parse JSON into a table variable
        DECLARE @BayRulesTable TABLE (
            Granularity NVARCHAR(50),
            Gran_Code INT,
            ApplyColumn NVARCHAR(50),
            ValueToSet NVARCHAR(MAX),
            Var_or_Col NVARCHAR(50),
            Group_Key NVARCHAR(50)
        );

        INSERT INTO @BayRulesTable (Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key)
        SELECT 
            JSON_VALUE(value, '$.Granularity'),
            JSON_VALUE(value, '$.Gran_Code'),
            JSON_VALUE(value, '$.ApplyColumn'),
            JSON_VALUE(value, '$.ValueToSet'),
            JSON_VALUE(value, '$.Var_or_Col'),
            JSON_VALUE(value, '$.Group_Key')
        FROM OPENJSON(@BayRulesLocal);

        -- Step 3: Insert rows into BayRules
        INSERT INTO BayRules (ReportID, Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key)
        SELECT 
            @ReportID, 
            Granularity, 
            Gran_Code, 
            ApplyColumn, 
            ValueToSet, 
            Var_or_Col, 
            Group_Key
        FROM @BayRulesTable;

        -- Step 4: Commit the transaction
        COMMIT TRANSACTION;

        -- Set output parameters
        SET @SQL_Success = 1;
        SET @SQL_Message = CONCAT(
            'Success: ', 
            (SELECT COUNT(*) FROM @BayRulesTable), 
            ' rows added to BayRules for ReportID ', @ReportID, '.'
        );

    END TRY
    BEGIN CATCH
        -- Rollback the transaction on error
        IF @@TRANCOUNT > 0
            ROLLBACK TRANSACTION;

        -- Capture error message
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT(
            'Error: ', 
            ERROR_MESSAGE()
        );
    END CATCH;
END;

CREATE   PROCEDURE [ui].[NEW_ManageBayRules]
    @ReportID INT,
    @BayRulesLocal NVARCHAR(MAX), -- JSON string containing the rows for BayRules
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
BEGIN TRY -- Insert rows into dbo.BayRules by selecting rows with SourceReportID and setting ReportID to DestReportID INSERT INTO dbo.BayRules (ReportID, Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key) SELECT @DestReportID, -- Setting ReportID to DestReportID Granularity, Gran_Code, ApplyColumn, ValueToSet, Var_or_Col, Group_Key FROM dbo.BayRules WHERE ReportID = @SourceReportID AND Group_Key IN ('Drivers', 'Metrics', 'Rounding', 'Resize');
-- Set success output parameters SET @SQL_Success = 1; SET @SQL_Message = 'Success: BayRules copied from SourceReportID to DestReportID.'; END TRY BEGIN CATCH -- Handle errors and set failure output parameters SET @SQL_Success = 0; SET @SQL_Message = CONCAT('Error: ', ERROR_MESSAGE()); END CATCH; END

CREATE   PROCEDURE [ui].[NEWCopyBayRules]
    @SourceReportID INT,
    @DestReportID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    -- DECLARE @BackupTableName SYSNAME;
    -- -- For example, T_ReportVersionAttributes_20250402123045
    -- SET @BackupTableName = 'T_ReportVersionAttributes_' 
    --     + CONVERT(VARCHAR(8), GETDATE(), 112)  -- YYYYMMDD
    --     + '_'
    --     + REPLACE(CONVERT(VARCHAR(8), GETDATE(), 108), ':', '');  -- HHMMSS

    -- DECLARE @SQLString NVARCHAR(MAX) = 
    --     N'SELECT * INTO ' + QUOTENAME(@BackupTableName)
    --     + N' FROM ReportVersionAttributes;';

    -- EXEC sp_executesql @SQLString;

    DECLARE @BackupColumnName VARCHAR(50);
    SET @BackupColumnName = CONVERT(VARCHAR(8), GETDATE(), 112)  + '_' + REPLACE(CONVERT(VARCHAR(8), GETDATE(), 108), ':', '');

    INSERT INTO Rva_Archive SELECT @BackupColumnName, * FROM ReportVersionAttributes WHERE ReportID = @ReportID;

    BEGIN TRY
        DELETE FROM ReportVersionAttributes WHERE ReportID=@ReportID;
        DELETE FROM MSO_Reports WHERE ReportID=@ReportID;
        DELETE FROM MSO_Products WHERE ReportID=@ReportID;
        DELETE FROM BayRules WHERE ReportID=@ReportID;
        -- Set success output parameters
        SET @SQL_Success = 1;
        SET @SQL_Message = 'Success: Report Deleted';
    END TRY
    BEGIN CATCH
        -- Handle errors and set failure output parameters
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT('Error: ', ERROR_MESSAGE());
    END CATCH;
END

CREATE   PROCEDURE [ui].[NEWDeleteReportAndBayRules]
    @ReportID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- Validate Input
        IF @ReportID IS NULL OR @ReportID <= 0
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = 'Error: Invalid ReportID. It must be a positive integer.';
            RETURN;
        END

        -- Select Bay Rules
        SELECT 
            Granularity,
            Gran_Code,
            Group_Key,
            ApplyColumn,
            ValueToSet,
            Var_or_Col
        FROM 
            [dbo].[BayRules]
        WHERE 
            ReportID = @ReportID;

        -- Check if any data was returned
        IF @@ROWCOUNT = 0
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = 'No Rules found for the specified ReportID.';
        END
        ELSE
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = 'Success: Rules retrieved successfully.';
        END
    END TRY
    BEGIN CATCH
        -- Handle Errors
        SET @SQL_Success = 0;
        SET @SQL_Message = CONCAT('Error: ', ERROR_MESSAGE());
    END CATCH;
END

CREATE   PROCEDURE [ui].[NEWGetBayRulesByReportID]
    @ReportID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN SET NOCOUNT ON;
-- Initialize outputs SET @SQL_Success = 0; SET @SQL_Message = 'Processing started';
-- Validate the input Granularity IF @Granularity NOT IN ('D', 'T', 'S') BEGIN SET @SQL_Message = 'Invalid Granularity. Use D, T, or S.'; RETURN; END
-- Check if ReportID exists IF NOT EXISTS (SELECT 1 FROM dbo.MSO_Reports WHERE ReportID = @ReportID) BEGIN SET @SQL_Message = 'ReportID not found in the table.'; RETURN; END
-- Select based on Granularity BEGIN TRY IF @Granularity = 'S' BEGIN SELECT Location_Code, Category_Code, Category_Name, CAST(Department_Number AS INT) AS Department_Number, Department_Name, CAST(Temp_Category_Number AS INT) AS Temp_Category_Number, Temp_Category_Name, Flow_Number, Exclude_from_analysis, Org_Bays AS Bays, Store_BaysPerfMix AS BaysPerfMix, Store_BaysFlex_PerfMix AS BaysFlex_PerfMix, Store_DOSCOS_Bays AS DOSCOS_Bays, Store_Bays_Recc AS Bays_Recc, Org_Store_Bays_Recc AS Org_Bays_Rec, CASE WHEN CAST(Store_Projected_Sales_Quantity AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Store_Projected_Sales_Quantity AS FLOAT) - Sales_Quantity, 2) END AS SalesUnits, CASE WHEN CAST(Store_Projected_Sales_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Store_Projected_Sales_Value AS FLOAT) - Sales_Value, 2) END AS SalesValue, CASE WHEN CAST(Store_Projected_Profit_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Store_Projected_Profit_Value AS FLOAT) - Profit_Value, 2) END AS Profit, CASE WHEN CAST(Store_Projected_Sales_Less_Waste AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Store_Projected_Sales_Less_Waste AS FLOAT) - Sales_Less_Waste, 2) END AS SLW, DoS_Target, Cases_Target AS Cos_Target, Org_Bays_Req_Target AS DoS_Target_Bays FROM dbo.MSO_Reports WHERE ReportID = @ReportID; END ELSE IF @Granularity = 'T' BEGIN SELECT Location_Code, Category_Code, Category_Name, CAST(Department_Number AS INT) AS Department_Number, Department_Name, CAST(Temp_Category_Number AS INT) AS Temp_Category_Number, Temp_Category_Name, Flow_Number, Exclude_from_analysis, Org_Bays AS Bays, Temp_BaysPerfMix AS BaysPerfMix, Temp_BaysFlex_PerfMix AS BaysFlex_PerfMix, Temp_DOSCOS_Bays AS DOSCOS_Bays, Temp_Bays_Recc AS Bays_Recc, Org_Temp_Bays_Recc AS Org_Bays_Rec, CASE WHEN CAST(Temp_Projected_Sales_Quantity AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Temp_Projected_Sales_Quantity AS FLOAT) - Sales_Quantity, 2) END AS SalesUnits, CASE WHEN CAST(Temp_Projected_Sales_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Temp_Projected_Sales_Value AS FLOAT) - Sales_Value, 2) END AS SalesValue, CASE WHEN CAST(Temp_Projected_Profit_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Temp_Projected_Profit_Value AS FLOAT) - Profit_Value, 2) END AS Profit, CASE WHEN CAST(Temp_Projected_Sales_Less_Waste AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Temp_Projected_Sales_Less_Waste AS FLOAT) - Sales_Less_Waste, 2) END AS SLW, DoS_Target, Cases_Target AS Cos_Target, Org_Bays_Req_Target AS DoS_Target_Bays FROM dbo.MSO_Reports WHERE ReportID = @ReportID; END ELSE IF @Granularity = 'D' BEGIN SELECT Location_Code, Category_Code, Category_Name, CAST(Department_Number AS INT) AS Department_Number, Department_Name, CAST(Temp_Category_Number AS INT) AS Temp_Category_Number, Temp_Category_Name, Flow_Number, Exclude_from_analysis, Org_Bays AS Bays, Dept_BaysPerfMix AS BaysPerfMix, Dept_BaysFlex_PerfMix AS BaysFlex_PerfMix, Dept_DOSCOS_Bays AS DOSCOS_Bays, Dept_Bays_Recc AS Bays_Recc, Org_Dept_Bays_Recc AS Org_Bays_Rec, CASE WHEN CAST(Dept_Projected_Sales_Quantity AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Dept_Projected_Sales_Quantity AS FLOAT) - Sales_Quantity, 2) END AS SalesUnits, CASE WHEN CAST(Dept_Projected_Sales_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Dept_Projected_Sales_Value AS FLOAT) - Sales_Value, 2) END AS SalesValue, CASE WHEN CAST(Dept_Projected_Profit_Value AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Dept_Projected_Profit_Value AS FLOAT) - Profit_Value, 2) END AS Profit, CASE WHEN CAST(Dept_Projected_Sales_Less_Waste AS FLOAT) = 0 THEN NULL ELSE ROUND(CAST(Dept_Projected_Sales_Less_Waste AS FLOAT) - Sales_Less_Waste, 2) END AS SLW, DoS_Target, Cases_Target AS Cos_Target, Org_Bays_Req_Target AS DoS_Target_Bays FROM dbo.MSO_Reports WHERE ReportID = @ReportID; END;
-- Set success message SET @SQL_Success = 1; SET @SQL_Message = 'Report data returned successfully.'; END TRY BEGIN CATCH -- Handle any errors SET @SQL_Success = 0; SET @SQL_Message = ERROR_MESSAGE(); END CATCH END;

CREATE PROCEDURE [ui].[NEWGetReportData]
    @ReportID INT,
