CREATE   PROCEDURE [ui].[Overrides_BayInputUpdate]
    @ReportID int,
    @Gran_Code nvarchar(55),
    @ValueToSet varchar(max) = NULL  -- Allow NULL as a default value
AS

CREATE   PROCEDURE [ui].[Overrides_ExcludeFromAnalysis]
    @ReportID int,
    @Gran_Code nvarchar(55),
    @ValueToSet varchar(max) = NULL  -- Allow NULL as a default value
AS

BEGIN
    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = N'Starting procedure execution.';

    BEGIN TRY
        DECLARE @DynamicTableNameCount INT;
        DECLARE @SQL NVARCHAR(MAX);

        -- Retrieve BayRulesCount for the specified ReportID
        SELECT @BayRulesCount = COUNT(*)
        FROM dbo.BayRules BR
        WHERE BR.ReportID = @ReportID;

        -- Construct the dynamic SQL to count tables with names matching pattern
        SET @SQL = N'SELECT @DynamicCount = COUNT(*)
                     FROM sys.tables
                     WHERE name LIKE ''C\_%\_'' + CAST(@ReportID AS NVARCHAR) + ''%'' ESCAPE ''\'''
                     + ' AND SCHEMA_NAME(schema_id) = ''dbo'';';

        -- Execute the dynamic SQL
        EXEC sp_executesql @SQL, N'@ReportID INT, @DynamicCount INT OUTPUT', @ReportID, @DynamicTableNameCount OUTPUT;

        -- Set the output parameter
        SET @CoreTables = @DynamicTableNameCount;

        -- Check if both counts were successfully retrieved
        IF @BayRulesCount IS NOT NULL AND @CoreTables IS NOT NULL
        BEGIN
            SET @SQL_Success = 1;
            SET @SQL_Message = N'Success, counts retrieved successfully.';
        END
        ELSE
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = N'Counts could not be retrieved. Check ReportID or data integrity.';
        END
    END TRY
    BEGIN CATCH
        -- Error handling
        SET @SQL_Success = 0;
        SET @SQL_Message = ERROR_MESSAGE();
    END CATCH;
END;

CREATE   PROCEDURE [ui].[RefreshScopeCheckNEW]
    @ReportID INT,                        -- Parameter to accept ReportID
    @BayRulesCount INT OUTPUT,           -- Output parameter for BayRulesCount
    @CoreTables INT OUTPUT,              -- Output parameter for DynamicTableNameCount
    @SQL_Success BIT OUTPUT,             -- Output parameter for success status
    @SQL_Message NVARCHAR(255) OUTPUT    -- Output parameter for message
AS

BEGIN
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = 'There was an issue retrieving report details.';

    -- Create a table variable to store the results
    DECLARE @ReportResults TABLE (
ReportID INT,
        ReportOwner VARCHAR(50),
        BranchNumber INT,
        ReportName VARCHAR(255),
        ReportOwnerName VARCHAR(255),
        LocationName VARCHAR(255),
        Granularity VARCHAR(32),          -- moved before Status and widened
        Status VARCHAR(25),
        LastRefreshed NVARCHAR(10),
        CoreRowCount INT
    );

    -- Dynamic SQL query
    DECLARE @Sql NVARCHAR(MAX) = N'
    SELECT 
        r.ReportID,
        r.ReportOwner,
        r.BranchNumber,
        r.ReportName,
        u.DisplayName AS ReportOwnerName,
        l.Location_Name AS LocationName,
        r.ViewbyStoreTempcatDept AS Granularity,
        r.Status,
        CONVERT(NVARCHAR(10), r.LastRefreshed, 103) AS LastRefreshed,
        (SELECT COUNT(*) 
         FROM sys.tables 
         WHERE name = ''C_StoreCategory_'' + CAST(r.ReportID AS NVARCHAR(10))) AS CoreRowCount
    FROM 
        ReportVersionAttributes r
    JOIN 
        Users u ON r.ReportOwner = u.MyID
    JOIN
        Locations l ON r.BranchNumber = l.Location_Code
    WHERE 1 = 1';

    -- Apply filters dynamically
    IF @ReportOwnerID <> 'All'
        SET @Sql = @Sql + ' AND r.ReportOwner = @ReportOwnerID';

    IF @IncludeArchived = 0
        SET @Sql = @Sql + ' AND r.Status <> ''Archive''';

    IF @ClusterFilterID <> 0
        SET @Sql = @Sql + ' AND l.Location_ClusterID = @ClusterFilterID';

    IF @LocationFilterID <> 0
        SET @Sql = @Sql + ' AND l.Location_ID = @LocationFilterID';

    IF @ReportID IS NOT NULL
        SET @Sql = @Sql + ' AND r.ReportID = @ReportID';

    IF ISNULL(@FreetextSearch, '') <> ''
        SET @Sql = @Sql + ' AND r.ReportName COLLATE SQL_Latin1_General_CP1_CI_AS LIKE ''%'' + @FreetextSearch + ''%''';

    IF ISNULL(@Filter_ReportName, '') <> ''
        SET @Sql = @Sql + ' AND r.ReportName COLLATE SQL_Latin1_General_CP1_CI_AS LIKE ''%'' + @Filter_ReportName + ''%''';

    IF ISNULL(@Filter_ReportOwnerName, '') <> ''
        SET @Sql = @Sql + ' AND u.DisplayName COLLATE SQL_Latin1_General_CP1_CI_AS LIKE ''%'' + @Filter_ReportOwnerName + ''%''';

    IF @Filter_LocCode IS NOT NULL
        SET @Sql = @Sql + ' AND l.Location_Code = @Filter_LocCode';

    IF ISNULL(@Filter_LocName, '') <> ''
        SET @Sql = @Sql + ' AND l.Location_Name COLLATE SQL_Latin1_General_CP1_CI_AS LIKE ''%'' + @Filter_LocName + ''%''';

    -- Execute the SQL
    INSERT INTO @ReportResults
    EXEC sp_executesql @Sql,
        N'@ReportOwnerID VARCHAR(50), 
          @ClusterFilterID INT, 
          @LocationFilterID INT, 
          @FreetextSearch VARCHAR(255), 
          @Filter_ReportName VARCHAR(255), 
          @Filter_ReportOwnerName VARCHAR(255), 
          @Filter_LocCode INT, 
          @Filter_LocName VARCHAR(255),
          @ReportID INT',
        @ReportOwnerID, 
        @ClusterFilterID, 
        @LocationFilterID, 
        @FreetextSearch, 
        @Filter_ReportName, 
        @Filter_ReportOwnerName, 
        @Filter_LocCode, 
        @Filter_LocName,
        @ReportID;

    -- Check the results
    IF EXISTS (SELECT 1 FROM @ReportResults)
    BEGIN
        SET @SQL_Success = 1;
        SET @SQL_Message = CAST((SELECT COUNT(*) FROM @ReportResults) AS NVARCHAR(10)) + ' reports retrieved successfully.';
        SELECT * FROM @ReportResults;
    END
    ELSE
    BEGIN
        SET @SQL_Success = 0;
        SET @SQL_Message = 'No reports found with the specified criteria.';
    END

    SET NOCOUNT OFF;
END;

CREATE PROCEDURE [ui].[ReportsGetDetails]
    @ReportOwnerID VARCHAR(50),
    @IncludeArchived INT,
    @ClusterFilterID INT = 0,
    @LocationFilterID INT = 0,
    @FreetextSearch VARCHAR(255) = NULL,  -- Optional
    @Filter_ReportName VARCHAR(255) = NULL,  -- New filter
    @Filter_ReportOwnerName VARCHAR(255) = NULL,  -- New filter
    @Filter_LocCode INT = NULL,  -- New filter
    @Filter_LocName VARCHAR(255) = NULL,  -- New filter
    @ReportID INT = NULL, -- New filter
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(255) OUTPUT
AS

BEGIN

    -- DECLARE @BackupTableName SYSNAME;
    -- -- For example, T_ReportVersionAttributes_20250402123045
    -- SET @BackupTableName = 'T_ReportVersionAttributes_' 
    --     + CONVERT(VARCHAR(8), GETDATE(), 112)  -- YYYYMMDD
    --     + '_'
    --     + REPLACE(CONVERT(VARCHAR(8), GETDATE(), 108), ':', '');  -- HHMMSS

    -- DECLARE @SQLString NVARCHAR(MAX) = 
    --     N'SELECT * INTO ' + QUOTENAME(@BackupTableName)
    --     + N' FROM ReportVersionAttributes;';

    -- EXEC sp_executesql @SQLString;

    DECLARE @BackupColumnName VARCHAR(50);
    SET @BackupColumnName = CONVERT(VARCHAR(8), GETDATE(), 112)  + '_' + REPLACE(CONVERT(VARCHAR(8), GETDATE(), 108), ':', '');

    INSERT INTO Rva_Archive SELECT @BackupColumnName, * FROM ReportVersionAttributes WHERE ReportID = @ReportID AND ReportOwner = @ReportOwnerID;

    BEGIN TRY
        BEGIN TRANSACTION;

        ------------------------------------------------------------------
        -- 1) Check if the given @ReportID already exists in the table
        ------------------------------------------------------------------
        IF EXISTS (
            SELECT 1
            FROM ReportVersionAttributes
            WHERE ReportID = @ReportID
        )
        BEGIN
            ------------------------------------------------------------------
            -- 2) Row exists → perform the UPDATE
            ------------------------------------------------------------------
            UPDATE ReportVersionAttributes
            SET 
                ReportOwner       
      = @ReportOwnerID,
                BranchNumber            = @BranchNumber,
                PerfStartDate           = @PerfStartDate,
                PerfEndDate             = @PerfEndDate,
                MacroSpaceDate          = CAST(@MacroSpaceDate AS NVARCHAR(8)),
                ViewbyStoreTempcatDept  = @DisplayGranularity,
                Cohort                  = @Cohort,
                CohortStores            = @CohortStores,
                BranchName              = @BranchName,
                FullBaySize             = @FullBaySize,
                RoundBaySize            = @RoundBaySize,
                DaysTrading             = @DaysTrading,
                UseTrend                = @UseTrend,
                PotentialSalesAdjustment= @PotentialSalesAdjustment,
                CubeFlex                = @CubeFlex,
                UnitSalesMix_UserSet    = @UnitSalesMix_UserSet,
                ValueSalesMix_UserSet   = @ValueSalesMix_UserSet,
                ProfitSalesMix_UserSet  = @ProfitSalesMix_UserSet,
                SalesLessWaste_UserSet  = @SalesLessWaste_UserSet,
                SalesProfitMix_UserSet  = @SalesProfitMix_UserSet,
                SalesMixCube_UserSet    = @SalesMixCube_UserSet,
                DOSCasePack_UserSet     = @DOSCasePack_UserSet,
                FixtureDensity_UserSet  = 0,
                MixIncCohort_UserSet    = @MixIncCohort_UserSet,
                SpaceAdjustment         = @SpaceAdjustment,
                ElasticitySalesDiff     = @ElasticitySalesDiff,
                DoSDefault              = @DoSDefault,
                CasesDefault            = @CasesDefault,
                BayRoundingThreshold    = @BayRoundingThreshold,
                PerfDateExclusions      = @PerfDateExclusions,
                ExcludeCategories       = @ExcludeCategories,
                ReportName              = @ReportName,
                [Status]                = @Status,
                UMDID                   = @UMDID,
                RefreshTypeNeeded       = @RefreshTypeNeeded
            WHERE ReportID = @ReportID;

            -- Return values for an UPDATE
            SET @SQL_Success       = 1;
            SET @SQL_Message       = N'UPDATE successful.';
            SET @GeneratedReportID = @ReportID;  -- The same ID (updated row)
        END
        ELSE
        BEGIN
            PRINT 'INSERT code triggered...';
            ------------------------------------------------------------------
            -- 3) Row does NOT exist → perform the INSERT
            ------------------------------------------------------------------
            SET @GeneratedReportID = ISNULL(
                (
                    SELECT TOP 1 ReportID
                    FROM ReportVersionAttributes
                    ORDER BY ReportID DESC
                ),
                0
            ) + 1;

            INSERT INTO ReportVersionAttributes
            (
                ReportID,
                ReportOwner,
                BranchNumber,
                PerfStartDate,
                PerfEndDate,
                MacroSpaceDate,
                Cohort,
                CohortStores,
                BranchName,
                ViewbyStoreTempcatDept,
                FullBaySize,
                RoundBaySize,
                DaysTrading,
                UseTrend,
                PotentialSalesAdjustment,
                CubeFlex,
                UnitSalesMix_UserSet,
                ValueSalesMix_UserSet,
                ProfitSalesMix_UserSet,
                SalesLessWaste_UserSet,
                SalesProfitMix_UserSet,
                SalesMixCube_UserSet,
                DOSCasePack_UserSet,
                FixtureDensity_UserSet,
                MixIncCohort_UserSet,
                SpaceAdjustment,
                ElasticitySalesDiff,
                DoSDefault,
                CasesDefault,

 BayRoundingThreshold,
                PerfDateExclusions,
                ExcludeCategories,
                ReportName,
                [Status],
                UMDID,
                RefreshTypeNeeded
            )
            VALUES
            (
                @GeneratedReportID,
                @ReportOwnerID,
                @BranchNumber,
                @PerfStartDate,
                @PerfEndDate,
                CAST(@MacroSpaceDate AS NVARCHAR(8)),
                @Cohort,
                @CohortStores,
                @BranchName,
                @DisplayGranularity,
                @FullBaySize,
                @RoundBaySize,
                @DaysTrading,
                @UseTrend,
                @PotentialSalesAdjustment,
                @CubeFlex,
                @UnitSalesMix_UserSet,
                @ValueSalesMix_UserSet,
                @ProfitSalesMix_UserSet,
                @SalesLessWaste_UserSet,
                @SalesProfitMix_UserSet,
                @SalesMixCube_UserSet,
                @DOSCasePack_UserSet,
                @FixtureDensity_UserSet,
                @MixIncCohort_UserSet,
                @SpaceAdjustment,
                @ElasticitySalesDiff,
                @DoSDefault,
                @CasesDefault,
                @BayRoundingThreshold,
                @PerfDateExclusions,
                @ExcludeCategories,
                @ReportName,
                @Status,
                @UMDID,
                @RefreshTypeNeeded
            );

            -- Return values for an INSERT
            SET @SQL_Success = 1;
            SET @SQL_Message = 'INSERT successful.';
        END;

        COMMIT TRANSACTION;

        ----------------------------------------------------------------------
        -- 4) Create the backup table IF the Upsert was successful
        ----------------------------------------------------------------------

    END TRY
    BEGIN CATCH
        ROLLBACK TRANSACTION;

        SET @SQL_Success       = 0;
        SET @SQL_Message       = ERROR_MESSAGE();
        SET @GeneratedReportID = NULL;
    END CATCH;
END;

-- ============================================================================
-- Procedure:    ReportsUpsertRVARow
-- Description:  Checks if a row exists for the given @ReportID in 
--               ReportVersionAttributes. If it does, Update; otherwise Insert.
--               Also creates a backup of ReportVersionAttributes named 
--               T_ReportVersionAttributes_YYYYMMDDHHMMSS upon success.
-- ============================================================================
CREATE   PROCEDURE [ui].[ReportsUpsertRVARow]
    @ReportID                 INT,
    @ReportOwnerID            INT = NULL,
    @BranchNumber             INT = NULL,
    @PerfStartDate            INT = NULL,
    @PerfEndDate              INT = NULL,
    @MacroSpaceDate           INT = 0,
    @Cohort                   NVARCHAR(MAX) = NULL,
    @CohortStores             INT = NULL,
    @BranchName               NVARCHAR(MAX) = '',
    @FullBaySize              DECIMAL(18, 2) = NULL,
    @RoundBaySize             DECIMAL(18, 2) = NULL,
    @DaysTrading              FLOAT = NULL,
    @UseTrend                 CHAR(1) = '',
    @PotentialSalesAdjustment DECIMAL(18, 2) = NULL,
    @CubeFlex                 DECIMAL(18, 2) = NULL,
    @UnitSalesMix_UserSet     FLOAT = NULL,
    @ValueSalesMix_UserSet    FLOAT = NULL,
    @ProfitSalesMix_UserSet   FLOAT = NULL,
    @SalesLessWaste_UserSet   FLOAT = NULL,
    @SalesProfitMix_UserSet   FLOAT = NULL,
    @SalesMixCube_UserSet     FLOAT = NULL,
    @DOSCasePack_UserSet      FLOAT = 0,
    @FixtureDensity_UserSet   FLOAT = NULL,
    @MixIncCohort_UserSet     FLOAT = NULL,
    @SpaceAdjustment          DECIMAL(18, 2) = NULL,
    @ElasticitySalesDiff      DECIMAL(18, 2) = NULL,
    @DoSDefault               FLOAT = NULL,
    @CasesDefault             FLOAT = NULL,
    @BayRoundingThreshold     FLOAT = NULL,
    @PerfDateExclusions       NVARCHAR(MAX) = '',
    @ExcludeCategories        NVARCHAR(MAX) = '',
    @ReportName               NVARCHAR(MAX) = '',
    @Status                   NVARCHAR(128) = '',
    @UMDID                    CHAR(15) = '',
    @RefreshTypeNeeded        NVARCHAR(128) = '',
    @DisplayGranularity       NVARCHAR(1),
    @SQL_Success              BIT            OUTPUT,
    @SQL_Message              NVARCHAR(255)  OUTPUT,
    @GeneratedReportID        INT            OUTPUT
AS

BEGIN
    -- Initialize success and message output
    SET @SQL_Success = 0; -- Assume failure initially
    SET @SQL_Message = 'Procedure started.';

    BEGIN TRY
        -- Fetch all columns from ReportVersionAttributes based on the ReportID
        SELECT
            @RVA_ReportID = ReportID,
            @RVA_ReportOwner = RIGHT('00' + CAST(ReportOwner AS VARCHAR(2)), 2),
            @RVA_BranchNumber = BranchNumber,
            @RVA_ReportName = ReportName,
            @RVA_PerfStartDate = PerfStartDate,
            @RVA_PerfEndDate = PerfEndDate,
            @RVA_MacroSpaceDate = MacroSpaceDate,
            @RVA_Cohort = Cohort,
            @RVA_CohortStores = CohortStores,
            @RVA_BranchName = BranchName,
            @RVA_FullBaySize = FullBaySize,
            @RVA_RoundBaySize = RoundBaySize,
            @RVA_UnitOfMeasure = UnitOfMeasure,
            @RVA_DaysTrading = DaysTrading,
            @RVA_ViewbyStoreTempcatDept = ViewbyStoreTempcatDept,
            @RVA_MinBaysforMaxRange = MinBaysforMaxRange,
            @RVA_UseProductCube = UseProductCube,
            @RVA_UseTrend = UseTrend,
            @RVA_UseMinLMtr = UseMinLMtr,
            @RVA_UseMaxLMtr = UseMaxLMtr,
            @RVA_IncNumberofProducts = IncNumberofProducts,
            @RVA_PotentialSalesAdjustment = PotentialSalesAdjustment,
            @RVA_CubeFlex = CubeFlex,
            @RVA_TopHighlight = TopHighlight,
            @RVA_BottomHighlight = BottomHighlight,
            @RVA_UnitSalesMix_UserSet = UnitSalesMix_UserSet,
            @RVA_ValueSalesMix_UserSet = ValueSalesMix_UserSet,
            @RVA_ProfitSalesMix_UserSet = ProfitSalesMix_UserSet,
            @RVA_SalesLessWaste_UserSet = SalesLessWaste_UserSet
,
            @RVA_SalesProfitMix_UserSet = SalesProfitMix_UserSet,
            @RVA_SalesMixCube_UserSet = SalesMixCube_UserSet,
            @RVA_DOSCasePack_UserSet = DOSCasePack_UserSet,
            @RVA_FixtureDensity_UserSet = FixtureDensity_UserSet,
            @RVA_MixIncCohort_UserSet = MixIncCohort_UserSet,
            @RVA_SpaceAdjustment = SpaceAdjustment,
            @RVA_ElasticitySalesDiff = ElasticitySalesDiff,
            @RVA_DensityMetric = DensityMetric,
            @RVA_UMDID = UMDID,
            @RVA_DoSDefault = DoSDefault,
            @RVA_CasesDefault = CasesDefault,
            @RVA_BayRoundingThreshold = BayRoundingThreshold,
            @RVA_PerfDateExclusions = PerfDateExclusions,
            @RVA_ExcludeCategories = ExcludeCategories,
            @RVA_RefreshTypeNeeded = RefreshTypeNeeded,
            @RVA_BayRulesCount = BayRulesCount,
            @RVA_LastRefreshed = LastRefreshed,
            @RVA_CoreTablesCreated = CoreTablesCreated,
            @RVA_Status = Status,
            @RVA_Cluster_ID = Cluster_ID
        FROM ReportVersionAttributes
        WHERE ReportID = @ReportID;

        -- Check if a record was found
        IF @@ROWCOUNT = 0
        BEGIN
            SET @SQL_Success = 0;
            SET @SQL_Message = 'No record found for the given ReportID.';
            RETURN;
        END

        -- Success
        SET @SQL_Success = 1;
        SET @SQL_Message = 'Report details successfully retreived.';
    END TRY
    BEGIN CATCH
        -- Handle any errors
        SET @SQL_Success = 0;
        SET @SQL_Message = 'Error: ' + ERROR_MESSAGE();
    END CATCH
END;

CREATE   PROCEDURE [ui].[RVAGetDetails]
    @ReportID INT,
    @RVA_ReportID INT OUTPUT,
    @RVA_ReportOwner VARCHAR(2) OUTPUT,
    @RVA_BranchNumber INT OUTPUT,
    @RVA_ReportName VARCHAR(MAX) OUTPUT,
    @RVA_PerfStartDate INT OUTPUT,
    @RVA_PerfEndDate INT OUTPUT,
    @RVA_MacroSpaceDate VARCHAR(8) OUTPUT,
    @RVA_Cohort VARCHAR(MAX) OUTPUT,
    @RVA_CohortStores SMALLINT OUTPUT,
    @RVA_BranchName VARCHAR(MAX) OUTPUT,
    @RVA_FullBaySize DECIMAL(18, 2) OUTPUT,
    @RVA_RoundBaySize DECIMAL(18, 2) OUTPUT,
    @RVA_UnitOfMeasure VARCHAR(MAX) OUTPUT,
    @RVA_DaysTrading INT OUTPUT,
    @RVA_ViewbyStoreTempcatDept VARCHAR(MAX) OUTPUT,
    @RVA_MinBaysforMaxRange CHAR(1) OUTPUT,
    @RVA_UseProductCube CHAR(1) OUTPUT,
    @RVA_UseTrend CHAR(1) OUTPUT,
    @RVA_UseMinLMtr CHAR(1) OUTPUT,
    @RVA_UseMaxLMtr CHAR(1) OUTPUT,
    @RVA_IncNumberofProducts CHAR(1) OUTPUT,
    @RVA_PotentialSalesAdjustment DECIMAL(18, 2) OUTPUT,
    @RVA_CubeFlex DECIMAL(18, 2) OUTPUT,
    @RVA_TopHighlight DECIMAL(18, 2) OUTPUT,
    @RVA_BottomHighlight DECIMAL(18, 2) OUTPUT,
    @RVA_UnitSalesMix_UserSet FLOAT OUTPUT,
    @RVA_ValueSalesMix_UserSet FLOAT OUTPUT,
    @RVA_ProfitSalesMix_UserSet FLOAT OUTPUT,
    @RVA_SalesLessWaste_UserSet FLOAT OUTPUT,
    @RVA_SalesProfitMix_UserSet FLOAT OUTPUT,
    @RVA_SalesMixCube_UserSet FLOAT OUTPUT,
    @RVA_DOSCasePack_UserSet FLOAT OUTPUT,
    @RVA_FixtureDensity_UserSet FLOAT OUTPUT,
    @RVA_MixIncCohort_UserSet FLOAT OUTPUT,
    @RVA_SpaceAdjustment DECIMAL(18, 2) OUTPUT,
    @RVA_ElasticitySalesDiff DECIMAL(18, 2) OUTPUT,
    @RVA_DensityMetric VARCHAR(MAX) OUTPUT,
    @RVA_UMDID CHAR(15) OUTPUT,
    @RVA_DoSDefault FLOAT OUTPUT,
    @RVA_CasesDefault FLOAT OUTPUT,
    @RVA_BayRoundingThreshold FLOAT OUTPUT,
    @RVA_PerfDateExclusions NVARCHAR(MAX) OUTPUT,
    @RVA_ExcludeCategories VARCHAR(MAX) OUTPUT,
    @RVA_RefreshTypeNeeded VARCHAR(MAX) OUTPUT,
    @RVA_BayRulesCount INT OUTPUT,
    @RVA_LastRefreshed NVARCHAR(50) OUTPUT,
    @RVA_CoreTablesCreated INT OUTPUT,
    @RVA_Status VARCHAR(25) OUTPUT,
    @RVA_Cluster_ID INT OUTPUT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message VARCHAR(255) OUTPUT
AS

-- NEW VERSION WHICH USES A COUNT OF DISTINCT OF WEEKS SPECIFIED IN THE RVA PerfStart PerfEnd VALUES -- AND NOT THE THE COUNT OF WEEKS ON SALE FOR EACH PRODUCT BEGIN SET NOCOUNT ON;
-- Initialise outputs SET @SQL_Success = 1; SET @SQL_Message = 'Generate Core success'; SET @Output_Report = '';
------------------------------------------------------------------------- -- Load RVA values for the requested ReportID (unchanged pattern) ------------------------------------------------------------------------- IF @ReportID IS NOT NULL AND (SELECT COUNT(*) FROM ReportVersionAttributes WHERE ReportID = @ReportID) > 0 BEGIN DECLARE @UMDID NVARCHAR(50) = NULL, -- contains HierarchyVersion_ID as text @Cohort NVARCHAR(50) = NULL, @MacroSpaceDate DATE = NULL, @DoSDefault FLOAT = NULL, @CasesDefault FLOAT = NULL, @PerfDateExclusions NVARCHAR(MAX) = NULL, @PerfStartDate INT = NULL, @PerfEndDate INT = NULL, @UseTrend NVARCHAR(1) = NULL, @BranchNumber INT = NULL, @DaysTrading INT = NULL, @Status NVARCHAR(128), @BayRoundingThreshold FLOAT = NULL;
-- Minimal change: read directly from RVA (no @Defaults temp table needed here) SELECT @UMDID = UMDID, @Cohort = Cohort, @MacroSpaceDate = CAST(CAST(MacroSpaceDate AS VARCHAR(MAX)) AS DATE), @DoSDefault = DoSDefault, @CasesDefault = CasesDefault, @PerfDateExclusions = PerfDateExclusions, @PerfStartDate = PerfStartDate, @PerfEndDate = PerfEndDate, @UseTrend = UseTrend, @BranchNumber = BranchNumber, @DaysTrading = DaysTrading, @Status = [Status], @BayRoundingThreshold = BayRoundingThreshold FROM ReportVersionAttributes WHERE ReportID = @ReportID; --------------------------------------------------------------------- -- NEW (minimal): derive effective HierarchyVersion_ID -- 1) Use explicit parameter if provided -- 2) Otherwise TRY_CAST the UMDID (NVARCHAR) to INT --------------------------------------------------------------------- DECLARE @EffectiveHierarchyVersion_ID INT = NULL;
SET @EffectiveHierarchyVersion_ID = TRY_CAST(@UMDID AS INT);
-- Validate the effective version value exists and is usable IF @EffectiveHierarchyVersion_ID IS NULL BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'Invalid or missing HierarchyVersion_ID (UMDID not an integer and no override provided).'; RETURN; END;
-- Validate using HierarchyDetail only (authoritative table for versioning) IF NOT EXISTS ( SELECT 1 FROM dbo.HierarchyDetail WHERE HierarchyVersion_ID = @EffectiveHierarchyVersion_ID ) BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No HierarchyDetail rows found for the specified HierarchyVersion_ID.'; RETURN; END;
-- Ensure there is data for each level via HierarchyDetail IF NOT EXISTS ( SELECT 1 FROM dbo.HierarchyDetail WHERE HierarchyVersion_ID = @EffectiveHierarchyVersion_ID AND Hierarchy1_Code IS NOT NULL ) BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No Hierarchy1 data found for the specified HierarchyVersion_ID (via HierarchyDetail).'; RETURN; END;
IF NOT EXISTS ( SELECT 1 FROM dbo.HierarchyDetail WHERE HierarchyVersion_ID = @EffectiveHierarchyVersion_ID AND Hierarchy2_Code IS NOT NULL ) BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No Hierarchy2 data found for the specified HierarchyVersion_ID (via HierarchyDetail).'; RETURN; END;
IF NOT EXISTS ( SELECT 1 FROM dbo.HierarchyDetail WHERE HierarchyVersion_ID = @EffectiveHierarchyVersion_ID AND Hierarchy3_Code IS NOT NULL ) BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No Hierarchy3 data found for the specified HierarchyVersion_ID (via HierarchyDetail).'; RETURN; END;
--------------------------------------------------------------------- -- Existing RVA presence checks --------------------------------------------------------------------- IF @UMDID IS NULL OR @Cohort IS NULL OR @MacroSpaceDate IS NULL OR @DoSDefault IS NULL OR @CasesDefault IS NULL OR @PerfStartDate IS NULL OR @PerfEndDate IS NULL OR @UseTrend IS NULL OR @BranchNumber IS NULL OR @DaysTrading IS NULL OR @BayRoundingThreshold IS NULL BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'Missing values in RVA'; RETURN; END
IF @Status = 'PerfLock' BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'Master data for this report is no longer available. Please duplicate the report, update the dates and execute.'; RETURN; END
--------------------------------------------------------------------- -- Existing logic continues (unchanged unless commented as NEW) --------------------------------------------------------------------- DECLARE @sql_string NVARCHAR(MAX); DECLARE @DateString NVARCHAR(MAX);
DECLARE @sql NVARCHAR(MAX);
DECLARE @ExcludedDates TABLE (Date_ID VARCHAR(50));
IF (@PerfDateExclusions IS NOT NULL AND @PerfDateExclusions != 'None') BEGIN SET @DateString = REPLACE(REPLACE(@PerfDateExclusions, '(', ''), ')', ''); INSERT INTO @ExcludedDates (Date_ID) SELECT TRIM(value) FROM STRING_SPLIT(@DateString, ','); END ELSE BEGIN INSERT INTO @ExcludedDates (Date_ID) VALUES ('190001'); END
SELECT Date_ID, Location_Code, Product_Code, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste INTO #Perf FROM Perfmax WHERE Location_Code = @BranchNumber AND Date_ID BETWEEN @PerfStartDate AND @PerfEndDate AND NOT EXISTS (SELECT 1 FROM @ExcludedDates WHERE Date_ID = Perfmax.Date_ID);
DROP TABLE IF EXISTS T_Perf; SELECT * INTO T_Perf FROM #Perf;
-- Compute weeks count and guard against divide-by-zero (NEW safety) DECLARE @CountPerfWks INT = (SELECT COUNT(DISTINCT Date_ID) FROM #Perf); IF @CountPerfWks = 0 BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No performance weeks in the selected range after exclusions.'; RETURN; END;
-- Weekly averages (based on distinct weeks in period, not weeks-on-sale per SKU) SELECT Location_Code, Product_Code, COUNT(DISTINCT Date_ID) AS Weeks_On_Sale, SUM(Sales_Quantity) * 1.0 / @CountPerfWks AS Sales_Quantity, SUM(Sales_Value) * 1.0 / @CountPerfWks AS Sales_Value, SUM(Profit_Value) * 1.0 / @CountPerfWks AS Profit_Value, SUM(Sales_Less_Waste) * 1.0 / @CountPerfWks AS Sales_Less_Waste INTO #PerfAvg FROM #Perf GROUP BY Location_Code, Product_Code;
DROP TABLE IF EXISTS T_PerfAvg; SELECT * INTO T_PerfAvg FROM #PerfAvg;
-- Micro SELECT * INTO #Micro FROM [Micro Space] WHERE Location_Code = @BranchNumber;
DROP TABLE IF EXISTS T_Micro; SELECT * INTO T_Micro FROM #Micro;
-- Macro SELECT [Location_Code], [Category_Code], CAST([Bays] AS FLOAT) AS [Bays], [Create_Date] INTO #Macro FROM MacroSnapShot WHERE Create_Date = @MacroSpaceDate AND Location_Code = @BranchNumber;
DROP TABLE IF EXISTS T_Macro; SELECT * INTO T_Macro FROM #Macro;
--------------------------------------------------------------------- -- Macro + Hierarchy (VERSION-SCOPED) *** NEW filter in WHERE *** --------------------------------------------------------------------- SELECT m.Location_Code, m.Category_Code, h.Hierarchy3_Name AS Category_Name, h.Hierarchy2_Code AS Department_Number, h.Hierarchy2_Name AS Department_Name, h.FlowNumber AS Flow_Number, h.Hierarchy1_Code AS Temp_Category_Number, h.Hierarchy1_Name AS Temp_Category_Name, h.DOS AS DoS_Target, h.COS AS Cases_Target, h.MinBays AS Min_Bays, h.MaxBays AS Max_Bays, ISNULL(h.BayRoundingThreshold, @BayRoundingThreshold) AS BayRoundingThreshold, h.Trend, CASE WHEN h.Exclude_From_Analysis = 'Y' THEN 1 ELSE 0 END AS Exclude_From_Analysis, m.Bays INTO #MacroHier FROM dbo.HierarchyDetail h JOIN #Macro m ON h.Hierarchy3_Code = m.Category_Code WHERE h.HierarchyVersion_ID = @EffectiveHierarchyVersion_ID; -- NEW
DROP TABLE IF EXISTS T_MacroHier; SELECT * INTO T_MacroHier FROM #MacroHier;
-- Early fail if Macro categories did not match this version IF NOT EXISTS (SELECT 1 FROM #MacroHier) BEGIN SET @SQL_Success = 0; SET @SQL_Message = 'No categories in Macro match the specified HierarchyVersion_ID.'; RETURN; END;
-- MacroHier + Micro WITH CTE_Tptu AS ( SELECT ms.Product_Gtin, SUM(ms.Position_TotalUnits) AS TotalUnits FROM #Micro ms JOIN #MacroHier mh ON ms.Category_Code = mh.Category_Code GROUP BY ms.Product_Gtin ), CTE_Gtin AS ( SELECT ms.Category_Code, ms.Product_Gtin, ms.Position_TotalUnits, ms.Product_Cube, ms.Product_CasePackUnits, ms.Position_FacingsWide, mh.Trend, mh.DoS_Target, mh.Cases_Target FROM #Micro ms JOIN #MacroHier mh ON ms.Category_Code = mh.Category_Code ) SELECT g.Category_Code, g.Product_Gtin AS Product_Code, g.Position_TotalUnits, g.Product_Cube, g.Product_CasePackUnits, g.Position_FacingsWide, t.TotalUnits, g.Trend, g.DoS_Target, g.Cases_Target, (CAST(g.Position_TotalUnits AS FLOAT) / CAST(t.TotalUnits AS FLOAT)) AS pct_ptu INTO #MacroHierMicro FROM CTE_Gtin g JOIN CTE_Tptu t ON g.Product_Gtin = t.Product_Gtin;
DROP TABLE IF EXISTS T_MacroHierMicro; SELECT * INTO T_MacroHierMicro FROM #MacroHierMicro;
-- Apply perf averages by product SELECT p.Location_Code, u.Category_Code, u.Product_Code, u.Trend, u.DoS_Target, u.Cases_Target, (p.Sales_Quantity * u.pct_ptu) AS Sales_Quantity, (p.Sales_Value * u.pct_ptu) AS Sales_Value, (p.Profit_Value * u.pct_ptu) AS Profit_Value, (p.Sales_Less_Waste * u.pct_ptu) AS Sales_Less_Waste, u.Position_TotalUnits, u.Product_Cube, u.Product_CasePackUnits, u.Position_FacingsWide INTO #MacroHierMicroPerfAvg FROM #PerfAvg p JOIN #MacroHierMicro u ON p.Product_Code = u.Product_Code;
DROP TABLE IF EXISTS T_MacroHierMicroPerfAvg; SELECT * INTO T_MacroHierMicroPerfAvg FROM #MacroHierMicroPerfAvg;
DECLARE @AvgCubeGlobal FLOAT = (SELECT AVG(Product_Cube) FROM #MacroHierMicroPerfAvg);
-- Build #Products with version-scoped HierarchyFull (LEFT JOIN) SELECT p.Location_Code, p.Product_Code, p.Category_Code, h.Hierarchy3_Name AS Category_Name, h.Hierarchy2_Code AS Department_Number, h.Hierarchy2_Name AS Department_Name, h.Hierarchy1_Code AS Temp_Category_Number, h.Hierarchy1_Name AS Temp_Category_Name, p.Trend, p.DoS_Target, p.Cases_Target, p.Sales_Quantity, p.Sales_Value, p.Profit_Value, p.Sales_Less_Waste, p.Position_TotalUnits, p.Product_Cube, p.Product_CasePackUnits, p.Position_FacingsWide INTO #Products FROM #MacroHierMicroPerfAvg p LEFT JOIN dbo.HierarchyFull h ON p.Category_Code = h.Hierarchy3_Code;
-- Product-level calcs ALTER TABLE #Products ADD DOS FLOAT, DOS_Target_Units FLOAT, Cases_Target_Units FLOAT, Units_Required INT, Dos_Units_Required INT, Facings_Required FLOAT;
UPDATE #Products SET DOS = CASE WHEN Sales_Quantity = 0 THEN 0 ELSE Position_TotalUnits / (CAST(Sales_Quantity AS FLOAT) / @DaysTrading) END;
UPDATE #Products SET DOS_Target_Units = CASE WHEN Sales_Quantity = 0 THEN 0 ELSE (Sales_Quantity / @DaysTrading) * DOS END;
UPDATE #Products SET Cases_Target_Units = Product_CasePackUnits * Cases_Target;
-- Sum to category SELECT Location_Code, Category_Code, CASE WHEN @UseTrend = 'Y' THEN SUM(Sales_Quantity) * (1 + AVG(Trend)) ELSE SUM(Sales_Quantity) END AS Sales_Quantity, CASE WHEN @UseTrend = 'Y' THEN SUM(Sales_Value) * (1 + AVG(Trend)) ELSE SUM(Sales_Value) END AS Sales_Value, CASE WHEN @UseTrend = 'Y' THEN SUM(Profit_Value) * (1 + AVG(Trend)) ELSE SUM(Profit_Value) END AS Profit_Value, CASE WHEN @UseTrend = 'Y' THEN SUM(Sales_Less_Waste) * (1 + AVG(Trend)) ELSE SUM(Sales_Less_Waste) END AS Sales_Less_Waste, AVG(Trend) AS Avg_Trend, SUM(Sales_Quantity) AS Org_Sales_Quantity, SUM(Sales_Value) AS Org_Sales_Value, SUM(Profit_Value) AS Org_Profit_Value, SUM(Sales_Less_Waste) AS Org_Sales_Less_Waste INTO #MacroHierMicroPerfAvgSumGrp FROM #MacroHierMicroPerfAvg GROUP BY Location_Code, Category_Code;
-- Combine with MacroHier to form #Category SELECT mh.Location_Code, mh.Category_Code, mh.Category_Name, mh.Department_Number, mh.Department_Name, mh.Flow_Number, mh.Temp_Category_Number, mh.Temp_Category_Name, mh.Bays, Org_Bays = mh.Bays, mh.Min_Bays, mh.Max_Bays, mh.BayRoundingThreshold, mh.DoS_Target, mh.Cases_Target, mh.Exclude_From_Analysis, ps.Sales_Quantity, ps.Sales_Value, ps.Profit_Value, ps.Sales_Less_Waste, ps.Avg_Trend, ps.Org_Sales_Quantity, ps.Org_Sales_Value, ps.Org_Profit_Value, ps.Org_Sales_Less_Waste INTO #Category FROM #MacroHier mh JOIN #MacroHierMicroPerfAvgSumGrp ps ON mh.Category_Code = ps.Category_Code;
-- Add many calc columns (initialised to 0 per existing pattern) ALTER TABLE #Category ADD Department_Sub VARCHAR(255), UnitSalesMix_UserSet FLOAT, ValueSalesMix_UserSet FLOAT, ProfitSalesMix_UserSet FLOAT, SalesLessWaste_UserSet FLOAT, SalesProfitMix_UserSet FLOAT, SalesMixCube_UserSet FLOAT, DOSCasePack_UserSet FLOAT, FixtureDensity_UserSet FLOAT, MixIncCohort_UserSet FLOAT, Category_QuantCont_Pt FLOAT, Depart_QuantCont_Pt FLOAT, Temp_QuantCont_Pt FLOAT, Category_ValueCont_Pt FLOAT, Depart_ValueCont_Pt FLOAT, Temp_ValueCont_Pt FLOAT, Category_ProfitCont_Pt FLOAT, Depart_ProfitCont_Pt FLOAT, Temp_ProfitCont_Pt FLOAT, Category_WasteCont_Pt FLOAT, Depart_WasteCont_Pt FLOAT, Temp_WasteCont_Pt FLOAT, Store_PerfMix_Pt FLOAT, Dept_PerfMix_Pt FLOAT, Temp_PerfMix_Pt FLOAT, Store_BaysPerfMix FLOAT, Dept_BaysPerfMix FLOAT, Temp_BaysPerfMix FLOAT, Store_Ranking INT, Dept_Ranking INT, Temp_Ranking INT, Sum_Cubes FLOAT, Products_Count FLOAT, Cat_ProdCube_Avg FLOAT, Store_ProdCube_Avg FLOAT, Dept_ProdCube_Avg FLOAT, Temp_ProdCube_Avg FLOAT, Store_ProdCat_Avg FLOAT, Dept_ProdCat_Avg FLOAT, Temp_ProdCat_Avg FLOAT, Store_ProdCubeRatio FLOAT, Dept_ProdCubeRatio FLOAT, Temp_ProdCubeRatio FLOAT, Store_BaysFlex_PerfMix FLOAT, Dept_BaysFlex_PerfMix FLOAT, Temp_BaysFlex_PerfMix FLOAT, Bays_Req_Target FLOAT, Org_Bays_Req_Target FLOAT, Store_DOSCOS_Bays FLOAT, Dept_DOSCOS_Bays FLOAT, Temp_DOSCOS_Bays FLOAT, Store_Bays_Recc FLOAT, Dept_Bays_Recc FLOAT, Temp_Bays_Recc FLOAT, Org_Store_Bays_Recc FLOAT, Org_Dept_Bays_Recc FLOAT, Org_Temp_Bays_Recc FLOAT, DoSTargetAchieved FLOAT, CaseTargetAchieved FLOAT, BothTargetAchieved FLOAT, Store_Projected_Sales_Quantity FLOAT, Store_Projected_Org_Sales_Quantity FLOAT, Store_Projected_Sales_Value FLOAT, Store_Projected_Org_Sales_Value FLOAT, Store_Projected_Profit_Value FLOAT, Store_Projected_Org_Profit_Value FLOAT, Store_Projected_Sales_Less_Waste FLOAT, Store_Projected_Org_Sales_Less_Waste FLOAT, Dept_Projected_Sales_Quantity FLOAT, Dept_Projected_Org_Sales_Quantity FLOAT, Dept_Projected_Sales_Value FLOAT, Dept_Projected_Org_Sales_Value FLOAT, Dept_Projected_Profit_Value FLOAT, Dept_Projected_Org_Profit_Value FLOAT, Dept_Projected_Sales_Less_Waste FLOAT, Dept_Projected_Org_Sales_Less_Waste FLOAT, Temp_Projected_Sales_Quantity FLOAT, Temp_Projected_Org_Sales_Quantity FLOAT, Temp_Projected_Sales_Value FLOAT, Temp_Projected_Org_Sales_Value FLOAT, Temp_Projected_Profit_Value FLOAT, Temp_Projected_Org_Profit_Value FLOAT, Temp_Projected_Sales_Less_Waste FLOAT, Temp_Projected_Org_Sales_Less_Waste FLOAT;
UPDATE #Category SET Department_Sub = NULL, UnitSalesMix_UserSet = 0, ValueSalesMix_UserSet = 0, ProfitSalesMix_UserSet = 0, SalesLessWaste_UserSet = 0, SalesProfitMix_UserSet = 0, SalesMixCube_UserSet = 0, DOSCasePack_UserSet = 0, FixtureDensity_UserSet = 0, MixIncCohort_UserSet = 0, Category_QuantCont_Pt = 0, Depart_QuantCont_Pt = 0, Temp_QuantCont_Pt = 0, Category_ValueCont_Pt = 0, Depart_ValueCont_Pt = 0, Temp_ValueCont_Pt = 0, Category_ProfitCont_Pt = 0, Depart_ProfitCont_Pt = 0, Temp_ProfitCont_Pt = 0, Category_WasteCont_Pt = 0, Depart_WasteCont_Pt = 0, Temp_WasteCont_Pt = 0, Store_PerfMix_Pt = 0, Dept_PerfMix_Pt = 0, Temp_PerfMix_Pt = 0, Store_BaysPerfMix = 0, Dept_BaysPerfMix = 0, Temp_BaysPerfMix = 0, Store_Ranking = 0, Dept_Ranking = 0, Temp_Ranking = 0, Sum_Cubes = 0, Products_Count = 0, Cat_ProdCube_Avg = 0, Store_ProdCube_Avg = 0, Dept_ProdCube_Avg = 0, Temp_ProdCube_Avg = 0, Store_ProdCat_Avg = 0, Dept_ProdCat_Avg = 0, Temp_ProdCat_Avg = 0, Store_ProdCubeRatio = 0, Dept_ProdCubeRatio = 0, Temp_ProdCubeRatio = 0, Store_BaysFlex_PerfMix = 0, Dept_BaysFlex_PerfMix = 0, Temp_BaysFlex_PerfMix = 0, Bays_Req_Target = 0, Org_Bays_Req_Target = 0, Store_DOSCOS_Bays = 0, Dept_DOSCOS_Bays = 0, Temp_DOSCOS_Bays = 0, Store_Bays_Recc = 0, Dept_Bays_Recc = 0, Temp_Bays_Recc = 0, Org_Store_Bays_Recc = 0, Org_Dept_Bays_Recc = 0, Org_Temp_Bays_Recc = 0, DoSTargetAchieved = 0, CaseTargetAchieved = 0, BothTargetAchieved = 0, Store_Projected_Sales_Quantity = 0, Store_Projected_Org_Sales_Quantity = 0, Store_Projected_Sales_Value = 0, Store_Projected_Org_Sales_Value = 0, Store_Projected_Profit_Value = 0, Store_Projected_Org_Profit_Value = 0, Store_Projected_Sales_Less_Waste = 0, Store_Projected_Org_Sales_Less_Waste = 0, Dept_Projected_Sales_Quantity = 0, Dept_Projected_Org_Sales_Quantity = 0, Dept_Projected_Sales_Value = 0, Dept_Projected_Org_Sales_Value = 0, Dept_Projected_Profit_Value = 0, Dept_Projected_Org_Profit_Value = 0, Dept_Projected_Sales_Less_Waste = 0, Dept_Projected_Org_Sales_Less_Waste = 0, Temp_Projected_Sales_Quantity = 0, Temp_Projected_Org_Sales_Quantity = 0, Temp_Projected_Sales_Value = 0, Temp_Projected_Org_Sales_Value = 0, Temp_Projected_Profit_Value = 0, Temp_Projected_Org_Profit_Value = 0, Temp_Projected_Sales_Less_Waste = 0, Temp_Projected_Org_Sales_Less_Waste = 0;
-- Push average DoS/Cases down to #Category from #Products WITH TargetSums AS ( SELECT Category_Code, ROUND(AVG(DoS_Target), 2) AS AvgDosTarget, ROUND(AVG(Cases_Target), 2) AS AvgCasesTarget FROM #Products GROUP BY Category_Code ) UPDATE cc SET cc.DoS_Target = ts.AvgDosTarget, cc.Cases_Target = ts.AvgCasesTarget FROM #Category cc JOIN TargetSums ts ON cc.Category_Code = ts.Category_Code;
-- Snapshot for output tables SELECT @UserID AS UserID, @ReportID AS ReportID, * INTO #Category_final FROM #Category;
-- Refresh MSO_Reports for this user/report DELETE FROM MSO_Reports WHERE UserID = @UserID AND ReportID = @ReportID;
INSERT INTO MSO_Reports ( UserID, ReportID, Location_Code, Category_Code, Category_Name, Department_Number, Department_Sub, Flow_Number, Department_Name, Temp_Category_Number, Temp_Category_Name, Avg_Trend, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste, Org_Sales_Quantity, Org_Sales_Value, Org_Profit_Value, Org_Sales_Less_Waste, Bays, Min_Bays, Max_Bays, Exclude_from_analysis, DoS_Target, Cases_Target, Org_Bays, UnitSalesMix_UserSet, ValueSalesMix_UserSet, ProfitSalesMix_UserSet, SalesLessWaste_UserSet, SalesProfitMix_UserSet, SalesMixCube_UserSet, DOSCasePack_UserSet, FixtureDensity_UserSet, MixIncCohort_UserSet, BayRoundingThreshold, Category_QuantCont_Pt, Depart_QuantCont_Pt, Temp_QuantCont_Pt, Category_ValueCont_Pt, Depart_ValueCont_Pt, Temp_ValueCont_Pt, Category_ProfitCont_Pt, Depart_ProfitCont_Pt, Temp_ProfitCont_Pt, Category_WasteCont_Pt, Depart_WasteCont_Pt, Temp_WasteCont_Pt, Store_PerfMix_Pt, Dept_PerfMix_Pt, Temp_PerfMix_Pt, Store_BaysPerfMix, Dept_BaysPerfMix, Temp_BaysPerfMix, Store_Ranking, Dept_Ranking, Temp_Ranking, Sum_Cubes, Products_Count, Cat_ProdCube_Avg, Store_ProdCube_Avg, Dept_ProdCube_Avg, Temp_ProdCube_Avg, Store_ProdCat_Avg, Dept_ProdCat_Avg, Temp_ProdCat_Avg, Store_ProdCubeRatio, Dept_ProdCubeRatio, Temp_ProdCubeRatio, Store_BaysFlex_PerfMix, Dept_BaysFlex_PerfMix, Temp_BaysFlex_PerfMix, Bays_Req_Target, Org_Bays_Req_Target, Store_DOSCOS_Bays, Dept_DOSCOS_Bays, Temp_DOSCOS_Bays, Store_Bays_Recc, Dept_Bays_Recc, Temp_Bays_Recc, Org_Store_Bays_Recc, Org_Dept_Bays_Recc, Org_Temp_Bays_Recc, DoSTargetAchieved, CaseTargetAchieved, BothTargetAchieved, Store_Projected_Sales_Quantity, Store_Projected_Org_Sales_Quantity, Store_Projected_Sales_Value, Store_Projected_Org_Sales_Value, Store_Projected_Profit_Value, Store_Projected_Org_Profit_Value, Store_Projected_Sales_Less_Waste, Store_Projected_Org_Sales_Less_Waste, Dept_Projected_Sales_Quantity, Dept_Projected_Org_Sales_Quantity, Dept_Projected_Sales_Value, Dept_Projected_Org_Sales_Value, Dept_Projected_Profit_Value, Dept_Projected_Org_Profit_Value, Dept_Projected_Sales_Less_Waste, Dept_Projected_Org_Sales_Less_Waste, Temp_Projected_Sales_Quantity, Temp_Projected_Org_Sales_Quantity, Temp_Projected_Sales_Value, Temp_Projected_Org_Sales_Value, Temp_Projected_Profit_Value, Temp_Projected_Org_Profit_Value, Temp_Projected_Sales_Less_Waste, Temp_Projected_Org_Sales_Less_Waste ) SELECT UserID, ReportID, CAST(Location_Code AS BIGINT), CAST(Category_Code AS BIGINT), Category_Name, FORMAT(CAST(Department_Number AS BIGINT), '0'), Department_Sub, Flow_Number, Department_Name, FORMAT(CAST(Temp_Category_Number AS BIGINT), '0'), Temp_Category_Name, Avg_Trend, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste, Org_Sales_Quantity, Org_Sales_Value, Org_Profit_Value, Org_Sales_Less_Waste, Bays, Min_Bays, Max_Bays, Exclude_From_Analysis AS Exclude_from_analysis, DoS_Target, Cases_Target, Org_Bays, UnitSalesMix_UserSet, ValueSalesMix_UserSet, ProfitSalesMix_UserSet, SalesLessWaste_UserSet, SalesProfitMix_UserSet, SalesMixCube_UserSet, DOSCasePack_UserSet, FixtureDensity_UserSet, MixIncCohort_UserSet, BayRoundingThreshold, Category_QuantCont_Pt, Depart_QuantCont_Pt, Temp_QuantCont_Pt, Category_ValueCont_Pt, Depart_ValueCont_Pt, Temp_ValueCont_Pt, Category_ProfitCont_Pt, Depart_ProfitCont_Pt, Temp_ProfitCont_Pt, Category_WasteCont_Pt, Depart_WasteCont_Pt, Temp_WasteCont_Pt, Store_PerfMix_Pt, Dept_PerfMix_Pt, Temp_PerfMix_Pt, Store_BaysPerfMix, Dept_BaysPerfMix, Temp_BaysPerfMix, Store_Ranking, Dept_Ranking, Temp_Ranking, Sum_Cubes, Products_Count, Cat_ProdCube_Avg, Store_ProdCube_Avg, Dept_ProdCube_Avg, Temp_ProdCube_Avg, Store_ProdCat_Avg, Dept_ProdCat_Avg, Temp_ProdCat_Avg, Store_ProdCubeRatio, Dept_ProdCubeRatio, Temp_ProdCubeRatio, Store_BaysFlex_PerfMix, Dept_BaysFlex_PerfMix, Temp_BaysFlex_PerfMix, Bays_Req_Target, Org_Bays_Req_Target, Store_DOSCOS_Bays, Dept_DOSCOS_Bays, Temp_DOSCOS_Bays, Store_Bays_Recc, Dept_Bays_Recc, Temp_Bays_Recc, Org_Store_Bays_Recc, Org_Dept_Bays_Recc, Org_Temp_Bays_Recc, DoSTargetAchieved, CaseTargetAchieved, BothTargetAchieved, Store_Projected_Sales_Quantity, Store_Projected_Org_Sales_Quantity, Store_Projected_Sales_Value, Store_Projected_Org_Sales_Value, Store_Projected_Profit_Value, Store_Projected_Org_Profit_Value, Store_Projected_Sales_Less_Waste, Store_Projected_Org_Sales_Less_Waste, Dept_Projected_Sales_Quantity, Dept_Projected_Org_Sales_Quantity, Dept_Projected_Sales_Value, Dept_Projected_Org_Sales_Value, Dept_Projected_Profit_Value, Dept_Projected_Org_Profit_Value, Dept_Projected_Sales_Less_Waste, Dept_Projected_Org_Sales_Less_Waste, Temp_Projected_Sales_Quantity, Temp_Projected_Org_Sales_Quantity, Temp_Projected_Sales_Value, Temp_Projected_Org_Sales_Value, Temp_Projected_Profit_Value, Temp_Projected_Org_Profit_Value, Temp_Projected_Sales_Less_Waste, Temp_Projected_Org_Sales_Less_Waste FROM #Category_final;
-- Refresh MSO_Products for this user/report DELETE FROM MSO_Products WHERE UserID = @UserID AND ReportID = @ReportID;
INSERT INTO MSO_Products ( UserID, ReportID, Location_Code, Product_Code, Category_Code, Category_Name, Department_Number, Department_Name, Temp_Category_Number, Temp_Category_Name, Trend, DoS_Target, Cases_Target, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste, Position_TotalUnits, Product_Cube, Product_CasePackUnits, Position_FacingsWide, DOS, DOS_Target_Units, Cases_Target_Units, Units_Required, Dos_Units_Required, Facings_Required ) SELECT @UserID AS UserID, @ReportID AS ReportID, Location_Code, Product_Code, Category_Code, Category_Name, Department_Number, Department_Name, Temp_Category_Number, Temp_Category_Name, Trend, DoS_Target, Cases_Target, Sales_Quantity, Sales_Value, Profit_Value, Sales_Less_Waste, Position_TotalUnits, Product_Cube, Product_CasePackUnits, Position_FacingsWide, DOS, DOS_Target_Units, Cases_Target_Units, Units_Required, Dos_Units_Required, Facings_Required FROM #Products;
END ELSE BEGIN -- ReportID missing or not found in RVA PRINT 'Error: Input cannot be NULL.'; SET @SQL_Success = 0; SET @SQL_Message = 'ReportID does not exist in RVA'; RETURN; END
-- Final outputs (per your pattern) SET NOCOUNT OFF; SET @SQL_Success = 1; SET @SQL_Message = 'Generate Core success'; SET @Output_Report = ''; END;

CREATE PROCEDURE [ui].[StoreGenerateCore-NHV1]
    @UserID INT = NULL,
    @ReportID INT = NULL,
    @SQL_Success BIT OUTPUT,
    @SQL_Message VARCHAR(255) OUTPUT,
    @Output_Report NVARCHAR(MAX) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    SET @SQL_Success = 0;  -- Set as Failure
    SET @SQL_Message = 'This report belongs to another user.  Please duplicate report to your own account and try again';
    SET @Output_Report = '';

    DECLARE @SQL NVARCHAR(MAX) = NULL;
    DECLARE @Template NVARCHAR(MAX);
    DECLARE @Iteration INT = 1;
    DECLARE @Resize_Success BIT;
    DECLARE @Rounding_Success BIT;

    IF @ReportID IS NOT NULL AND (SELECT COUNT(*) FROM ReportVersionAttributes WHERE ReportOwner = @UserID AND ReportID = @ReportID) > 0
    BEGIN
        -- Get & Set variables from ReportVersionAttributes
        DECLARE 
            @UnitSalesMix_UserSet FLOAT = NULL, 
            @ValueSalesMix_UserSet FLOAT = NULL, 
            @ProfitSalesMix_UserSet FLOAT = NULL, 
            @SalesLessWaste_UserSet FLOAT = NULL, 
            @SalesProfitMix_UserSet FLOAT = NULL, 
            @SalesMixCube_UserSet FLOAT = NULL, 
            @DOSCasePack_UserSet FLOAT = NULL, 
            @FixtureDensity_UserSet FLOAT = NULL, 
            @MixIncCohort_UserSet FLOAT = NULL, 
            @ExcludeCategories NVARCHAR(MAX) = NULL,
            @DaysTrading INT = NULL,
            @CubeFlex FLOAT = NULL,
            @MinBayFraction FLOAT = NULL,
            @PotentialSalesAdjustment FLOAT = NULL;


        DECLARE @Defaults TABLE (
            UnitSalesMix_UserSet FLOAT, 
            ValueSalesMix_UserSet FLOAT, 
            ProfitSalesMix_UserSet FLOAT, 
            SalesLessWaste_UserSet FLOAT, 
            SalesProfitMix_UserSet FLOAT, 
            SalesMixCube_UserSet FLOAT, 
            DOSCasePack_UserSet FLOAT, 
            FixtureDensity_UserSet FLOAT, 
            MixIncCohort_UserSet FLOAT, 
            ExcludeCategories NVARCHAR(MAX),
            DaysTrading INT,
            CubeFlex FLOAT,
            MinBayFraction FLOAT,
            PotentialSalesAdjustment FLOAT
        );

        INSERT INTO @Defaults
        SELECT 
        UnitSalesMix_UserSet, 
        ValueSalesMix_UserSet, 
        ProfitSalesMix_UserSet, 
        SalesLessWaste_UserSet, 
        SalesProfitMix_UserSet, 
        SalesMixCube_UserSet, 
        DOSCasePack_UserSet, 
        FixtureDensity_UserSet, 
        MixIncCohort_UserSet, 
        CASE 
            WHEN ExcludeCategories NOT LIKE '(%)' THEN '(0)'
            ELSE ExcludeCategories 
        END AS ExcludeCategories,
        DaysTrading,
        CubeFlex,
        (RoundBaySize / FullBaySize) AS MinBayFraction,
        PotentialSalesAdjustment
        FROM ReportVersionAttributes
        WHERE ReportID = @ReportID;

        SELECT
        @UnitSalesMix_UserSet  = UnitSalesMix_UserSet, 
        @ValueSalesMix_UserSet  = ValueSalesMix_UserSet, 
        @ProfitSalesMix_UserSet  = ProfitSalesMix_UserSet, 
        @SalesLessWaste_UserSet  = SalesLessWaste_UserSet, 
        @SalesProfitMix_UserSet  = SalesProfitMix_UserSet, 
        @SalesMixCube_UserSet  = SalesMixCube_UserSet, 
        @DOSCasePack_UserSet  = DOSCasePack_UserSet, 
        @FixtureDensity_UserSet  = FixtureDensity_UserSet, 
        @MixIncCohort_UserSet  = MixIncCohort_UserSet, 
        @ExcludeCategories = ExcludeCategories,
        @DaysTrading =  DaysTrading,
        @CubeFlex = CubeFlex,
        @MinBayFraction = MinBayFraction,
        @PotentialSalesAdjustment = PotentialSalesAdjustment
        FROM @Defaults;


        --Set start condition by returning Bays to their start value
        UPDATE MSO_Reports
        SET Bays = Org_Bays
        WHERE UserID = @UserID AND ReportID = @ReportID;

        UPDATE MSO_Reports
        SET Exclude_from_analysis = 
            CASE 
                WHEN Exclude_from_analysis > 1 THEN 0
                ELSE Exclude_from_analysis
            END
        WHERE UserID = @UserID 
        AND ReportID = @ReportID;


        --Set RVA variables
        UPDATE MSO_Reports
        SET
        UnitSalesMix_UserSet = @UnitSalesMix_UserSet,
        ValueSalesMix_UserSet = @ValueSalesMix_UserSet,
        ProfitSalesMix_UserSet = @ProfitSalesMix_UserSet,
        SalesLessWaste_UserSet  = @SalesLessWaste_UserSet,
        SalesProfitMix_UserSet  = @SalesProfitMix_UserSet,
        SalesMixCube_UserSet  = @SalesMixCube_UserSet,
        DOSCasePack_UserSet  = @DOSCasePack_UserSet,
        FixtureDensity_UserSet = @FixtureDensity_UserSet,
        MixIncCohort_UserSet = @MixIncCohort_UserSet
        WHERE UserID = @UserID AND ReportID = @ReportID AND Exclude_from_analysis = 0;

        -- Fetch and Apply override values from BayRules
        DECLARE @OverrideRules TABLE (
            Granularity NVARCHAR(10),
            Gran_Code NVARCHAR(50),
            ApplyColumn NVARCHAR(50),
            ValueToSet NVARCHAR(MAX),
            Var_or_Col NVARCHAR(50),
            Group_Key NVARCHAR(50)
        );

        -- Populate the override rules
        INSERT INTO @OverrideRules
        SELECT 
            Granularity, 
            Gran_Code, 
            ApplyColumn, 
            CASE 
                WHEN Group_Key = 'Exclude' THEN '3' -- Set ValueToSet to 1 if Group_Key is 'Exclude'
                ELSE ValueToSet 
            END AS ValueToSet,
            Var_or_Col, 
            Group_Key 
        FROM BayRules 
        WHERE ReportID = @ReportID 
        AND Group_Key IN ('Drivers', 'Driver', 'Metrics', 'Exclude', 'Rounding', 'InputOverrides', 'AddCategory');

        -- Variables for cursor processing
        DECLARE @Granularity NVARCHAR(10);
        DECLARE @Gran_Code NVARCHAR(50);
        DECLARE @ApplyColumn NVARCHAR(50);
        DECLARE @ValueToSet NVARCHAR(MAX);

        -- Check if there are override rules to process
        IF (SELECT COUNT(*) FROM @OverrideRules) > 0
        BEGIN
            -- Cursor to iterate over each override rule
            DECLARE OverrideCursor CURSOR FOR
            SELECT Granularity, Gran_Code, ApplyColumn, ValueToSet
            FROM @OverrideRules;

            OPEN OverrideCursor;

            FETCH NEXT FROM OverrideCursor INTO @Granularity, @Gran_Code, @ApplyColumn, @ValueToSet;

            WHILE @@FETCH_STATUS = 0
            BEGIN
                -- Build the dynamic SQL for the current rule
                SET @SQL = 
                    'UPDATE MSO_Reports SET ' + QUOTENAME(@ApplyColumn) + ' = ' + 
                    CASE 
                        WHEN @ApplyColumn = 'Exclude_from_analysis' THEN QUOTENAME(@ValueToSet, '''') 
                        ELSE @ValueToSet 
                    END +
                    ' WHERE UserID = @UserID AND ReportID = @ReportID AND ' +
                    CASE 
                        WHEN @Granularity = 'C' THEN 'Category_Code' 
                        WHEN @Granularity = 'D' THEN 'Department_Number' 
                        WHEN @Granularity = 'T' THEN 'Temp_Category_Number' 
                    END + ' = ' + @Gran_Code + ';';

                -- Execute the generated SQL
                EXEC sp_executesql @SQL, N'@UserID INT, @ReportID INT', @UserID = @UserID, @ReportID = @ReportID;


                -- Move to the next rule
                FETCH NEXT FROM OverrideCursor INTO @Granularity, @Gran_Code, @ApplyColumn, @ValueToSet;
            END

            CLOSE OverrideCursor;
            DEALLOCATE OverrideCursor;

            -- Update the output report
            SET @Output_Report = @Output_Report + 'Overrides applied;';
        END
        ELSE
        BEGIN
            -- No overrides to process
            SET @Output_Report = @Output_Report + 'No Overrides requested;';
        END

       -- Test if any Bay resize have been set and execute them
        IF (SELECT COUNT(*) FROM BayRules WHERE ReportID = @ReportID AND Group_Key = 'Resize') > 0
        BEGIN
            EXEC dbo.StoreResizeLogic
                @UserID = @UserID,
                @ReportID = @ReportID,
                @Resize_Success = @Resize_Success OUTPUT;
            IF @Resize_Success = 0
            BEGIN
                SET @Output_Report = @Output_Report + ' Store Resize Failed;';
            END
            ELSE
            BEGIN
                SET @Output_Report = @Output_Report + ' Store Resizes applied;';            
            END
        END
        ELSE
        BEGIN
            SET @Output_Report = @Output_Report + ' No Resizes requested;';       
        END

        -- Check and correct format of ExcludeCategories value from RVA
        IF @ExcludeCategories = '()' 
        OR @ExcludeCategories = '0' 
        OR @ExcludeCategories = '' 
        OR @ExcludeCategories IS NULL
        BEGIN
            SET @ExcludeCategories = '(0)'
        END

        IF @ExcludeCategories NOT LIKE'(%)'
        BEGIN
            SET @ExcludeCategories = '(' + @ExcludeCategories + ')'
        END

        DECLARE sql_cursor CURSOR FOR 
        SELECT Iteration, QueryText 
        FROM SqlTemplates 
        WHERE QueryName NOT LIKE 'project%'
        AND [Version] = 2
        ORDER BY Iteration;

        OPEN sql_cursor;
        FETCH NEXT FROM sql_cursor INTO @Iteration, @Template;
        WHILE @@FETCH_STATUS = 0
        BEGIN
            SET @SQL = REPLACE(@Template, 'EXCLUDE_CATEGORIES_PLACEHOLDER', @ExcludeCategories);

            EXEC sp_executesql @SQL, 
                N'
                @UserID INT, @ReportID INT,@UnitSalesMix_UserSet FLOAT,@ValueSalesMix_UserSet FLOAT,@ProfitSalesMix_UserSet FLOAT,@SalesLessWaste_UserSet FLOAT,@SalesProfitMix_UserSet FLOAT,
                @SalesMixCube_UserSet FLOAT,@DOSCasePack_UserSet FLOAT,@FixtureDensity_UserSet FLOAT,@MixIncCohort_UserSet FLOAT,@DaysTrading INT,@CubeFlex FLOAT',
                @UserID,@ReportID,@UnitSalesMix_UserSet,@ValueSalesMix_UserSet,@ProfitSalesMix_UserSet,@SalesLessWaste_UserSet,@SalesProfitMix_UserSet, 
                @SalesMixCube_UserSet,@DOSCasePack_UserSet,@FixtureDensity_UserSet,@MixIncCohort_UserSet,@DaysTrading,@CubeFlex;

            FETCH NEXT FROM sql_cursor INTO @Iteration, @Template;
        END
        CLOSE sql_cursor;
        DEALLOCATE sql_cursor;


        EXEC [dbo].[StoreRounding]
            @ReportID,
            @UserID,
            @MinBayFraction,
            @Rounding_Success = @Rounding_Success OUTPUT;

        IF @Rounding_Success = 1
        BEGIN
            SET @Output_Report = @Output_Report + ' Rounding succeeded;';
        END
        ELSE
        BEGIN
            SET @Output_Report = @Output_Report + ' Rounding failed;';
        END;


        WITH storeRecc AS (
        SELECT
        Category_Code,
        Rounded_Value
        FROM
        Sys_Rounded_Recc 
        WHERE Hierarchy = 3 AND ReportID = @ReportID AND UserID = @UserID
        ),
        deptRecc AS (
        SELECT
        Category_Code,
        Rounded_Value
        FROM
        Sys_Rounded_Recc 
        WHERE Hierarchy = 2 AND ReportID = @ReportID AND UserID = @UserID    
        ),
        tempRecc AS (
        SELECT
        Category_Code,
        Rounded_Value
        FROM
        Sys_Rounded_Recc 
        WHERE Hierarchy = 1 AND ReportID = @ReportID AND UserID = @UserID
        )
        UPDATE mr
        SET
            Store_Bays_Recc = sr.Rounded_Value,
            Dept_Bays_Recc = dr.Rounded_Value,
            Temp_Bays_Recc = tr.Rounded_Value
        FROM MSO_Reports mr
        JOIN storeRecc sr ON mr.Category_Code = sr.Category_Code
        JOIN deptRecc dr ON mr.Category_Code = dr.Category_Code
        JOIN tempRecc tr ON mr.Category_Code = tr.Category_Code
        WHERE UserID = @UserID AND ReportID = @ReportID;

        DECLARE sql_cursor CURSOR FOR 
        SELECT Iteration, QueryText 
        FROM SqlTemplates 
        WHERE QueryName LIKE 'project%'
        AND [Version] = 2
        ORDER BY Iteration;

        OPEN sql_cursor;
        FETCH NEXT FROM sql_cursor INTO @Iteration, @Template;

        WHILE @@FETCH_STATUS = 0
        BEGIN
            -- Assign the fetched query text to @SQL
            SET @SQL = @Template;

            -- Execute the current SQL statement
            EXEC sp_executesql @SQL, 
                N'@UserID INT, @ReportID INT, @PotentialSalesAdjustment FLOAT', 
                @UserID, @ReportID, @PotentialSalesAdjustment;

            -- Fetch the next query
            FETCH NEXT FROM sql_cursor INTO @Iteration, @Template;
        END

        CLOSE sql_cursor;
        DEALLOCATE sql_cursor;

    SET @SQL_Success = 1;
    SET @SQL_Message = 'Store build completed succesfully';
    SET NOCOUNT OFF;
    END
END

CREATE PROCEDURE [ui].[StoreGenerateStore-NHV1]
    @UserID INT = NULL,
    @ReportID INT = NULL,
    @SQL_Success BIT OUTPUT,
    @SQL_Message VARCHAR(255) OUTPUT,
    @Output_Report NVARCHAR(MAX) OUTPUT
    
AS

BEGIN
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = 'There was a problem, user not added.';

    DECLARE @InsertedUsers TABLE (MyID INT); -- Table variable to capture inserted MyID

    -- Insert a new user and capture the new MyID into the table variable
    INSERT INTO dbo.Users (MyID, DisplayName, Email, IsOSPAdmin, Status, FunctionalUser, AdminUser, BlockAccess, O365Email, MFA_Mobile, ColourMode, HierarchyMaintenance)
    OUTPUT INSERTED.MyID INTO @InsertedUsers(MyID) -- Capture the newly inserted MyID into the table variable
    VALUES (
    (SELECT MAX(CAST(MyID AS INT)) + 1 FROM dbo.Users),  -- Auto-generate MyID by incrementing the current maximum
        @DisplayName,
        @Email,
        @IsOSPAdmin,
        @Status,
        ISNULL(@FunctionalUserStatus, 0),  -- Default FunctionalUser to 0 if not provided
        @AdminUser,
        @BlockAccess,
        @O365Email,
        @MFA_Mobile,
        @ColourMode,
        @HierarchyMaintenance
    );

    -- Set the output UserID
    SELECT @UserID = MyID FROM @InsertedUsers;

    -- Set success output parameters for user addition
    SET @SQL_Success = 1;
    SET @SQL_Message = 'New user added successfully with UserID = ' + CAST(@UserID AS NVARCHAR(10));
END;

CREATE PROCEDURE [ui].[UserManAddUser]
    @FunctionalUserStatus INT = NULL,
    @DisplayName VARCHAR(255) = NULL,
    @Email VARCHAR(255) = NULL,
    @IsOSPAdmin BIT = NULL,
    @Status VARCHAR(24) = NULL,
    @AdminUser INT = NULL,
    @BlockAccess INT = NULL,
    @O365Email NVARCHAR(MAX) = NULL,
    @MFA_Mobile VARCHAR(24) = NULL,
    @ColourMode CHAR(1) = 'L',
    @HierarchyMaintenance BIT = NULL,
    @UserID INT OUTPUT,  -- Output parameter for new UserID
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(100) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = 'There was a problem deleting the user.';

    -- Check if there are reports associated with the UserID in ReportVersionAttributes
    DECLARE @ReportCount INT;
    SELECT @ReportCount = COUNT(*)
    FROM ReportVersionAttributes
    WHERE ReportOwner = @UserID;

    IF @ReportCount > 0
    BEGIN
        -- Return message if the user has reports under their ownership
        SET @SQL_Success = 0;
        SET @SQL_Message = 'This user cannot be deleted, as there are live reports under their ownership.';
        RETURN;
    END

    -- Check if the user exists in the Users table
    IF EXISTS (SELECT 1 FROM Users WHERE MyID = @UserID)
    BEGIN
        -- Delete the user
        DELETE FROM Users WHERE MyID = @UserID;

        -- Return success message
        SET @SQL_Success = 1;
        SET @SQL_Message = 'User deleted successfully.';
    END
    ELSE
    BEGIN
        -- Return error message if user not found
        SET @SQL_Success = 0;
        SET @SQL_Message = 'User not found.';
    END
END;

CREATE PROCEDURE [ui].[UserManDelete] -- Corrected bracket here
    @UserID INT,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(100) OUTPUT
AS

BEGIN
    SET NOCOUNT ON;

    -- Initialize output parameters
    SET @SQL_Success = 0;
    SET @SQL_Message = 'There was a problem, user not updated.';

    -- Check if the user exists
    IF NOT EXISTS (SELECT 1 FROM Users WHERE MyID = @UserID)
    BEGIN
        SET @SQL_Message = 'User not found.';
        RETURN;
    END

    -- Validate ColourMode (optional validation rule)
    IF @ColourMode NOT IN ('L', 'D')
    BEGIN
        SET @SQL_Success = 0;
        SET @SQL_Message = 'Invalid ColourMode. Allowed values are ''L'' for Light or ''D'' for Dark.';
        RETURN;
    END

    -- Prepare update statement with dynamic field updates
    UPDATE Users
    SET
        DisplayName = COALESCE(@DisplayName, DisplayName),  -- Update if provided
        Email = COALESCE(@Email, Email),                    -- Update if provided
        IsOSPAdmin = COALESCE(@IsOSPAdmin, IsOSPAdmin),      -- Update if provided
        Status = COALESCE(@Status, Status),                 -- Update if provided
        FunctionalUser = COALESCE(@FunctionalUserStatus, FunctionalUser),  -- Update if provided
        AdminUser = COALESCE(@AdminUser, AdminUser),         -- Update if provided
        BlockAccess = COALESCE(@BlockAccess, BlockAccess),   -- Update if provided
        O365Email = COALESCE(@O365Email, O365Email),        -- Update if provided
        MFA_Mobile = COALESCE(@MFA_Mobile, MFA_Mobile),      -- Update if provided
        ColourMode = COALESCE(@ColourMode, ColourMode),      -- Update if provided
        HierarchyMaintenance = COALESCE(@HierarchyMaintenance, HierarchyMaintenance)  -- Update if provided
    WHERE MyID = @UserID;

    -- Check if the update was successful
    IF @@ROWCOUNT > 0
    BEGIN
        SET @SQL_Success = 1;
        SET @SQL_Message = 'User updated successfully.';
    END
    ELSE
    BEGIN
        SET @SQL_Success = 0;
        SET @SQL_Message = 'No changes were made to the user.';
    END
END;

CREATE PROCEDURE [ui].[UserManUpdateUser]
    @UserID INT,  -- Required: The ID of the user to update
    @FunctionalUserStatus INT = NULL,
    @DisplayName VARCHAR(255) = NULL,
    @Email VARCHAR(255) = NULL,
    @IsOSPAdmin BIT = NULL,
    @Status VARCHAR(24) = NULL,
    @AdminUser INT = NULL,
    @BlockAccess INT = NULL,
    @O365Email NVARCHAR(MAX) = NULL,
    @MFA_Mobile VARCHAR(24) = NULL,
    @ColourMode CHAR(1) = 'L',
    @HierarchyMaintenance BIT = NULL,
    @SQL_Success BIT OUTPUT,
    @SQL_Message NVARCHAR(100) OUTPUT
AS
